// frontend/src/App.js
import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import HomePage from "./pages/HomePage";
import MatchDetailPage from "./pages/MatchDetailPage";
import Header from "./components/Layout/Header"; // Новый компонент
// import Footer from './components/Layout/Footer'; // Можно добавить позже
import "./App.css"; // Общие стили для App, если нужны

function App() {
  return (
    <Router>
      <div className="flex flex-col min-h-screen bg-brand-dark text-gray-100">
        {" "}
        {/* Основной контейнер с темной темой */}
        <Header /> {/* Наш новый хедер */}
        <main className="flex-grow container mx-auto px-4 py-8 pt-20 md:pt-24">
          {" "}
          {/* Основной контент */}
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/match/:matchId" element={<MatchDetailPage />} />
            {/* Другие роуты (История, Таблицы) можно будет добавить сюда */}
          </Routes>
        </main>
        {/* <Footer /> Можно будет добавить футер позже */}
      </div>
    </Router>
  );
}

export default App;
