// frontend/src/components/Layout/Header.js
import React, { useState, useEffect, useRef } from "react";
import { Link, NavLink, useLocation } from "react-router-dom";
import NeuroPredictLogo from "../../assets/NeuroPredictLogo.png"; // Ваш логотип

const leaguesNav = [
  /* ... ваш список лиг ... */
];
const mainNavLinks = [
  { to: "/", text: "Главная" },
  { to: "/history", text: "История матчей" },
  { to: "/tables", text: "Турнирные таблицы" },
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const location = useLocation();

  useEffect(() => {
    /* ... ваш useEffect для закрытия дропдауна ... */
  });
  useEffect(() => {
    setIsLeaguesDropdownOpen(false);
  }, [location.pathname, location.search]);

  // Стили для кнопок навигации
  const navButtonBaseClasses =
    "px-4 py-2 rounded-full text-xs sm:text-sm font-semibold transition-all duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-brand-nav-bg";
  // Активная кнопка: желтый фон, темный текст, желтое свечение
  const navButtonActiveClasses =
    "bg-brand-primary-yellow text-brand-bg shadow-yellow-button-glow";
  // Неактивная кнопка: темно-серый фон, светло-серый текст, при наведении чуть светлее фон и желтый текст
  const navButtonInactiveClasses =
    "bg-brand-nav-button-bg text-brand-text-muted hover:bg-brand-nav-button-hover hover:text-brand-primary-yellow";

  return (
    // Одна навигационная панель
    <header className="bg-brand-nav-bg shadow-md sticky top-0 z-50 w-full">
      {/* Добавляем красную подсветку снизу хедера */}
      <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-transparent via-brand-primary-red to-transparent opacity-70"></div>

      <div className="container mx-auto px-4 h-16 flex justify-between items-center relative">
        {" "}
        {/* relative для позиционирования подсветки */}
        {/* Логотип и название сайта (слева) */}
        <Link
          to="/"
          className="flex items-center text-xl md:text-2xl font-bold text-brand-primary-yellow hover:opacity-80 transition-opacity"
        >
          <img
            src={NeuroPredictLogo}
            alt="NeuroPredict Logo"
            className="h-8 md:h-9 w-auto mr-2 md:mr-3"
          />
          NeuroPredict
        </Link>
        {/* Центральная навигация */}
        <nav className="flex items-center space-x-2 sm:space-x-3 md:space-x-4">
          {mainNavLinks.map((link) => (
            <NavLink
              key={link.to}
              to={link.to}
              className={({ isActive }) =>
                `${navButtonBaseClasses} ${
                  isActive ||
                  (link.to === "/" &&
                    location.pathname === "/" &&
                    location.search)
                    ? navButtonActiveClasses
                    : navButtonInactiveClasses
                }`
              }
            >
              {link.text}
            </NavLink>
          ))}

          {/* Выпадающий список "Лиги" */}
          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setIsLeaguesDropdownOpen((prev) => !prev)}
              // Для кнопки "Лиги" используем стили неактивной кнопки, но текст сделаем основным желтым
              className={`${navButtonBaseClasses} ${navButtonInactiveClasses.replace(
                "text-brand-text-muted",
                "text-brand-primary-yellow"
              )} flex items-center`}
            >
              Лиги
              <svg
                className={`w-3 h-3 ml-1.5 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                  clipRule="evenodd"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              <div className="absolute left-1/2 -translate-x-1/2 md:left-auto md:right-0 mt-2 w-56 bg-brand-nav-bg rounded-md shadow-xl z-30 py-1 border border-gray-700">
                {leaguesNav.map((league) => (
                  <NavLink
                    key={league.code}
                    to={`/?league=${league.code}`}
                    className="block px-4 py-2 text-sm text-brand-text-light hover:bg-gray-700 hover:text-brand-primary-yellow w-full text-left"
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>
        </nav>
        {/* Пустой div справа для балансировки центрирования навигации */}
        <div className="w-0 md:w-48 lg:w-56">
          {" "}
          {/* Подберите ширину, чтобы соответствовала блоку логотипа */}
        </div>
      </div>
    </header>
  );
}

export default Header;
