// frontend/src/pages/HomePage.js
// ... (импорты и leaguesForSidebar остаются как в предыдущем ответе) ...

function HomePage() {
  // ... (стейты, useEffect'ы, handleLeagueSelect, pageTitle - все как в предыдущем ответе) ...
  const [allFetchedMatches, setAllFetchedMatches] = useState([]);
  const [displayedMatches, setDisplayedMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchParams, setSearchParams] = useSearchParams();
  const selectedLeagueCode = searchParams.get("league");

  useEffect(() => {
    /* ... загрузка всех матчей ... */
  }, []);
  useEffect(() => {
    /* ... фильтрация и сортировка displayedMatches ... */
  }, [allFetchedMatches, selectedLeagueCode, loading]);
  const handleLeagueSelect = (leagueCode) => {
    /* ... */
  };
  const currentLeagueObject = selectedLeagueCode
    ? leaguesForSidebar.find((l) => l.code === selectedLeagueCode)
    : { name: "Топ матчи дня" };
  const pageTitle = currentLeagueObject.name;

  // Рассчитываем отступ для sticky сайдбаров от верха страницы
  // Высота хедера (h-16 в Header.js -> 4rem) + отступ <main> от хедера (pt-XX в App.js)
  // Если хедер h-16 (4rem) и отступ main pt-20 (5rem), то общий отступ до контента HomePage = 9rem.
  // Сайдбар должен быть ниже хедера. Если хедер h-16 (4rem ~ 64px), то top-20 (5rem ~ 80px) или top-[68px] для сайдбара.
  const headerHeightApprox = "4rem"; // Примерная высота вашего хедера (16 * 0.25rem)
  const mainPaddingTop = "5rem"; // Примерный pt-20 у main в App.js

  // Отступ сверху для sticky сайдбара: высота хедера + небольшой зазор
  // Например, если хедер 64px, то отступ top-[72px] или top-[80px] (5rem)
  const sidebarStickyTop = "top-[calc(4rem+0.5rem)]"; // 4rem (header h-16) + 0.5rem зазора
  // Максимальная высота сайдбара: высота окна минус позиция top сайдбара минус нижний отступ страницы (py-8 у main)
  const sidebarMaxHeight = `max-h-[calc(100vh-${sidebarStickyTop.replace(
    "top-",
    ""
  )}-2rem)]`; // 2rem - это py-8 (нижний) у main

  return (
    <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
      <aside
        className={`
          w-full lg:w-60 xl:w-64 flex-shrink-0 
          mb-6 lg:mb-0 
          lg:sticky ${sidebarStickyTop} self-start 
          ${sidebarMaxHeight} overflow-y-auto 
          rounded-lg
        `}
      >
        <LeagueSidebar
          leagues={leaguesForSidebar}
          selectedLeague={selectedLeagueCode}
          onSelectLeague={handleLeagueSelect}
        />
      </aside>

      <section className="w-full lg:flex-grow min-w-0">
        <h1 className="text-2xl sm:text-3xl font-bold mb-6 text-center text-brand-primary-yellow uppercase tracking-wider">
          {pageTitle}
        </h1>
        {loading && allFetchedMatches.length === 0 ? (
          <div className="flex justify-center items-center h-64">
            <p className="text-xl text-gray-400 animate-pulse">
              Загрузка матчей...
            </p>
          </div>
        ) : (
          <MatchList
            matches={displayedMatches}
            selectedLeague={selectedLeagueCode}
          />
        )}
      </section>

      <aside
        className={`
          w-full lg:w-72 xl:w-80 flex-shrink-0 
          hidden md:block 
          lg:sticky ${sidebarStickyTop} self-start 
          ${sidebarMaxHeight} overflow-y-auto 
          rounded-lg
        `}
      >
        <div className="bg-brand-surface p-4 rounded-lg shadow-md min-h-[300px]">
          <h3 className="text-xl font-semibold mb-3 text-brand-primary-yellow">
            Турнирная таблица
          </h3>
          <p className="text-brand-text-muted">
            {currentLeagueObject && selectedLeagueCode
              ? `Таблица для ${currentLeagueObject.name}`
              : "Выберите лигу"}
          </p>
        </div>
      </aside>
    </div>
  );
}

export default HomePage;
