import React, { useState, useEffect, useRef } from "react";
import { Link, NavLink, useLocation, useSearchParams } from "react-router-dom";
// `NavLink`: более умная версия `Link`, которая автоматически добавляет классы активности, если текущий URL совпадает с ее `to` пропсом.
// `useLocation`: хук для получения объекта `location`, который содержит информацию о текущем URL (pathname, search, hash).
// `useSearchParams`: хук для работы с параметрами запроса в URL (например, `?league=PL`).

// Список лиг для выпадающего меню (остается)
const leagues = [
  // Этот массив содержит данные о лигах, которые будут отображаться в выпадающем меню "Лиги" в хедере.
  // Каждая лига имеет `code` (для URL-параметра) и `name` (для отображения).
  { code: "PL", name: "Premier League" },
  { code: "PD", name: "La liga" },
  { code: "SA", name: "Serie A" },
  { code: "BL1", name: "Bundesliga" },
  { code: "FL1", name: "Ligue 1" },
];

// ОБНОВЛЕН ПОРЯДОК И СОСТАВ mainNavLinks
const mainNavLinks = [
  // Этот массив определяет основные ссылки навигации в хедере (кроме выпадающего списка "Лиги", который обрабатывается отдельно).
  // Каждая ссылка имеет `to` (путь) и `text` (текст ссылки).
  { to: "/", text: "Главная" },
  // Кнопка "Лиги" теперь будет частью этого массива, но с особым рендерингом
  // { type: 'dropdown', text: "Лиги" }, // Закомментировано, так как кнопка "Лиги" рендерится явно, а не через map.
  { to: "/tables", text: "Турнирные таблицы" },
  { to: "/history", text: "История матчей" },
];

function Header() {
  // Определяем функциональный React-компонент `Header`.

  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);
  // Состояние для управления видимостью выпадающего списка лиг. `false` по умолчанию (скрыт).

  const dropdownRef = useRef(null);
  // Создаем ссылку на DOM-элемент (контейнер выпадающего списка).
  // Это позволит нам проверять, происходит ли клик внутри или вне этого элемента.

  const location = useLocation();
  // Получаем объект `location` с информацией о текущем URL.
  // Это нужно для логики активных ссылок и закрытия выпадающего списка при смене URL.

  const [searchParams] = useSearchParams();
  // Получаем объект `searchParams` для чтения URL-параметров.
  // Это нужно для определения, активна ли кнопка "Лиги", если выбран какой-то параметр лиги.

  useEffect(() => {
    // Этот `useEffect` отвечает за закрытие выпадающего списка, если пользователь кликает ВНЕ его.
    function handleClickOutside(event) {
      // Функция-обработчик клика.
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        // Если ссылка на выпадающий список существует И клик произошел не внутри выпадающего списка,
        setIsLeaguesDropdownOpen(false); // то закрываем его.
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    // Добавляем обработчик события `mousedown` на весь документ при монтировании компонента.
    return () => document.removeEventListener("mousedown", handleClickOutside);
    // Функция очистки: удаляем обработчик события при размонтировании компонента, чтобы избежать утечек памяти.
  }, [dropdownRef]); // Зависимость от `dropdownRef` (хотя `useRef` обычно не меняется, это хорошая практика).

  useEffect(() => {
    // Этот `useEffect` отвечает за закрытие выпадающего списка при изменении маршрута.
    setIsLeaguesDropdownOpen(false); // Устанавливаем `isLeaguesDropdownOpen` в `false`.
  }, [location.pathname, location.search]);
  // Зависимости: эффект сработает, если изменится путь (`pathname`) или параметры (`search`) в URL.

  // 1. Определение базовых и активных/неактивных классов для кнопок навигации
  const navButtonBaseClasses =
    "px-4 py-2 rounded-full text-xs sm:text-sm font-semibold transition-all duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-color-surface";
  // Общие базовые стили для всех навигационных кнопок: отступы, скругление, размер шрифта, жирность, плавный переход, стили для фокуса.

  const navButtonActiveClasses =
    "bg-color-button-active-bg text-color-button-active-text shadow-red-button";
  // Стили для активной (выбранной) кнопки: фон, цвет текста, тень свечения.

  const navButtonInactiveClasses =
    "bg-color-button-inactive-bg text-color-button-inactive-text hover:bg-color-button-hover-bg hover:text-color-button-hover-text hover:shadow-red-button";
  // Стили для неактивной кнопки: фон, цвет текста, стили при наведении.

  const isLeagueButtonActive =
    isLeaguesDropdownOpen || !!searchParams.get("league");
  // Логика для определения, должна ли кнопка "Лиги" быть активной.
  // Она активна, если выпадающий список открыт (`isLeaguesDropdownOpen` - `true`)
  // ИЛИ если в URL есть параметр `?league=` (т.е. выбрана какая-то лига).

  // 2. JSX-разметка компонента Header
  return (
    <header className="bg-color-surface shadow-md sticky top-0 z-50 w-full">
      {/* Главный тег `<header>` для шапки сайта.
          `bg-color-surface`: фоновый цвет из Tailwind конфига.
          `shadow-md`: средняя тень.
          `sticky top-0`: делает шапку "липкой" - она остается наверху при прокрутке.
          `z-50`: высокий `z-index`, чтобы шапка всегда была поверх другого контента.
          `w-full`: занимает всю ширину. */}

      <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-transparent via-color-primary-red to-transparent opacity-60"></div>
      {/* Декоративная тонкая красная линия внизу шапки.
          `absolute bottom-0 left-0 right-0`: позиционируется абсолютно внизу по всей ширине.
          `h-0.5`: очень маленькая высота.
          `bg-gradient-to-r ...`: градиент от прозрачного через красный к прозрачному.
          `opacity-60`: полупрозрачная. */}

      <div className="container mx-auto px-4 h-16 flex justify-between items-center relative">
        {/* Внутренний контейнер шапки, который центрируется и имеет фиксированную высоту.
            `container mx-auto px-4`: центрирует содержимое, добавляет горизонтальные отступы.
            `h-16`: фиксированная высота 16 Tailwind единиц (4rem).
            `flex justify-between items-center`: элементы внутри (логотип, навигация, балансирующий div) распределяются по краям и центрируются по вертикали.
            `relative`: для позиционирования внутренних абсолютных элементов. */}

        <Link to="/" className="hover:opacity-90 transition-opacity">
          {/* Логотип/название сайта, который является ссылкой на главную страницу.
              `hover:opacity-90 transition-opacity`: плавное изменение прозрачности при наведении. */}
          <span className="text-2xl md:text-3xl font-extrabold text-gradient-neuro-title tracking-tight">
            NeuroPredict
          </span>
          {/* Текстовый логотип с градиентным цветом и жирным шрифтом. */}
        </Link>

        {/* Центральная навигация с измененным порядком */}
        <nav className="flex items-center space-x-1 sm:space-x-2 md:space-x-3">
          {/* Навигационное меню: flex-контейнер с отступами между элементами. */}

          {/* Кнопка "Главная" */}
          <NavLink
            to="/"
            // `to="/" `: ссылка на главную страницу.
            className={({ isActive }) =>
              // Функция для определения классов активности. `isActive` предоставляется `NavLink`.
              `${navButtonBaseClasses} ${
                // Базовые классы.
                (isActive && !location.search && location.pathname === "/") ||
                // Логика активности для "Главной":
                // Активна, если путь "/", НЕТ параметров поиска (т.е. не выбрана лига)
                // ИЛИ (эта часть может быть избыточной/неоднозначной, если "/" должен быть активен только без лиги)
                // если она активна, но путь не "/" (т.е., возможно, перешли с "/" на "/" с параметром, и NavLink все еще считает ее активной, но нам это не нужно).
                // Возможно, здесь `location.pathname === '/' && !location.search` было бы достаточно для точной активации "Главной".
                (isActive && location.pathname !== "/") // Эта часть делает кнопку "Главная" активной, если пользователь находится на любом маршруте, который считается связанным с корнем (например, '/?league=PL'), но только если NavLink сам ее пометил как активную.
                  ? navButtonActiveClasses // Применяем активные классы.
                  : navButtonInactiveClasses // Применяем неактивные классы.
              }`
            }
          >
            Главная
          </NavLink>

          {/* Выпадающий список "Лиги" */}
          <div className="relative" ref={dropdownRef}>
            {/* Контейнер для выпадающего списка. `relative` для позиционирования выпадающего меню.
                `ref={dropdownRef}`: связываем этот `div` с ссылкой `dropdownRef` для отслеживания кликов вне его. */}
            <button
              onClick={() => setIsLeaguesDropdownOpen((prev) => !prev)}
              // При клике на кнопку переключаем состояние `isLeaguesDropdownOpen`.
              className={`${navButtonBaseClasses} ${
                isLeagueButtonActive
                  ? navButtonActiveClasses
                  : navButtonInactiveClasses
              } flex items-center`}
              // Применяем классы: базовые, плюс активные/неактивные в зависимости от `isLeagueButtonActive`.
              // `flex items-center`: для выравнивания текста "Лиги" и иконки стрелки.
            >
              Лиги
              <svg
                className={`w-3 h-3 ml-1.5 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                // Иконка стрелки:
                // `w-3 h-3 ml-1.5`: размеры и отступ.
                // `transform transition-transform`: для плавной анимации поворота.
                // `rotate-180`: поворачивает стрелку на 180 градусов, когда выпадающий список открыт.
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                  clipRule="evenodd"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              // Условный рендеринг: выпадающее меню отображается ТОЛЬКО, если `isLeaguesDropdownOpen` `true`.
              <div className="absolute left-0 mt-2 w-56 bg-color-surface rounded-md shadow-xl z-30 py-1 border border-gray-700">
                {/* Контейнер для самого выпадающего меню:
                    `absolute left-0`: позиционируется абсолютно слева от кнопки.
                    `mt-2`: отступ сверху.
                    `w-56`: фиксированная ширина.
                    `bg-color-surface rounded-md shadow-xl z-30 py-1 border border-gray-700`: стили фона, скругления, тени, z-индекса, отступов и границы. */}
                {leagues.map((league) => (
                  // Перебираем массив `leagues` для создания каждой ссылки в выпадающем меню.
                  <NavLink
                    key={league.code} // Уникальный ключ для React.
                    to={`/?league=${league.code}`} // Ссылка на главную страницу с параметром лиги.
                    className={`block px-4 py-2 text-sm w-full text-left transition-colors
                      ${
                        // Классы активности для элементов выпадающего списка:
                        searchParams.get("league") === league.code
                          ? "bg-color-primary-red text-color-button-inactive-text" // Если эта лига выбрана (параметр в URL совпадает), то активные стили.
                          : "text-color-button-inactive-text hover:bg-color-primary-red hover:text-color-primary-yellow" // Иначе, неактивные стили и стили при наведении.
                      }`}
                  >
                    {league.name}
                    {/* Название лиги (текст ссылки). */}
                  </NavLink>
                ))}
              </div>
            )}
          </div>

          {/* Кнопка "Турнирные таблицы" */}
          <NavLink
            to="/tables" // Заглушка
            // Ссылка на страницу турнирных таблиц.
            className={({ isActive }) =>
              `${navButtonBaseClasses} ${
                isActive ? navButtonActiveClasses : navButtonInactiveClasses
              }`
            }
            // Классы активности: если текущий путь `/tables`, кнопка активна.
          >
            Турнирные таблицы
          </NavLink>

          {/* Кнопка "История матчей" */}
          <NavLink
            to="/history" // Заглушка
            // Ссылка на страницу истории матчей.
            className={({ isActive }) =>
              `${navButtonBaseClasses} ${
                isActive ? navButtonActiveClasses : navButtonInactiveClasses
              }`
            }
            // Классы активности: если текущий путь `/history`, кнопка активна.
          >
            История матчей
          </NavLink>
        </nav>

        <div className="w-0 sm:w-32 md:w-48 lg:w-[18rem] hidden sm:block">
          {/* Балансирующий div */}
          {/* Этот пустой `div` используется для визуального баланса в шапке.
              Поскольку логотип находится слева, а навигация в центре,
              этот `div` справа "занимает" место, чтобы центрировать навигацию.
              Его ширина динамически меняется в зависимости от размера экрана,
              и он скрыт на очень маленьких экранах. */}
        </div>
      </div>
    </header>
  );
}

export default Header;
// Экспортируем компонент `Header` по умолчанию, чтобы его можно было импортировать в `App.js` и использовать в макете.
