// frontend/src/components/Layout/Header.js
import React, { useState, useEffect, useRef } from "react";
import { Link, NavLink, useLocation } from "react-router-dom";
import NeuroPredictLogo from "../../assets/NeuroPredictLogo.png"; // Ваш логотип

const leaguesNav = [
  { code: "PL", name: "Premier League" },
  { code: "BL1", name: "Bundesliga" },
  { code: "SA", name: "Serie A" },
  { code: "PD", name: "La Liga" },
  { code: "FL1", name: "Ligue 1" },
];
const mainNavLinks = [
  { to: "/", text: "Главная" },
  { to: "/history", text: "История матчей" },
  { to: "/tables", text: "Турнирные таблицы" },
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const location = useLocation();

  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsLeaguesDropdownOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [dropdownRef]);

  useEffect(() => {
    setIsLeaguesDropdownOpen(false);
  }, [location.pathname, location.search]);

  const navButtonBase =
    "px-3 sm:px-4 py-2 rounded-full text-xs sm:text-sm font-semibold transition-colors duration-150 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-brand-surface";
  const navButtonActive =
    "bg-brand-button-active-bg text-brand-button-active-text shadow-yellow-glow-sm";
  const navButtonInactive =
    "bg-brand-button-inactive-bg text-brand-button-inactive-text hover:bg-brand-button-hover-bg hover:text-brand-button-hover-text";

  return (
    <header className="bg-brand-surface shadow-md sticky top-0 z-50 w-full">
      {/* Эффект красной подсветки снизу хедера */}
      <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-transparent via-brand-primary-red to-transparent opacity-60 group-hover:opacity-100 transition-opacity"></div>

      <div className="container mx-auto px-4 h-16 flex justify-between items-center relative">
        <Link
          to="/"
          className="flex items-center hover:opacity-90 transition-opacity"
        >
          <img
            src={NeuroPredictLogo}
            alt="NeuroPredict Logo"
            className="h-8 sm:h-9 w-auto mr-2"
          />
          <span className="text-xl sm:text-2xl font-bold text-gradient-neuro">
            {" "}
            {/* Градиентный текст */}
            NeuroPredict
          </span>
        </Link>

        <nav className="flex items-center space-x-1 sm:space-x-2 md:space-x-3">
          {mainNavLinks.map((link) => (
            <NavLink
              key={link.to}
              to={link.to}
              className={({ isActive }) =>
                `${navButtonBase} ${
                  isActive ||
                  (link.to === "/" &&
                    location.pathname === "/" &&
                    !!location.search) // Активна, если это главная с параметрами
                    ? navButtonActive
                    : navButtonInactive
                }`
              }
            >
              {link.text}
            </NavLink>
          ))}
          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setIsLeaguesDropdownOpen((prev) => !prev)}
              className={`${navButtonBase} ${navButtonInactive.replace(
                "text-brand-button-inactive-text",
                "text-brand-primary-yellow"
              )} flex items-center`} // Кнопка "Лиги" всегда с желтым текстом
            >
              Лиги
              <svg
                className={`w-3 h-3 ml-1 transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                  clipRule="evenodd"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              <div className="absolute left-1/2 -translate-x-1/2 md:left-auto md:right-0 mt-2 w-56 bg-brand-surface rounded-md shadow-xl z-30 py-1 border border-gray-700">
                {leaguesNav.map((league) => (
                  <NavLink
                    key={league.code}
                    to={`/?league=${league.code}`}
                    className="block px-4 py-2 text-sm text-brand-text-light hover:bg-gray-700 hover:text-brand-primary-yellow w-full text-left"
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>
        </nav>

        {/* Пустой div для выравнивания, если необходимо */}
        <div className="w-0 sm:w-32 md:w-48 lg:w-56 hidden sm:block">
          {" "}
          {/* Скрыт на очень маленьких экранах */}
        </div>
      </div>
    </header>
  );
}
export default Header;
