// frontend/src/components/Layout/Header.js
import React, { useState, useEffect, useRef } from "react";
import { Link, NavLink, useLocation } from "react-router-dom";
import NeuroPredictLogo from "../../assets/NeuroPredictLogo.png"; // Убедитесь, что путь правильный

// Список лиг для выпадающего меню (остается)
const leaguesNav = [
  { code: "PL", name: "Premier League" },
  { code: "BL1", name: "Bundesliga" },
  { code: "SA", name: "Serie A" },
  { code: "PD", name: "La Liga" },
  { code: "FL1", name: "Ligue 1" },
];

// Список ссылок для основной навигации
const mainNavLinks = [
  { to: "/", text: "Главная" },
  { to: "/history", text: "История матчей" }, // Заглушка
  { to: "/tables", text: "Турнирные таблицы" }, // Заглушка
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const location = useLocation();

  // Закрытие дропдауна по клику вне и при смене URL
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsLeaguesDropdownOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [dropdownRef]);

  useEffect(() => {
    setIsLeaguesDropdownOpen(false);
  }, [location.pathname, location.search]);

  // Стили для кнопок навигации
  const navButtonBaseClasses =
    "px-4 py-2 rounded-full text-sm font-semibold transition-all duration-150 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-brand-bg-nav";
  const navButtonActiveClasses =
    "bg-brand-yellow-accent text-brand-bg-dark shadow-yellow-glow-button";
  const navButtonInactiveClasses =
    "bg-gray-700 text-brand-text-muted hover:bg-gray-600 hover:text-brand-text-light";

  return (
    // Теперь хедер - это одна навигационная панель
    <header className="bg-brand-bg-nav shadow-md sticky top-0 z-50 w-full">
      <div className="container mx-auto px-4 h-16 flex justify-between items-center">
        {/* Логотип и название сайта (слева) */}
        <Link
          to="/"
          className="flex items-center text-xl md:text-2xl font-bold text-brand-yellow-accent hover:opacity-80 transition-opacity"
        >
          <img
            src={NeuroPredictLogo}
            alt="NeuroPredict Logo"
            className="h-8 md:h-9 w-auto mr-2 md:mr-3"
          />
          NeuroPredict
        </Link>

        {/* Центральная навигация */}
        <nav className="flex items-center space-x-2 sm:space-x-3 md:space-x-4">
          {mainNavLinks.map((link) => (
            <NavLink
              key={link.to}
              to={link.to}
              // Для "Главная" проверяем также, если есть query параметры (например, от фильтра лиг)
              isActive={(match, currentLocation) => {
                if (link.to === "/")
                  return (
                    currentLocation.pathname === "/" && !currentLocation.search
                  ); // Строго главная
                if (!match) return false; // для NavLink v5
                return true; // для NavLink v6 isActive проп уже есть
              }}
              className={({ isActive }) =>
                `${navButtonBaseClasses} ${
                  isActive ||
                  (link.to === "/" &&
                    location.pathname === "/" &&
                    location.search)
                    ? navButtonActiveClasses
                    : navButtonInactiveClasses
                }`
              }
            >
              {link.text}
            </NavLink>
          ))}

          {/* Выпадающий список "Лиги" */}
          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setIsLeaguesDropdownOpen((prev) => !prev)}
              className={`${navButtonBaseClasses} ${navButtonInactiveClasses} flex items-center`}
            >
              Лиги
              <svg
                className={`w-4 h-4 ml-1.5 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                  clipRule="evenodd"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              <div className="absolute left-1/2 -translate-x-1/2 md:left-auto md:right-0 mt-2 w-52 bg-gray-700 rounded-md shadow-xl z-30 py-1 border border-gray-600">
                {leaguesNav.map((league) => (
                  <NavLink
                    key={league.code}
                    to={`/?league=${league.code}`} // Направляем на главную с параметром лиги
                    // Активность для элементов дропдауна будет определяться параметром в URL на HomePage
                    className="block px-4 py-2 text-sm text-brand-text-light hover:bg-gray-600 hover:text-brand-yellow-accent w-full text-left"
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>
        </nav>

        {/* Пустой div справа для балансировки, если логотип занимает место, а справа от навигации ничего нет */}
        {/* Его ширина должна быть примерно равна ширине логотипа + название */}
        <div className="w-0 md:w-48">
          {" "}
          {/* Подберите ширину или удалите, если навигация должна растягиваться */}
        </div>
      </div>
    </header>
  );
}

export default Header;
