// frontend/src/components/MatchItem.js
import React from "react";
import { Link } from "react-router-dom";
// import { formatInTimeZone } from 'date-fns-tz'; // Для точного МСК

const formatMatchDateTime = (utcDateString, localMatchTimeString) => {
  let dateToFormat = localMatchTimeString || utcDateString;
  if (!dateToFormat) return "N/A";
  try {
    const date = new Date(dateToFormat);
    // Пример для МСК с date-fns-tz:
    // if (utcDateString) {
    //   return formatInTimeZone(new Date(utcDateString), 'Europe/Moscow', 'dd.MM.yyyy | HH:mm', { timeZone: 'Europe/Moscow' });
    // }
    return (
      date.toLocaleString("ru-RU", {
        day: "2-digit",
        month: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      }) +
      (localMatchTimeString && !String(utcDateString).endsWith("Z")
        ? ` (Местн.)`
        : ` (UTC)`)
    );
  } catch (e) {
    return String(dateToFormat).substring(0, 16).replace("T", " ");
  }
};

// Информация для отображения прогноза на основе вашего нового скриншота
const getPredictionDetails = (match) => {
  // Эту логику вам нужно будет реализовать на основе данных, которые приходят от вашего API.
  // Пример, если у вас есть поля 'bet_type', 'bet_value', 'bet_odd', 'bet_confidence'
  // Сейчас я просто верну заглушки, похожие на скриншот.
  // Вам нужно будет адаптировать это под ваши РЕАЛЬНЫЕ данные из match.

  // Пример 1: "Обе забьют @1.75", "Уверенность: 85%"
  // Пример 2: "Тотал > 2.5 @1.85", "Уверенность: 78%"
  // Пример 3: "Победа Барселоны 2:1", "Коэффициент: 3.50" (из предыдущего скриншота)

  // Для простоты пока оставим исходный прогноз H/D/A, если он есть
  if (match.Predicted_Outcome) {
    let outcomeText = "";
    if (match.Predicted_Outcome === "H")
      outcomeText = `${match.homeTeam_name} П1`;
    else if (match.Predicted_Outcome === "A")
      outcomeText = `${match.awayTeam_name} П2`;
    else if (match.Predicted_Outcome === "D") outcomeText = "Ничья";

    // Предположим, у вас есть вероятность для основного прогноза
    let confidence = null;
    if (match.Predicted_Outcome === "H" && match.Prob_H)
      confidence = match.Prob_H;
    else if (match.Predicted_Outcome === "A" && match.Prob_A)
      confidence = match.Prob_A;
    else if (match.Predicted_Outcome === "D" && match.Prob_D)
      confidence = match.Prob_D;

    return {
      text: outcomeText, // Это будет основной текст прогноза
      confidence: confidence
        ? `Уверенность: ${(confidence * 100).toFixed(0)}%`
        : null,
    };
  }
  return null;
};

function MatchItem({ match }) {
  const predictionDetails = getPredictionDetails(match);

  return (
    <Link
      to={`/match/${match.id}`}
      className="block bg-prognoz-card rounded-lg shadow-lg hover:shadow-red-glow-soft transition-all duration-200 ease-in-out transform hover:-translate-y-1"
    >
      <div className="p-4 md:p-5 h-full flex flex-col">
        {" "}
        {/* Уменьшил паддинги для более плотного вида */}
        {/* Верхняя часть: Названия команд, дата, лига */}
        <div className="mb-3">
          <h3 className="text-base md:text-lg font-semibold text-prognoz-red-light truncate">
            {match.homeTeam_name} - {match.awayTeam_name}
          </h3>
          <p className="text-xs text-prognoz-text-secondary mt-1">
            {formatMatchDateTime(match.utcDate, match.localMatchTime)}
            {match.competition_name && ` • ${match.competition_name}`}
          </p>
        </div>
        {/* Средняя часть: Прогноз и уверенность */}
        {predictionDetails && (
          <div className="my-2 py-3 border-t border-b border-gray-700 relative flex justify-between items-center">
            {/* Красная полоска слева */}
            <div className="absolute top-1/2 -translate-y-1/2 left-0 w-1 h-3/4 bg-prognoz-red-base rounded-r-sm"></div>
            <div className="pl-3">
              {" "}
              {/* Отступ от полоски */}
              <p className="text-sm font-semibold text-prognoz-text-primary">
                {predictionDetails.text} {/* Основной текст прогноза */}
              </p>
              {predictionDetails.confidence && (
                <p className="text-xs text-prognoz-text-secondary mt-0.5">
                  {predictionDetails.confidence}
                </p>
              )}
            </div>
            {/* Кнопка "Ставить" */}
            <button
              className="bg-prognoz-yellow-accent text-prognoz-bg font-semibold text-xs px-4 py-2 rounded-md hover:opacity-90 transition-opacity whitespace-nowrap"
              onClick={(e) => {
                e.preventDefault();
                alert("Переход к букмекеру (заглушка)");
              }} // Предотвращаем переход по Link
            >
              СТАВИТЬ
            </button>
          </div>
        )}
        {/* Если матч завершен, показываем счет вместо блока прогноза (или дополнительно) */}
        {match.status === "FINISHED" &&
          !predictionDetails && ( // Показываем, если нет блока прогноза
            <div className="my-2 py-3 border-t border-b border-gray-700">
              <p className="text-lg font-bold text-center text-prognoz-yellow-accent">
                {match.score_fullTime_home} : {match.score_fullTime_away}
              </p>
              <p className="text-xs text-center text-prognoz-text-secondary">
                Матч завершен
              </p>
            </div>
          )}
        {/* Пустое пространство, чтобы карточки были одинаковой высоты, если нет прогноза/счета */}
        {!predictionDetails && match.status !== "FINISHED" && (
          <div className="flex-grow"></div>
        )}
        {/* Можно добавить логотипы команд внизу или где-то еще, если нужно вернуть */}
        {/* <div className="flex justify-between items-center mt-auto pt-3">
            <img src={match.homeTeam_crest} alt="" className="w-8 h-8 object-contain" />
            <img src={match.awayTeam_crest} alt="" className="w-8 h-8 object-contain" />
        </div> */}
      </div>
    </Link>
  );
}

export default MatchItem;
