// frontend/src/components/Layout/Header.js
import React, { useState, useEffect, useRef } from "react";
// ИМПОРТИРУЕМ useSearchParams
import { Link, NavLink, useLocation, useSearchParams } from "react-router-dom";
// Логотип-картинка больше не используется, так как название сайта текстовое с градиентом
// import NeuroPredictLogo from '../../assets/NeuroPredictLogo.png';

const leaguesNav = [
  { code: "PL", name: "Premier League" },
  { code: "BL1", name: "Bundesliga" },
  { code: "SA", name: "Serie A" },
  { code: "PD", name: "La Liga" },
  { code: "FL1", name: "Ligue 1" },
];
const mainNavLinks = [
  { to: "/", text: "Главная" },
  { to: "/history", text: "История матчей" }, // Заглушка для будущей страницы
  { to: "/tables", text: "Турнирные таблицы" }, // Заглушка для будущей страницы
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);
  const dropdownRef = useRef(null); // Для отслеживания кликов вне выпадающего списка
  const location = useLocation(); // Для определения активного пути
  const [searchParams] = useSearchParams(); // ПОЛУЧАЕМ searchParams ДЛЯ ПРОВЕРКИ query-параметра 'league'

  // Эффект для закрытия выпадающего списка при клике вне его
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsLeaguesDropdownOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [dropdownRef]);

  // Эффект для закрытия выпадающего списка при изменении URL (например, при выборе лиги)
  useEffect(() => {
    setIsLeaguesDropdownOpen(false);
  }, [location.pathname, location.search]); // Следим за полным путем и query-параметрами

  // Базовые классы для всех кнопок навигации
  const navButtonBaseClasses =
    "px-4 py-2 rounded-full text-xs sm:text-sm font-semibold transition-all duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-brand-surface";
  // Классы для активной кнопки
  const navButtonActiveClasses =
    "bg-brand-primary-yellow text-brand-text-nav-active shadow-red-button-glow";
  // Классы для неактивной кнопки
  const navButtonInactiveClasses =
    "bg-brand-nav-button-bg text-brand-text-nav-inactive hover:bg-brand-nav-button-hover-bg hover:text-brand-text-nav-hover hover:shadow-red-button-glow";

  return (
    <header className="bg-brand-surface shadow-md sticky top-0 z-50 w-full">
      {/* Декоративная красная линия-подсветка снизу хедера */}
      <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-transparent via-brand-primary-red to-transparent opacity-60 group-hover:opacity-100 transition-opacity"></div>

      <div className="container mx-auto px-4 h-16 flex justify-between items-center relative">
        {/* Название сайта слева */}
        <Link to="/" className="hover:opacity-90 transition-opacity">
          {/* Применяем класс для градиентного текста, определенный в index.css или через плагин Tailwind */}
          <span className="text-2xl md:text-3xl font-extrabold text-gradient-red-yellow tracking-tight">
            NeuroPredict
          </span>
        </Link>

        {/* Центральная навигация */}
        <nav className="flex items-center space-x-1 sm:space-x-2 md:space-x-3">
          {mainNavLinks.map((link) => (
            <NavLink
              key={link.to}
              to={link.to}
              // Логика для определения активной кнопки "Главная", даже если есть query-параметры
              className={({ isActive }) =>
                `${navButtonBaseClasses} ${
                  (isActive && !location.search && link.to === "/") ||
                  (isActive && link.to !== "/") // Для других ссылок просто isActive
                    ? navButtonActiveClasses
                    : navButtonInactiveClasses
                }`
              }
            >
              {link.text}
            </NavLink>
          ))}

          {/* Выпадающий список "Лиги" */}
          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setIsLeaguesDropdownOpen((prev) => !prev)}
              // Кнопка "Лиги": если дропдаун открыт или если выбрана какая-то лига (параметр ?league= в URL),
              // то кнопка будет выглядеть как активная. Иначе - как неактивная.
              className={`${navButtonBaseClasses} ${
                isLeaguesDropdownOpen || searchParams.get("league")
                  ? navButtonActiveClasses
                  : navButtonInactiveClasses.replace(
                      "text-brand-text-nav-inactive",
                      "text-brand-primary-yellow"
                    ) // Делаем текст желтым, если не активна, но это кнопка "Лиги"
              } flex items-center`}
            >
              Лиги
              <svg
                className={`w-3 h-3 ml-1.5 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                  clipRule="evenodd"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              // Позиционируем выпадающее меню строго под кнопкой "Лиги"
              // Используем right-0, если кнопка "Лиги" - последний элемент в nav
              // или left-0, если она где-то в начале/середине и нужно выравнивание по левому краю кнопки.
              // Для центрирования под кнопкой, можно использовать left-1/2 -translate-x-1/2 относительно родительского div.
              // Так как кнопка "Лиги" у нас сейчас не первая и не последняя, left-0 будет правильнее.
              <div className="absolute left-0 mt-2 w-56 bg-brand-surface rounded-md shadow-xl z-30 py-1 border border-gray-700">
                {leaguesNav.map((league) => (
                  <NavLink
                    key={league.code}
                    to={`/?league=${league.code}`} // Направляем на главную с параметром лиги
                    // Активность элемента списка определяем по совпадению query-параметра 'league'
                    className={`block px-4 py-2 text-sm w-full text-left transition-colors
                      ${
                        searchParams.get("league") === league.code
                          ? "bg-brand-primary-red text-white" // Активная лига в списке
                          : "text-brand-text-light hover:bg-gray-700 hover:text-brand-primary-yellow"
                      }`}
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>
        </nav>

        {/* Правый блок для баланса центрирования навигации (ширину нужно подобрать) */}
        <div className="w-0 sm:w-32 md:w-48 lg:w-[18rem] hidden sm:block">
          {" "}
          {/* Скрыт на очень маленьких экранах */}
          {/* Этот блок помогает отцентрировать <nav>, если он не занимает всю ширину между лого и правым краем. */}
          {/* Его ширина должна быть примерно равна ширине блока с названием сайта. */}
        </div>
      </div>
    </header>
  );
}

export default Header;
