// frontend/src/components/Layout/Header.js
import React, { useState, useEffect, useRef } from "react";
import { Link, NavLink, useLocation } from "react-router-dom";

const leaguesNav = [
  /* ... ваш список лиг ... */
];
const mainNavLinks = [
  { to: "/", text: "Главная" },
  { to: "/history", text: "История матчей" },
  { to: "/tables", text: "Турнирные таблицы" },
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const location = useLocation();

  useEffect(() => {
    /* ... ваш useEffect для закрытия дропдауна ... */
  });
  useEffect(() => {
    setIsLeaguesDropdownOpen(false);
  }, [location.pathname, location.search]);

  const navButtonBaseClasses =
    "px-4 py-2 rounded-md text-xs sm:text-sm font-semibold transition-all duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-brand-primary-yellow focus:ring-opacity-50";
  const navButtonActiveClasses =
    "bg-brand-primary-yellow text-brand-bg shadow-yellow-button-glow";
  const navButtonInactiveClasses =
    "bg-brand-nav-button-bg text-brand-text-nav hover:bg-opacity-80 hover:shadow-md";

  return (
    <div className="sticky top-0 z-50 w-full">
      {/* Верхняя секция с градиентом и названием сайта */}
      <div className="bg-header-gradient relative py-5 md:py-6 shadow-lg">
        <div className="container mx-auto text-center">
          <Link to="/" className="inline-block">
            {/* Используем кастомный класс для градиентного текста */}
            <h1 className="text-4xl md:text-5xl font-extrabold text-gradient-red-yellow tracking-wide">
              NeuroPredict
            </h1>
            <p className="text-xs md:text-sm text-brand-text-header mt-1">
              Прогнозы с точностью до гола
            </p>
          </Link>
        </div>
        {/* Нижняя граница с градиентом для эффекта свечения */}
        <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-transparent via-brand-primary-red to-transparent opacity-75"></div>
      </div>

      {/* Навигационная панель */}
      <nav className="bg-brand-nav-bg shadow-md">
        <div className="container mx-auto px-4 h-12 md:h-14 flex justify-center items-center space-x-2 sm:space-x-3 md:space-x-4">
          {mainNavLinks.map((link) => (
            <NavLink
              key={link.to}
              to={link.to}
              className={({ isActive }) =>
                `${navButtonBaseClasses} ${
                  isActive ||
                  (link.to === "/" &&
                    location.pathname === "/" &&
                    location.search)
                    ? navButtonActiveClasses
                    : navButtonInactiveClasses
                }`
              }
            >
              {link.text}
            </NavLink>
          ))}
          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setIsLeaguesDropdownOpen((prev) => !prev)}
              className={`${navButtonBaseClasses} ${navButtonInactiveClasses} flex items-center`}
            >
              Лиги
              <svg
                className={`w-3 h-3 ml-1.5 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                  clipRule="evenodd"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              <div className="absolute left-1/2 -translate-x-1/2 md:left-auto md:right-0 mt-2 w-56 bg-brand-nav-bg rounded-md shadow-xl z-30 py-1 border border-gray-700">
                {leaguesNav.map((league) => (
                  <NavLink
                    key={league.code}
                    to={`/?league=${league.code}`}
                    className="block px-4 py-2 text-sm text-brand-text-light hover:bg-gray-700 hover:text-brand-primary-yellow w-full text-left"
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
}
export default Header;
