// frontend/src/components/Layout/Header.js
import React from "react";
import { Link, NavLink, useLocation } from "react-router-dom";

// Для иконок можно использовать react-icons, например: npm install react-icons
// import { IoHomeOutline, IoFootballOutline, IoListOutline, IoStatsChartOutline, IoPeopleOutline } from 'react-icons/io5';

// Список ссылок для навигации
const navLinks = [
  {
    to: "/",
    text: "Главная" /*icon: <IoHomeOutline className="mr-1 mb-0.5"/>*/,
  },
  {
    to: "/today",
    text: "Сегодняшние матчи" /*icon: <IoFootballOutline className="mr-1 mb-0.5"/>*/,
  }, // Заглушка
  {
    to: "/all-predictions",
    text: "Топ прогнозы" /*icon: <IoListOutline className="mr-1 mb-0.5"/>*/,
  }, // Заглушка
  {
    to: "/statistics",
    text: "Статистика" /*icon: <IoStatsChartOutline className="mr-1 mb-0.5"/>*/,
  }, // Заглушка
  {
    to: "/experts",
    text: "Эксперты" /*icon: <IoPeopleOutline className="mr-1 mb-0.5"/>*/,
  }, // Заглушка
];

function Header() {
  const location = useLocation();

  return (
    <div className="sticky top-0 z-50 w-full">
      {/* Верхняя красная секция с названием сайта */}
      <div className="bg-brand-red text-center py-8 md:py-10">
        {" "}
        {/* Увеличил отступы */}
        <Link to="/" className="inline-block">
          <h1 className="text-4xl md:text-5xl font-bold text-brand-yellow tracking-wide">
            FootballPredict
          </h1>
          <p className="text-sm md:text-base text-gray-200 mt-1">
            Прогнозы с точностью до гола
          </p>
        </Link>
      </div>

      {/* Навигационная панель */}
      <nav className="bg-brand-nav-bg shadow-md">
        <div className="container mx-auto px-4 h-14 flex justify-center items-center space-x-2 sm:space-x-3 md:space-x-4">
          {navLinks.map((link) => (
            <NavLink
              key={link.to}
              to={link.to}
              className={({ isActive }) =>
                `flex items-center px-4 py-2 rounded-full text-sm font-semibold transition-all duration-150 ease-in-out
                hover:shadow-lg hover:scale-105
                ${
                  isActive
                    ? "bg-brand-yellow text-brand-dark-bg shadow-md" // Активная кнопка
                    : "bg-brand-yellow text-brand-dark-bg opacity-80 hover:opacity-100" // Неактивная кнопка
                }`
              }
              // Если ссылка соответствует текущему пути (или базовому пути для HomePage)
              //isActive={location.pathname === link.to || (link.to === "/" && location.pathname.startsWith("/?league="))}
            >
              {/* {link.icon} */}
              {link.text}
            </NavLink>
          ))}
        </div>
      </nav>
    </div>
  );
}

export default Header;
