import React from "react";
import { Link } from "react-router-dom";

const dateTime = (utcDate) => {
  let dateStr = utcDate;
  const date = new Date(dateStr);

  return date.toLocaleString("ru-RU", {
    day: "2-digit",
    month: "2-digit",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
  });
};

const getOutcomeColor = (prediction) => {
  if (prediction === "H")
    return { text: "Прогноз: П1", colorClasses: "bg-green-600 text-white" };
  if (prediction === "A")
    return {
      text: "Прогноз: П2",
      colorClasses: "bg-color-primary-red text-white",
    };
  if (prediction === "D")
    return {
      text: "Прогноз: X",
      colorClasses: "bg-color-primary-yellow text-color-bg",
    };
  return null;
};

function MatchItem({ match }) {
  const predictionDisplay = match.Predicted_Outcome
    ? getOutcomeColor(match.Predicted_Outcome)
    : null;

  return (
    <Link
      to={`/match/${match.id}`}
      className="block bg-card-bg-gradient rounded-lg shadow-xl border border-color-card-border overflow-hidden
                 transform hover:-translate-y-1 hover:shadow-card-hover transition-all duration-300 ease-in-out"
    >
      <div className="p-4 md:p-5 flex flex-col h-full relative">
        <div className="absolute top-0 left-0 w-1 h-full bg-gradient-to-b from-transparent via-color-primary-red to-transparent opacity-75"></div>
        <div className="pl-3">
          <div className="flex justify-around items-center mb-3">
            {/* Контейнер для командных логотипов и текста "VS".
                `flex`: элементы располагаются горизонтально.
                `justify-around`: равномерно распределяет пространство между элементами.
                `items-center`: выравнивает элементы по центру по вертикали.
                `mb-3`: нижний отступ. */}
            <div className="flex flex-col items-center text-center w-2/5">
              {/* Контейнер для домашней команды.
                  `flex flex-col`: элементы (логотип, название) располагаются вертикально.
                  `items-center`: выравнивает по центру.
                  `text-center`: текст по центру.
                  `w-2/5`: занимает 40% ширины. */}
              {match.homeTeam_crest && (
                // Условный рендеринг: если есть URL для логотипа домашней команды, показываем его.
                <img
                  src={match.homeTeam_crest} // Источник изображения.
                  alt={match.homeTeam_name} // Альтернативный текст для доступности.
                  className="h-16 w-16 object-contain mb-1" // Размеры, масштабирование, нижний отступ.
                />
              )}
              <span className="text-sm font-semibold text-color-text-white truncate w-full">
                {match.homeTeam_name}
                {/* Название домашней команды.
                    `truncate w-full`: обрезает текст, если он слишком длинный, и добавляет многоточие. */}
              </span>
            </div>
            <span className="text-2xl font-bold text-color-text-second">
              VS
            </span>
            {/* Текст "VS" (против) по центру. */}
            <div className="flex flex-col items-center text-center w-2/5">
              {/* Аналогичный контейнер для гостевой команды. */}
              {match.awayTeam_crest && (
                <img
                  src={match.awayTeam_crest}
                  alt={match.awayTeam_name}
                  className="h-16 w-16 object-contain mb-1"
                />
              )}
              <span className="text-sm font-semibold text-color-text-white truncate w-full">
                {match.awayTeam_name}
              </span>
            </div>
          </div>

          {/* Прогноз */}
          {predictionDisplay && (
            // Условный рендеринг: если `predictionDisplay` существует (т.е. есть прогноз), показываем его.
            <div className="my-3 text-center">
              <span
                className={`px-4 py-1.5 text-xs font-bold rounded-full shadow-md ${predictionDisplay.colorClasses}`}
                // Стили для плашки прогноза: отступы, размер шрифта, жирный текст, скругленные углы, тень и динамические классы цвета.
              >
                {predictionDisplay.text}{" "}
                {/* Выводим полный текст прогноза (например, "Прогноз: П1"). */}
              </span>
            </div>
          )}

          {/* Счет, если матч завершен */}
          {match.status === "FINISHED" && (
            // Условный рендеринг: если статус матча "FINISHED" (завершен), показываем счет.
            <p className="text-center text-lg font-bold my-2 text-color-primary-yellow">
              {match.score_fullTime_home} - {match.score_fullTime_away}
              {/* Отображаем финальный счет домашней и гостевой команды. */}
            </p>
          )}

          {/* Заполнитель высоты, если нет ни прогноза, ни счета */}
          {!predictionDisplay && match.status !== "FINISHED" && (
            // Этот `div` используется как "пустой" заполнитель,
            // чтобы карточки выглядели одинаково по высоте, даже если у матча нет прогноза или он еще не завершен (нет счета).
            <div className="my-3 h-8"></div>
          )}

          {/* Дата и время */}
          <div className="mt-auto text-center text-xs text-color-text-second pt-3">
            {/* Контейнер для даты и времени:
                `mt-auto`: прижимает этот блок к низу flex-контейнера.
                `text-center`: текст по центру.
                `text-xs`: очень маленький размер шрифта.
                `text-color-text-second`: приглушенный цвет текста.
                `pt-3`: верхний отступ. */}
            {dateTime(match.utcDate)}
            {/* Вызываем вспомогательную функцию для форматирования и отображения даты/времени. */}
          </div>
        </div>
      </div>
    </Link>
  );
}

export default MatchItem;
