// frontend/src/components/Layout/Header.js
import React, { useState, useEffect, useRef } from "react";
import { Link, NavLink, useLocation } from "react-router-dom";
import NeuroPredictLogo from "../../assets/NeuroPredictLogo.png"; // Убедитесь, что путь к логотипу правильный

const leaguesNav = [
  { code: "PL", name: "Premier League" },
  { code: "BL1", name: "Bundesliga" },
  { code: "SA", name: "Serie A" },
  { code: "PD", name: "La Liga" },
  { code: "FL1", name: "Ligue 1" },
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);
  const dropdownRef = useRef(null); // Для закрытия дропдауна по клику вне
  const location = useLocation();

  // Закрытие дропдауна по клику вне его
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsLeaguesDropdownOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [dropdownRef]);

  // Закрытие дропдауна при смене URL (например, при выборе лиги)
  useEffect(() => {
    setIsLeaguesDropdownOpen(false);
  }, [location.search]); // Следим за изменением query-параметров

  const navLinkClasses = ({ isActive }) =>
    `px-3 py-2 rounded-md text-sm font-medium transition-colors ${
      isActive
        ? "bg-brand-red text-white"
        : "text-gray-300 hover:bg-gray-700 hover:text-brand-yellow"
    }`;

  return (
    <header className="bg-gray-900 shadow-lg sticky top-0 z-50 w-full">
      <div className="container mx-auto px-4 h-16 flex items-center">
        {" "}
        {/* Фиксированная высота хедера h-16 */}
        {/* Логотип и название сайта */}
        <Link
          to="/"
          className="flex items-center text-xl md:text-2xl font-bold text-brand-yellow hover:text-brand-red transition-colors"
        >
          <img
            src={NeuroPredictLogo}
            alt="NeuroPredict Logo"
            className="h-8 w-auto mr-2 md:mr-3"
          />
          NeuroPredict
        </Link>
        {/* Центральная навигация */}
        <nav className="flex-grow flex justify-center items-center space-x-2 sm:space-x-3 md:space-x-4 lg:space-x-6">
          <NavLink to="/" className={navLinkClasses}>
            Главная
          </NavLink>

          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setIsLeaguesDropdownOpen((prev) => !prev)}
              className="px-3 py-2 rounded-md text-sm font-medium text-gray-300 hover:bg-gray-700 hover:text-brand-yellow focus:outline-none flex items-center transition-colors"
            >
              Лиги
              <svg
                className={`w-4 h-4 ml-1 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M19 9l-7 7-7-7"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              <div className="absolute left-1/2 -translate-x-1/2 mt-2 w-48 bg-gray-800 rounded-md shadow-xl z-30 py-1">
                {leaguesNav.map((league) => (
                  <NavLink
                    key={league.code}
                    to={`/?league=${league.code}`} // Переход на главную с параметром лиги
                    className="block px-4 py-2 text-sm text-gray-300 hover:bg-gray-700 hover:text-brand-yellow w-full text-left"
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>

          <NavLink to="/history" className={navLinkClasses}>
            История матчей
          </NavLink>
          <NavLink to="/tables" className={navLinkClasses}>
            Турнирные таблицы
          </NavLink>
        </nav>
        {/* Правая часть хедера (можно оставить пустой для баланса или добавить что-то) */}
        {/* Чтобы навигация была точно по центру, этот блок должен быть примерно такой же ширины, как логотип */}
        {/* Или можно удалить этот блок и использовать justify-between на родительском div, если логотип и меню - единственные элементы */}
        <div className="w-auto" style={{ minWidth: "150px" }}>
          {" "}
          {/* Примерная ширина лого блока, чтобы nav был по центру. Подберите! */}
          {/* Тут может быть кнопка "Войти" или иконка пользователя */}
        </div>
      </div>
    </header>
  );
}

export default Header;
