// frontend/src/components/MatchItem.js
import React from "react";
import { Link } from "react-router-dom";

const formatMatchDateTime = (utcDateString, localMatchTimeString) => {
  /* ... ваша функция ... */
};

const getPredictionDisplay = (prediction) => {
  // Цвета для прогноза из вашего нового скриншота (зеленый для П1, красный для П2)
  if (prediction === "H")
    return { text: "П1", colorClasses: "bg-green-500 text-white" };
  if (prediction === "A")
    return { text: "П2", colorClasses: "bg-brand-primary-red text-white" }; // Используем основной красный
  if (prediction === "D")
    return { text: "X", colorClasses: "bg-gray-500 text-white" }; // Ничья - серый или желтый?
  return { text: prediction, colorClasses: "bg-gray-600 text-white" };
};

function MatchItem({ match }) {
  const predictionDisplay = match.Predicted_Outcome
    ? getPredictionDisplay(match.Predicted_Outcome)
    : null;

  return (
    <Link
      to={`/match/${match.id}`}
      className="block rounded-lg overflow-hidden shadow-lg hover:shadow-red-edge-glow transition-all duration-300 ease-in-out transform hover:-translate-y-1"
    >
      {/* Используем градиентный фон для карточки, как в вашем HTML примере */}
      <div className="bg-gradient-to-r from-brand-bg-card to-gray-900 p-5 flex flex-col h-full relative">
        {" "}
        {/* to-gray-900 для более темного края */}
        {/* Красная полоска слева, как в вашем HTML примере .match-card::before */}
        <div className="absolute top-0 left-0 w-1 h-full bg-gradient-to-b from-transparent via-brand-primary-red to-transparent opacity-75 group-hover:opacity-100 transition-opacity"></div>
        {/* Контент карточки с отступом от левой полоски */}
        <div className="pl-3">
          {" "}
          {/* Отступ от левой полоски */}
          {/* Логотипы и VS */}
          <div className="flex justify-around items-center mb-3">
            <div className="flex flex-col items-center text-center w-2/5">
              {match.homeTeam_crest && (
                <img
                  src={match.homeTeam_crest}
                  alt={match.homeTeam_name}
                  className="h-14 w-14 object-contain mb-1"
                />
              )}
              <span className="text-sm font-semibold text-brand-text-light truncate w-full">
                {match.homeTeam_name}
              </span>
            </div>
            <span className="text-xl font-bold text-brand-text-muted">VS</span>
            <div className="flex flex-col items-center text-center w-2/5">
              {match.awayTeam_crest && (
                <img
                  src={match.awayTeam_crest}
                  alt={match.awayTeam_name}
                  className="h-14 w-14 object-contain mb-1"
                />
              )}
              <span className="text-sm font-semibold text-brand-text-light truncate w-full">
                {match.awayTeam_name}
              </span>
            </div>
          </div>
          {/* Прогноз */}
          {predictionDisplay && (
            <div className="my-3 text-center">
              <span
                className={`px-3 py-1 text-xs font-bold rounded-full shadow-md ${predictionDisplay.colorClasses}`}
              >
                Прогноз: {predictionDisplay.text}
              </span>
            </div>
          )}
          {match.status === "FINISHED" && (
            <p className="text-center text-lg font-bold my-2 text-brand-primary-yellow">
              {match.score_fullTime_home} - {match.score_fullTime_away}
            </p>
          )}
          {!predictionDisplay && match.status !== "FINISHED" && (
            <div className="my-3 h-8"></div>
          )}
          {/* Дата и время */}
          <div className="mt-auto text-center text-xs text-brand-text-muted pt-3">
            {formatMatchDateTime(match.utcDate, match.localMatchTime)}
          </div>
        </div>
      </div>
    </Link>
  );
}
export default MatchItem;
