// frontend/src/pages/HistoryPage.js

import React from "react";
// Импортируем библиотеку React. Это минимально необходимо для создания любого React-компонента.

function HistoryPage() {
  // Определяем функциональный React-компонент с именем `HistoryPage`.
  // Это функция, которая будет возвращать JSX (подобие HTML), который React затем превратит в элементы DOM для отображения в браузере.

  return (
    // Все, что находится внутри `return`, является JSX-разметкой, которая будет отрисована компонентом.
    <div className="flex items-center justify-center min-h-[calc(100vh-4rem-4rem)]">
      {" "}
      {/* Высота окна минус хедер и паддинги */}
      {/* Это внешний контейнер страницы. Он использует Tailwind CSS для центрирования содержимого.
          `flex`: делает контейнер flexbox-элементом.
          `items-center`: выравнивает элементы по центру по вертикали (поперечная ось).
          `justify-center`: выравнивает элементы по центру по горизонтали (главная ось).
          `min-h-[calc(100vh-4rem-4rem)]`: устанавливает минимальную высоту этого контейнера.
              `100vh`: 100% высоты окна просмотра (viewport height).
              `- 4rem`: вычитаем высоту `Header` (который, вероятно, имеет `h-16`, что равно 4rem).
              `- 4rem`: вычитаем общие вертикальные отступы (например, `py-8` в `App.js` `main` блоке, что равно 2rem * 2 = 4rem).
              Это гарантирует, что содержимое будет центрировано вертикально по оставшемуся доступному пространству. */}
      <div className="bg-color-surface p-8 rounded-lg shadow-lg text-center">
        {/* Это внутренний контейнер, который служит как "карточка" или "блок" с сообщением.
            `bg-color-surface`: устанавливает фоновый цвет из вашей палитры Tailwind (вероятно, темный оттенок).
            `p-8`: добавляет большой внутренний отступ со всех сторон (padding).
            `rounded-lg`: делает углы блока скругленными.
            `shadow-lg`: добавляет большую тень для визуального выделения.
            `text-center`: центрирует весь текст внутри этого блока. */}

        <h1 className="text-3xl font-bold text-color-primary-red mb-4">
          История матчей
        </h1>
        {/* Заголовок страницы:
            `text-3xl`: большой размер шрифта.
            `font-bold`: жирный шрифт.
            `text-color-primary-red`: устанавливает цвет текста в красный из вашей палитры Tailwind.
            `mb-4`: добавляет нижний отступ. */}

        <p className="text-color-text-light">
          Здесь будет отображаться история завершенных матчей и их результаты.
        </p>
        {/* Основной абзац с пояснительным текстом.
            `text-color-text-light`: светлый цвет текста из вашей палитры Tailwind. */}

        <p className="text-sm text-color-text-muted mt-2">
          (Эта страница пока в разработке.)
        </p>
        {/* Дополнительный абзац с примечанием о статусе разработки.
            `text-sm`: маленький размер шрифта.
            `text-color-text-muted`: приглушенный цвет текста.
            `mt-2`: верхний отступ. */}
      </div>
    </div>
  );
}

export default HistoryPage;
// Экспортируем компонент `HistoryPage` по умолчанию.
// Это делает его доступным для импорта и использования в других файлах,
// например, в `App.js`, где он подключается к маршруту `/history`.
