// frontend/src/components/Layout/Header.js
import React, { useState } from "react";
import { Link, NavLink } from "react-router-dom";
// import Logo from '../../assets/NeuroPredictLogo.png'; // Если у вас есть файл логотипа

const leagues = [
  { code: "PL", name: "Premier League" },
  { code: "BL1", name: "Bundesliga" },
  { code: "SA", name: "Serie A" },
  { code: "PD", name: "La Liga" },
  { code: "FL1", name: "Ligue 1" },
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);

  return (
    <header className="bg-gray-900 shadow-lg">
      {" "}
      {/* Темный фон для хедера */}
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        {/* Логотип и название сайта */}
        <Link
          to="/"
          className="text-2xl font-bold text-brand-yellow hover:text-brand-red transition-colors"
        >
          {/* <img src={Logo} alt="NeuroPredict Logo" className="h-8 w-auto inline-block mr-2" /> */}
          NeuroPredict
        </Link>

        {/* Навигационное меню */}
        <nav className="flex items-center space-x-6">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `hover:text-brand-yellow transition-colors ${
                isActive ? "text-brand-yellow font-semibold" : "text-gray-300"
              }`
            }
          >
            Главная
          </NavLink>

          {/* Выпадающий список лиг */}
          <div className="relative">
            <button
              onClick={() => setIsLeaguesDropdownOpen(!isLeaguesDropdownOpen)}
              className="text-gray-300 hover:text-brand-yellow transition-colors focus:outline-none flex items-center"
            >
              Лиги
              <svg
                className={`w-4 h-4 ml-1 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M19 9l-7 7-7-7"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              <div className="absolute right-0 mt-2 w-48 bg-gray-800 rounded-md shadow-xl z-20 py-1">
                {leagues.map((league) => (
                  <NavLink
                    key={league.code}
                    // Пока ссылка ведет на главную, но с параметром. Позже можно сделать отдельные страницы лиг.
                    to={`/?league=${league.code}`} // ИЛИ /leagues/${league.code} если будут отдельные страницы
                    onClick={() => setIsLeaguesDropdownOpen(false)} // Закрыть дропдаун при клике
                    className={(
                      { isActive } // isActive здесь будет работать для query-параметра немного иначе
                    ) =>
                      `block px-4 py-2 text-sm text-gray-300 hover:bg-gray-700 hover:text-brand-yellow ${
                        // Для подсветки активной лиги, если параметр совпадает, нужна будет доп. логика или использование useLocation
                        // Пока просто базовый стиль
                        ""
                      }`
                    }
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>

          <NavLink
            to="/history" // Заглушка, такой страницы пока нет
            className={({ isActive }) =>
              `hover:text-brand-yellow transition-colors ${
                isActive ? "text-brand-yellow font-semibold" : "text-gray-300"
              }`
            }
          >
            История матчей
          </NavLink>
          <NavLink
            to="/tables" // Заглушка
            className={({ isActive }) =>
              `hover:text-brand-yellow transition-colors ${
                isActive ? "text-brand-yellow font-semibold" : "text-gray-300"
              }`
            }
          >
            Турнирные таблицы
          </NavLink>
        </nav>
      </div>
    </header>
  );
}

export default Header;
