// frontend/src/components/Layout/Header.js
import React from "react";
import { Link, NavLink } from "react-router-dom";

const navLinks = [
  { to: "/", text: "Главная" },
  { to: "/today", text: "Сегодняшние матчи" },
  { to: "/all-predictions", text: "Топ прогнозы" },
  { to: "/statistics", text: "Статистика" },
  { to: "/experts", text: "Эксперты" },
];

function Header() {
  return (
    <div className="sticky top-0 z-50 w-full shadow-lg">
      {" "}
      {/* Добавил shadow-lg для всего хедера */}
      {/* Верхняя красная секция с градиентом и подсветкой */}
      <div className="bg-red-gradient-hero shadow-red-glow-soft">
        {" "}
        {/* Используем градиент и свечение */}
        <div className="container mx-auto text-center py-6 md:py-8">
          <Link to="/" className="inline-block">
            <h1 className="text-4xl md:text-5xl font-bold text-prognoz-orange-yellow tracking-wider">
              PROГНОЗ {/* Изменил название */}
            </h1>
            <p className="text-sm md:text-base text-gray-200 mt-1">
              Точные футбольные прогнозы
            </p>
          </Link>
        </div>
      </div>
      {/* Навигационная панель */}
      <nav className="bg-prognoz-card">
        {" "}
        {/* Используем цвет фона карточек */}
        <div className="container mx-auto px-4 h-12 md:h-14 flex justify-center items-center space-x-2 sm:space-x-3 md:space-x-4">
          {navLinks.map((link) => (
            <NavLink
              key={link.to}
              to={link.to}
              className={({ isActive }) =>
                `px-3 md:px-4 py-2 rounded-full text-xs sm:text-sm font-medium transition-all duration-150 ease-in-out
                hover:shadow-md 
                ${
                  isActive
                    ? "bg-prognoz-orange-yellow text-prognoz-bg shadow-yellow-glow-soft" // Активная кнопка
                    : "bg-gray-700 text-prognoz-text-secondary hover:bg-gray-600 hover:text-prognoz-text-primary" // Неактивная кнопка
                }`
              }
            >
              {link.text}
            </NavLink>
          ))}
        </div>
      </nav>
    </div>
  );
}

export default Header;
