// frontend/src/pages/TablesPage.js

import React from "react";
// Импортируем библиотеку React. Это необходимо для того, чтобы React мог понимать и создавать JSX-элементы,
// из которых состоит наш компонент.

function TablesPage() {
  // Определяем функциональный React-компонент под названием `TablesPage`.
  // Как и все функциональные компоненты React, он является функцией, которая возвращает JSX-разметку.

  return (
    // Все, что находится внутри `return`, это JSX, который React будет отрисовывать в браузере.
    <div className="flex items-center justify-center min-h-[calc(100vh-4rem-4rem)]">
      {" "}
      {/* Высота окна минус хедер и паддинги */}
      {/* Это основной внешний контейнер для содержимого страницы.
          Он использует Tailwind CSS для центрирования своего дочернего элемента как по горизонтали, так и по вертикали.
          `flex`: превращает контейнер в flex-контейнер, что позволяет использовать flexbox-свойства для выравнивания содержимого.
          `items-center`: выравнивает дочерние элементы по центру вдоль поперечной оси (вертикально, если `flex-direction` по умолчанию `row`, или горизонтально, если `flex-direction` `column`). В данном случае, так как это единственный дочерний элемент, он центрируется вертикально.
          `justify-center`: выравнивает дочерние элементы по центру вдоль главной оси (горизонтально, если `flex-direction` по умолчанию `row`). В данном случае, он центрируется горизонтально.
          `min-h-[calc(100vh-4rem-4rem)]`: устанавливает минимальную высоту этого `div`.
              `100vh`: означает 100% высоты окна просмотра (viewport height).
              `- 4rem`: это вычитание высоты шапки (`Header`), которая, скорее всего, имеет высоту `h-16` (что равно 4rem). Это нужно, чтобы контент не перекрывался шапкой и корректно центрировался по оставшейся высоте.
              `- 4rem`: это вычитание общей вертикальной padding (отступа) элемента `<main>` в `App.js`, который оборачивает страницы. В `App.js` есть `py-8`, что означает `padding-top: 2rem` и `padding-bottom: 2rem`, итого 4rem.
              Вместе эти вычитания гарантируют, что содержимое будет центрировано по доступному пространству между шапкой и нижним краем блока `main`. */}
      <div className="bg-color-surface p-8 rounded-lg shadow-lg text-center">
        {/* Этот внутренний `div` представляет собой "карточку" или "блок" с сообщением.
            `bg-color-surface`: устанавливает фоновый цвет этого блока, используя пользовательскую переменную из конфигурации Tailwind (вероятно, темный цвет).
            `p-8`: добавляет равномерный внутренний отступ в 8 единиц Tailwind (обычно 2rem) со всех четырех сторон блока.
            `rounded-lg`: делает углы блока скругленными, придавая ему более мягкий вид.
            `shadow-lg`: добавляет большую тень под блоком, чтобы он выделялся и выглядел объемно.
            `text-center`: центрирует весь текст внутри этого `div` по горизонтали. */}

        <h1 className="text-3xl font-bold text-color-primary-red mb-4">
          Турнирные таблицы
        </h1>
        {/* Заголовок страницы или основного блока:
            `text-3xl`: устанавливает размер шрифта "3xl" (достаточно крупный).
            `font-bold`: делает шрифт жирным.
            `text-color-primary-red`: устанавливает цвет текста в красный, используя пользовательскую переменную из конфигурации Tailwind.
            `mb-4`: добавляет нижний отступ в 4 единицы Tailwind (1rem) под заголовком. */}

        <p className="text-color-text-white">
          Здесь будут отображаться турнирные таблицы по выбранным лигам.
        </p>
        {/* Основной абзац, поясняющий будущую функциональность страницы:
            `text-color-text-light`: устанавливает цвет текста в светлый оттенок из вашей палитры Tailwind. */}

        <p className="text-sm text-color-text-second mt-2">
          (Эта страница пока в разработке.)
        </p>
        {/* Дополнительный абзац, информирующий пользователя о текущем статусе разработки страницы:
            `text-sm`: устанавливает маленький размер шрифта.
            `text-color-text-second`: устанавливает приглушенный цвет текста (чтобы показать, что это менее важная информация).
            `mt-2`: добавляет верхний отступ в 2 единицы Tailwind (0.5rem) над этим абзацем. */}
      </div>
    </div>
  );
}

export default TablesPage;
// Экспортируем компонент `TablesPage` по умолчанию.
// Это позволяет другим файлам (например, `App.js`) импортировать и использовать этот компонент
// для рендеринга на соответствующих маршрутах.
