// frontend/src/components/Layout/LeagueSidebar.js
import React from "react";

function LeagueSidebar({ leagues, selectedLeague, onSelectLeague }) {
  return (
    // Убираем общий фон и тень с этого div, если кнопки будут занимать всю ширину
    // или оставляем, если хотим, чтобы сайдбар имел свой фон, отличный от фона страницы.
    // Оставим bg-brand-surface для фона самого блока сайдбара.
    <div className="bg-brand-surface p-2 sm:p-3 rounded-lg shadow-md">
      {/* Заголовок "Лиги" УБРАН */}
      {/* <h2 className="text-lg sm:text-xl font-semibold mb-4 text-brand-text-title px-1 text-center sm:text-left">
        Лиги
      </h2> */}
      <ul className="space-y-1.5 sm:space-y-2">
        {" "}
        {/* Уменьшил немного space-y */}
        {leagues.map((league) => {
          const isActive =
            selectedLeague === league.code ||
            (!selectedLeague && league.code === null);
          return (
            <li key={league.code || "all-leagues"}>
              <button
                onClick={() => onSelectLeague(league.code)}
                // Убираем явный фон с кнопки, акцент на квадрате.
                // Добавляем group для hover-эффектов на дочерних элементах.
                // focus:ring убран, так как обводка теперь не нужна.
                className={`w-full flex flex-col items-center justify-center p-2 rounded-md transition-all duration-150 ease-in-out group focus:outline-none 
                  ${isActive ? "" : "hover:bg-gray-700/40"}`} // Легкий фон при наведении на неактивную кнопку
                title={league.name}
              >
                {/* Квадрат для логотипа или текста "ВСЕ" */}
                <div
                  className={`
                    w-14 h-14 sm:w-16 sm:h-16 mb-1 rounded-md flex items-center justify-center overflow-hidden transition-all duration-150
                    border-2 
                    ${
                      isActive
                        ? "bg-brand-primary-red border-brand-primary-red shadow-red-button-glow" // Красный фон и рамка для активного квадрата
                        : "bg-gray-700 border-transparent group-hover:border-brand-primary-yellow/50" // Неактивный: фон серый, рамка прозрачная, при наведении на кнопку - полупрозрачная желтая рамка
                    }
                  `}
                >
                  {/* === КОД ДЛЯ ЛОГОТИПА "ВСЕ" (ИЛИ ТЕКСТА "ВСЕ") === */}
                  {league.logo ? ( // Если есть логотип (для лиг)
                    <img
                      src={league.logo}
                      alt={`${league.name} logo`}
                      className="w-full h-full object-contain p-1.5" // Увеличил немного p для лого
                    />
                  ) : (
                    // Если логотипа нет (для кнопки "ВСЕ")
                    <span
                      className={
                        `text-lg sm:text-xl font-bold 
                      ${
                        isActive
                          ? "text-white"
                          : "text-brand-text-light group-hover:text-brand-primary-yellow"
                      }`
                        // Текст "ВСЕ": белый на красном фоне, светло-серый на сером, желтый при наведении на кнопку
                      }
                    >
                      {/* Здесь отображается текст для кнопки "ВСЕ" */}
                      {league.code === null
                        ? "ВСЕ"
                        : league.shortName || league.code}
                    </span>
                  )}
                  {/* === КОНЕЦ КОДА ДЛЯ ЛОГОТИПА "ВСЕ" === */}
                </div>
                {/* Подпись под квадратиком */}
                <span
                  className={`text-xs text-center truncate w-full mt-1 px-1 transition-colors
                    ${
                      isActive
                        ? "text-brand-primary-red font-semibold"
                        : "text-brand-text-muted group-hover:text-brand-text-light"
                    }`}
                >
                  {league.name}
                </span>
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default LeagueSidebar;
