// frontend/src/components/Layout/Header.js
import React, { useState, useEffect, useRef } from "react";
import { Link, NavLink, useLocation, useSearchParams } from "react-router-dom";
// Логотип-картинка не используется
// import NeuroPredictLogo from '../../assets/NeuroPredictLogo.png';

// Список лиг для выпадающего меню (остается)
const leaguesNav = [
  { code: "PL", name: "Premier League" },
  { code: "BL1", name: "Bundesliga" },
  { code: "SA", name: "Serie A" },
  { code: "PD", name: "La Liga" },
  { code: "FL1", name: "Ligue 1" },
];

// ОБНОВЛЕН ПОРЯДОК И СОСТАВ mainNavLinks
const mainNavLinks = [
  { to: "/", text: "Главная" },
  // Кнопка "Лиги" теперь будет частью этого массива, но с особым рендерингом
  // { type: 'dropdown', text: "Лиги" }, // Мы обработаем ее отдельно ниже
  { to: "/tables", text: "Турнирные таблицы" },
  { to: "/history", text: "История матчей" },
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const location = useLocation();
  const [searchParams] = useSearchParams();

  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsLeaguesDropdownOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [dropdownRef]);

  useEffect(() => {
    setIsLeaguesDropdownOpen(false);
  }, [location.pathname, location.search]);

  const navButtonBaseClasses =
    "px-4 py-2 rounded-full text-xs sm:text-sm font-semibold transition-all duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-brand-surface";
  const navButtonActiveClasses =
    "bg-brand-button-active-bg text-brand-button-active-text shadow-red-button-glow";
  const navButtonInactiveClasses =
    "bg-brand-button-inactive-bg text-brand-button-inactive-text hover:bg-brand-button-hover-bg hover:text-brand-button-hover-text hover:shadow-red-button-glow";

  const isLeagueButtonActive =
    isLeaguesDropdownOpen || !!searchParams.get("league");

  return (
    <header className="bg-brand-surface shadow-md sticky top-0 z-50 w-full">
      {/* Декоративная красная линия-подсветка снизу хедера */}
      <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-transparent via-brand-primary-red to-transparent opacity-60 group-hover:opacity-100 transition-opacity"></div>

      <div className="container mx-auto px-4 h-16 flex justify-between items-center relative">
        {/* Название сайта слева */}
        <Link to="/" className="hover:opacity-90 transition-opacity">
          {/* Применяем класс для градиентного текста, сгенерированный плагином */}
          <span className="text-2xl md:text-3xl font-extrabold text-gradient-neuro-title tracking-tight">
            NeuroPredict
          </span>
        </Link>

        {/* Центральная навигация */}
        <nav className="flex items-center space-x-1 sm:space-x-2 md:space-x-3">
          {mainNavLinks.map((link) => (
            <NavLink
              key={link.to}
              to={link.to}
              className={({ isActive }) =>
                `${navButtonBaseClasses} ${
                  (link.to === "/" && isActive && !location.search) ||
                  (link.to !== "/" && isActive)
                    ? navButtonActiveClasses
                    : navButtonInactiveClasses
                }`
              }
            >
              {link.text}
            </NavLink>
          ))}

          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setIsLeaguesDropdownOpen((prev) => !prev)}
              className={`${navButtonBaseClasses} ${
                isLeagueButtonActive
                  ? navButtonActiveClasses // Активная кнопка "Лиги" будет красной
                  : navButtonInactiveClasses // Неактивная - темно-серая с желтым текстом
              } flex items-center`}
            >
              Лиги
              <svg
                className={`w-3 h-3 ml-1.5 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                  clipRule="evenodd"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              <div className="absolute left-0 mt-2 w-56 bg-brand-surface rounded-md shadow-xl z-30 py-1 border border-gray-700">
                {leaguesNav.map((league) => (
                  <NavLink
                    key={league.code}
                    to={`/?league=${league.code}`}
                    className={`block px-4 py-2 text-sm w-full text-left transition-colors
                      ${
                        searchParams.get("league") === league.code
                          ? "bg-brand-primary-red text-brand-button-inactive-text"
                          : "text-brand-button-inactive-text hover:bg-brand-primary-red hover:text-brand-primary-yellow" // Неактивные элементы списка - желтый текст
                      }`}
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>
        </nav>

        <div className="w-0 sm:w-32 md:w-48 lg:w-[18rem] hidden sm:block">
          {/* Балансирующий div */}
        </div>
      </div>
    </header>
  );
}

export default Header;
