// frontend/src/components/Layout/LeagueSidebar.js
import React from "react";

function LeagueSidebar({ leagues, selectedLeague, onSelectLeague }) {
  return (
    <div className="bg-brand-surface p-3 rounded-lg shadow-md">
      <h2 className="text-lg sm:text-xl font-semibold mb-4 text-brand-text-title px-1 text-center sm:text-left">
        {" "}
        {/* Центрирование на мобильных */}
        Лиги
      </h2>
      <ul className="space-y-2">
        {leagues.map((league) => {
          const isActive =
            selectedLeague === league.code ||
            (!selectedLeague && league.code === null);
          return (
            <li key={league.code || "all-leagues"}>
              <button
                onClick={() => onSelectLeague(league.code)}
                className={`w-full flex flex-col items-center justify-center p-2 rounded-md transition-all duration-150 ease-in-out group focus:outline-none focus:ring-1 focus:ring-brand-primary-yellow/50
                  ${
                    isActive
                      ? "bg-opacity-10" // Легкий фон для всей активной кнопки, если нужно
                      : "hover:bg-gray-700/50" // Фон при наведении на неактивную
                  }`}
                title={league.name}
              >
                {/* Квадрат для логотипа или текста "ВСЕ" */}
                <div
                  className={`
                    w-12 h-12 sm:w-14 sm:h-14 mb-1 rounded flex items-center justify-center overflow-hidden transition-all duration-150
                    border-2 
                    ${
                      isActive
                        ? "bg-brand-primary-red border-brand-primary-red shadow-red-button-glow" // Красный фон и рамка для активного квадрата
                        : "bg-gray-700 border-gray-600 group-hover:border-brand-primary-yellow" // Стили неактивного и при наведении на кнопку
                    }
                  `}
                >
                  {league.logo ? (
                    <img
                      src={league.logo}
                      alt={`${league.name} logo`}
                      className="w-full h-full object-contain p-1" // p-1 для небольшого отступа лого от краев квадрата
                    />
                  ) : (
                    <span
                      className={`text-base sm:text-lg font-bold 
                      ${
                        isActive
                          ? "text-white"
                          : "text-brand-text-light group-hover:text-brand-primary-yellow"
                      }`}
                    >
                      {league.code === null
                        ? "ВСЕ"
                        : league.shortName || league.code}
                    </span>
                  )}
                </div>
                {/* Подпись под квадратиком */}
                <span
                  className={`text-xs text-center truncate w-full mt-1 px-1 transition-colors
                    ${
                      isActive
                        ? "text-brand-primary-red font-semibold"
                        : "text-brand-text-muted group-hover:text-brand-text-light"
                    }`}
                >
                  {/* Для "ВСЕ" можно либо не показывать подпись, либо показывать "Все матчи" */}
                  {league.name}
                </span>
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default LeagueSidebar;
