// frontend/src/pages/TablesPage.js
import React from "react";

function TablesPage() {
  return (
    <div className="flex items-center justify-center min-h-[calc(100vh-4rem-4rem)]">
      {" "}
      {/* Высота окна минус хедер и паддинги */}
      <div className="bg-brand-surface p-8 rounded-lg shadow-lg text-center">
        <h1 className="text-3xl font-bold text-brand-primary-red mb-4">
          Турнирные таблицы
        </h1>
        <p className="text-brand-text-light">
          Здесь будут отображаться турнирные таблицы по выбранным лигам.
        </p>
        <p className="text-sm text-brand-text-muted mt-2">
          (Эта страница пока в разработке.)
        </p>
      </div>
    </div>
  );
}

export default TablesPage;
