// frontend/src/pages/HomePage.js
// ДОБАВЛЯЕМ НЕДОСТАЮЩИЕ ИМПОРТЫ ИЗ REACT
import React, { useState, useEffect } from "react";
// ДОБАВЛЯЕМ useSearchParams
import { useSearchParams } from "react-router-dom";
import MatchList from "../components/MatchList"; // Убедитесь, что этот компонент существует
import { getMatches } from "../api";
import LeagueSidebar from "../components/Layout/LeagueSidebar"; // Убедитесь, что этот компонент существует

// Импорты логотипов лиг (убедитесь, что пути и имена файлов верны)
import plLogo from "../assets/leagues/premier-league.png";
import blLogo from "../assets/leagues/bundesliga.png";
import saLogo from "../assets/leagues/serie-a.png";
import pdLogo from "../assets/leagues/la-liga.png";
import flLogo from "../assets/leagues/ligue-1.png";

const leaguesForSidebar = [
  // Эта константа должна быть доступна
  { code: null, name: "ВСЕ", shortName: "Все", logo: null },
  { code: "PL", name: "Premier League", shortName: "АПЛ", logo: plLogo },
  { code: "BL1", name: "Bundesliga", shortName: "Бундеслига", logo: blLogo },
  { code: "SA", name: "Serie A", shortName: "Серия А", logo: saLogo },
  { code: "PD", name: "La Liga", shortName: "Ла Лига", logo: pdLogo },
  { code: "FL1", name: "Ligue 1", shortName: "Лига 1", logo: flLogo },
];

function HomePage() {
  const [allFetchedMatches, setAllFetchedMatches] = useState([]);
  const [displayedMatches, setDisplayedMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchParams, setSearchParams] = useSearchParams();
  const selectedLeagueCode = searchParams.get("league");

  useEffect(() => {
    const fetchAllMatches = async () => {
      setLoading(true);
      const data = await getMatches(null);
      if (Array.isArray(data)) {
        setAllFetchedMatches(data);
      } else {
        console.error(
          "HomePage: Данные от getMatches не являются массивом!",
          data
        );
        setAllFetchedMatches([]);
      }
      setLoading(false);
    };
    fetchAllMatches();
  }, []); // Загрузка один раз

  useEffect(() => {
    if (loading && allFetchedMatches.length === 0) {
      // Показываем загрузку, только если еще ничего не загружено
      setDisplayedMatches([]); // Очищаем, пока грузится
      return;
    }
    if (!loading && allFetchedMatches.length === 0) {
      // Если загрузка завершена, а матчей нет
      setDisplayedMatches([]);
      return;
    }

    let filtered = allFetchedMatches;

    if (selectedLeagueCode) {
      filtered = filtered.filter(
        (match) => match.competition_code === selectedLeagueCode
      );
    }

    const now = new Date();
    const oneWeekFromNow = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000);
    const upcomingFiltered = filtered.filter((match) => {
      if (!match.utcDate) return false;
      const matchDate = new Date(match.utcDate);
      return (
        match.status !== "FINISHED" &&
        matchDate <= oneWeekFromNow &&
        matchDate >= new Date(now.getFullYear(), now.getMonth(), now.getDate())
      );
    });

    upcomingFiltered.sort((a, b) => new Date(a.utcDate) - new Date(b.utcDate));
    setDisplayedMatches(upcomingFiltered);
  }, [allFetchedMatches, selectedLeagueCode, loading]); // Добавляем loading в зависимости

  const handleLeagueSelect = (leagueCode) => {
    setSearchParams(leagueCode ? { league: leagueCode } : {});
  };

  const currentLeagueObject = selectedLeagueCode
    ? leaguesForSidebar.find((l) => l.code === selectedLeagueCode)
    : { name: "Топ матчи дня" }; // Для заголовка, если выбрано "ВСЕ"

  const pageTitle = currentLeagueObject.name;

  // Отступ сверху для sticky сайдбаров: высота хедера. Ваш хедер имеет h-16 (4rem).
  // Добавляем небольшой зазор, например, 0.5rem. Итого top-[4.5rem]
  const sidebarStickyTop = "top-[calc(4rem+0.5rem)]"; // 4rem (header h-16) + 0.5rem зазора
  // Максимальная высота сайдбара: высота окна минус позиция top сайдбара минус нижний отступ страницы (py-8 у main в App.js)
  const sidebarMaxHeight = `max-h-[calc(100vh_-_theme(spacing.16)_-_0.5rem_-_theme(spacing.8))]`; // 100vh - header_height - top_offset_sidebar - main_padding_bottom

  return (
    <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
      {/* Левый сайдбар */}
      <aside
        className={`w-full lg:w-60 xl:w-64 flex-shrink-0 mb-6 lg:mb-0 lg:sticky ${sidebarStickyTop} self-start rounded-lg`}
        // Убраны ${sidebarMaxHeight} и overflow-y-auto
      >
        <LeagueSidebar
          leagues={leaguesForSidebar}
          selectedLeague={selectedLeagueCode}
          onSelectLeague={handleLeagueSelect}
        />
      </aside>

      {/* Центральная колонка с матчами */}
      <section className="w-full lg:flex-grow min-w-0">
        <h1 className="text-2xl sm:text-3xl font-bold mb-6 text-center text-brand-primary-yellow uppercase tracking-wider">
          {pageTitle}
        </h1>
        {loading && allFetchedMatches.length === 0 ? (
          <div className="flex justify-center items-center h-64">
            <p className="text-xl text-brand-text-muted animate-pulse">
              Загрузка матчей...
            </p>
          </div>
        ) : (
          <MatchList
            matches={displayedMatches}
            selectedLeague={selectedLeagueCode}
          />
        )}
      </section>

      {/* Правый сайдбар */}
      <aside
        className={`w-full lg:w-72 xl:w-80 flex-shrink-0 hidden md:block lg:sticky ${sidebarStickyTop} self-start rounded-lg`}
        // Убраны ${sidebarMaxHeight} и overflow-y-auto
      >
        <div className="bg-brand-surface p-4 rounded-lg shadow-md min-h-[300px]">
          <h3 className="text-xl font-semibold mb-3 text-brand-primary-yellow">
            Турнирная таблица
          </h3>
          <p className="text-brand-text-muted">
            {currentLeagueObject && selectedLeagueCode
              ? `Таблица для ${currentLeagueObject.name}`
              : "Выберите лигу"}
          </p>
        </div>
      </aside>
    </div>
  );
}
export default HomePage;
