// frontend/src/pages/HomePage.js
import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import MatchList from "../components/MatchList";
import { getMatches } from "../api";
import LeagueSidebar from "../components/Layout/LeagueSidebar"; // Мы его уже обновили

// Импорты логотипов лиг (замените на свои, если скачали)
// import plLogo from '../assets/leagues/pl.png'; ...

<h1 className="text-2xl sm:text-3xl font-bold mb-6 text-center text-brand-primary-yellow uppercase tracking-wider">
  {pageTitle}
</h1>;

const leaguesForSidebar = [
  { code: null, name: "ВСЕ", shortName: "Все", logo: null },
  {
    code: "PL",
    name: "Premier League",
    shortName: "АПЛ",
    logo: "https://upload.wikimedia.org/wikipedia/en/thumb/f/f2/Premier_League_Logo.svg/1200px-Premier_League_Logo.svg.png",
  },
  {
    code: "BL1",
    name: "Bundesliga",
    shortName: "БУН",
    logo: "https://upload.wikimedia.org/wikipedia/en/thumb/d/df/Bundesliga_logo_%282017%29.svg/1200px-Bundesliga_logo_%282017%29.svg.png",
  },
  {
    code: "SA",
    name: "Serie A",
    shortName: "СА",
    logo: "https://upload.wikimedia.org/wikipedia/en/thumb/e/e9/Serie_A_logo_%282022%29.svg/1200px-Serie_A_logo_%282022%29.svg.png",
  },
  {
    code: "PD",
    name: "La Liga",
    shortName: "ЛАЛ",
    logo: "https://upload.wikimedia.org/wikipedia/commons/thumb/0/0f/LaLiga_logo_2023.svg/1200px-LaLiga_logo_2023.svg.png",
  },
  {
    code: "FL1",
    name: "Ligue 1",
    shortName: "Л1",
    logo: "https://upload.wikimedia.org/wikipedia/commons/thumb/4/49/Ligue1_Uber_Eats_logo.png/1200px-Ligue1_Uber_Eats_logo.png",
  },
];

function HomePage() {
  const [allFetchedMatches, setAllFetchedMatches] = useState([]);
  const [displayedMatches, setDisplayedMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchParams, setSearchParams] = useSearchParams();
  const selectedLeagueCode = searchParams.get("league");

  useEffect(() => {
    const fetchAllMatches = async () => {
      setLoading(true);
      const data = await getMatches(null);
      if (Array.isArray(data)) {
        setAllFetchedMatches(data);
      } else {
        setAllFetchedMatches([]);
      }
      setLoading(false);
    };
    fetchAllMatches();
  }, []);

  useEffect(() => {
    if (loading) return; // Не фильтровать, пока исходные данные не загружены

    let filtered = allFetchedMatches;
    if (selectedLeagueCode) {
      filtered = filtered.filter(
        (match) => match.competition_code === selectedLeagueCode
      );
    }

    const now = new Date();
    const oneWeekFromNow = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000);
    const upcomingFiltered = filtered.filter((match) => {
      if (!match.utcDate) return false;
      const matchDate = new Date(match.utcDate);
      // Отображаем матчи, которые еще не начались (или начались сегодня) и в пределах недели
      return (
        match.status !== "FINISHED" &&
        matchDate <= oneWeekFromNow &&
        matchDate >= new Date(now.getFullYear(), now.getMonth(), now.getDate())
      );
    });

    upcomingFiltered.sort((a, b) => new Date(a.utcDate) - new Date(b.utcDate));
    setDisplayedMatches(upcomingFiltered);
  }, [allFetchedMatches, selectedLeagueCode, loading]);

  const handleLeagueSelect = (leagueCode) => {
    setSearchParams(leagueCode ? { league: leagueCode } : {});
  };

  const currentLeagueObject = selectedLeagueCode
    ? leaguesForSidebar.find((l) => l.code === selectedLeagueCode)
    : { name: "Топ матчи дня" }; // Заголовок по умолчанию, если "ВСЕ"

  const pageTitle = currentLeagueObject.name;

  // Рассчитываем отступ для сайдбаров от верха страницы
  // Высота красной части хедера (py-8 или py-10 -> 4rem или 5rem) + высота навигации (h-14 -> 3.5rem) + отступ <main> (py-8 -> 2rem сверху)
  // Пример для py-8 в красной части: 4rem + 3.5rem + 2rem = 9.5rem. Tailwind: top-[9.5rem] или через theme()
  // Для простоты используем фиксированное значение, например, top-36 (9rem) или top-40 (10rem) и подбираем
  const sidebarTopOffset = "top-36"; // Или, например, "top-[150px]" если высота хедера фиксирована
  const sidebarMaxHeight = `max-h-[calc(100vh-${sidebarTopOffset.replace(
    "top-",
    ""
  )}-2rem)]`; // 2rem - это py-8 из main

  return (
    <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
      <aside
        className={`w-full lg:w-60 xl:w-64 flex-shrink-0 mb-6 lg:mb-0 lg:sticky ${sidebarTopOffset} self-start ${sidebarMaxHeight} overflow-y-auto rounded-lg`}
      >
        <LeagueSidebar
          leagues={leaguesForSidebar}
          selectedLeague={selectedLeagueCode}
          onSelectLeague={handleLeagueSelect}
        />
      </aside>

      <section className="w-full lg:flex-grow min-w-0">
        <h2 className="text-3xl sm:text-4xl font-bold mb-8 text-center text-brand-yellow">
          {pageTitle}
        </h2>
        {loading && allFetchedMatches.length === 0 ? ( // Показываем загрузку только если еще нет никаких данных
          <div className="flex justify-center items-center h-64">
            <p className="text-xl text-gray-400 animate-pulse">
              Загрузка матчей...
            </p>
          </div>
        ) : (
          <MatchList
            matches={displayedMatches}
            selectedLeague={selectedLeagueCode}
          />
        )}
      </section>

      <aside
        className={`w-full lg:w-72 xl:w-80 flex-shrink-0 hidden md:block lg:sticky ${sidebarTopOffset} self-start ${sidebarMaxHeight} overflow-y-auto rounded-lg`}
      >
        <div className="bg-brand-card-bg p-4 rounded-lg shadow-md min-h-[300px]">
          <h3 className="text-xl font-semibold mb-3 text-brand-yellow">
            Турнирная таблица
          </h3>
          <p className="text-brand-muted-text">
            {currentLeagueObject && selectedLeagueCode
              ? `Таблица для ${currentLeagueObject.name}`
              : "Выберите лигу"}
          </p>
        </div>
      </aside>
    </div>
  );
}

export default HomePage;
