// frontend/src/pages/HomePage.js
import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import MatchList from "../components/MatchList";
import { getMatches } from "../api";
import LeagueSidebar from "../components/Layout/LeagueSidebar";

// Убедитесь, что пути к локальным логотипам правильные, или используйте URL
// import plLogo from '../assets/leagues/pl.png';
// ... и так далее для других лиг

const leaguesForSidebar = [
  { code: null, name: "ВСЕ", shortName: "Все", logo: null },
  {
    code: "PL",
    name: "Premier League",
    shortName: "АПЛ",
    logo: "https://upload.wikimedia.org/wikipedia/en/thumb/f/f2/Premier_League_Logo.svg/1200px-Premier_League_Logo.svg.png" /* plLogo */,
  },
  {
    code: "BL1",
    name: "Bundesliga",
    shortName: "БУН",
    logo: "https://upload.wikimedia.org/wikipedia/en/thumb/d/df/Bundesliga_logo_%282017%29.svg/1200px-Bundesliga_logo_%282017%29.svg.png" /* blLogo */,
  },
  {
    code: "SA",
    name: "Serie A",
    shortName: "СА",
    logo: "https://upload.wikimedia.org/wikipedia/en/thumb/e/e9/Serie_A_logo_%282022%29.svg/1200px-Serie_A_logo_%282022%29.svg.png" /* saLogo */,
  },
  {
    code: "PD",
    name: "La Liga",
    shortName: "ЛАЛ",
    logo: "https://upload.wikimedia.org/wikipedia/commons/thumb/0/0f/LaLiga_logo_2023.svg/1200px-LaLiga_logo_2023.svg.png" /* pdLogo */,
  },
  {
    code: "FL1",
    name: "Ligue 1",
    shortName: "Л1",
    logo: "https://upload.wikimedia.org/wikipedia/commons/thumb/4/49/Ligue1_Uber_Eats_logo.png/1200px-Ligue1_Uber_Eats_logo.png" /* flLogo */,
  },
];

function HomePage() {
  const [allFetchedMatches, setAllFetchedMatches] = useState([]); // Храним все загруженные матчи
  const [displayedMatches, setDisplayedMatches] = useState([]); // Матчи для отображения (отфильтрованные)
  const [loading, setLoading] = useState(true);
  const [searchParams, setSearchParams] = useSearchParams();
  const selectedLeagueCode = searchParams.get("league");

  // Загрузка всех матчей один раз при монтировании
  useEffect(() => {
    const fetchAllMatches = async () => {
      setLoading(true);
      const data = await getMatches(null); // Загружаем ВСЕ лиги
      if (Array.isArray(data)) {
        setAllFetchedMatches(data);
      } else {
        console.error(
          "HomePage: Данные от getMatches не являются массивом!",
          data
        );
        setAllFetchedMatches([]);
      }
      setLoading(false);
    };
    fetchAllMatches();
  }, []); // Пустой массив зависимостей - загрузка один раз

  // Фильтрация и сортировка матчей при изменении allFetchedMatches или selectedLeagueCode
  useEffect(() => {
    if (allFetchedMatches.length === 0 && !loading) {
      // Если нет загруженных матчей (и не идет загрузка)
      setDisplayedMatches([]);
      return;
    }

    let filtered = allFetchedMatches;

    // Фильтр по лиге
    if (selectedLeagueCode) {
      filtered = filtered.filter(
        (match) => match.competition_code === selectedLeagueCode
      );
    }

    // Фильтр по предстоящим матчам на ближайшую неделю
    const now = new Date();
    const oneWeekFromNow = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000);

    const upcomingFiltered = filtered.filter((match) => {
      if (!match.utcDate) return false;
      const matchDate = new Date(match.utcDate);
      return (
        match.status !== "FINISHED" &&
        matchDate >= now &&
        matchDate <= oneWeekFromNow
      );
    });

    // Сортировка
    upcomingFiltered.sort((a, b) => new Date(a.utcDate) - new Date(b.utcDate));
    setDisplayedMatches(upcomingFiltered);
  }, [allFetchedMatches, selectedLeagueCode, loading]); // Добавил loading в зависимости

  const handleLeagueSelect = (leagueCode) => {
    if (leagueCode) {
      setSearchParams({ league: leagueCode });
    } else {
      setSearchParams({});
    }
  };

  const currentLeagueObject = selectedLeagueCode
    ? leaguesForSidebar.find((l) => l.code === selectedLeagueCode)
    : null;

  const pageTitle = currentLeagueObject
    ? currentLeagueObject.name
    : "Все предстоящие матчи (неделя)";

  return (
    <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
      <aside className="w-full lg:w-60 xl:w-64 flex-shrink-0 mb-6 lg:mb-0 lg:sticky lg:top-[calc(4rem+1.5rem)] self-start max-h-[calc(100vh-4rem-1.5rem-2rem)] overflow-y-auto rounded-lg">
        {/* 4rem - высота хедера (h-16), 1.5rem - py-3 в хедере, 2rem - отступ сверху main */}
        <LeagueSidebar
          leagues={leaguesForSidebar}
          selectedLeague={selectedLeagueCode}
          onSelectLeague={handleLeagueSelect}
        />
      </aside>

      <section className="w-full lg:flex-grow min-w-0">
        <h1 className="text-2xl sm:text-3xl font-bold mb-6 text-center text-brand-yellow">
          {pageTitle}
        </h1>
        {loading ? (
          <div className="flex justify-center items-center h-64">
            <p className="text-xl text-gray-400 animate-pulse">
              Загрузка матчей...
            </p>
          </div>
        ) : (
          <MatchList
            matches={displayedMatches}
            selectedLeague={selectedLeagueCode}
          />
        )}
      </section>

      <aside className="w-full lg:w-72 xl:w-80 flex-shrink-0 hidden md:block lg:sticky lg:top-[calc(4rem+1.5rem)] self-start max-h-[calc(100vh-4rem-1.5rem-2rem)] overflow-y-auto rounded-lg">
        <div className="bg-gray-800 p-4 rounded-lg shadow-md min-h-[300px]">
          <h2 className="text-xl font-semibold mb-3 text-brand-yellow">
            Турнирная таблица
          </h2>
          <p className="text-gray-400">
            {currentLeagueObject
              ? `Таблица для ${currentLeagueObject.name}`
              : "Выберите лигу"}
          </p>
        </div>
      </aside>
    </div>
  );
}

export default HomePage;
