// frontend/src/App.js
import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import HomePage from "./pages/HomePage";
import MatchDetailPage from "./pages/MatchDetailPage";
import Header from "./components/Layout/Header";
import TablesPage from "./pages/TablesPage"; // Импорт заглушки
import HistoryPage from "./pages/HistoryPage"; // Импорт заглушек
import "./App.css"; // Если у вас есть общий CSS файл App.css

function App() {
  return (
    <Router>
      <div className="flex flex-col min-h-screen bg-brand-bg text-brand-text-light">
        <Header />
        {/*
          Установим padding-top для main точно равным высоте хедера (h-16 = 4rem = 64px).
          Это должно убрать зазор между хедером и основным контентом.
        */}
        <main className="flex-grow container mx-auto px-4 py-8 pt-16">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/match/:matchId" element={<MatchDetailPage />} />
            <Route path="/tables" element={<TablesPage />} />
            <Route path="/history" element={<HistoryPage />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}
export default App;
