// frontend/src/components/MatchItem.js
import React from "react";
import { Link } from "react-router-dom";
// import { formatInTimeZone } from 'date-fns-tz'; // Если будете использовать date-fns-tz

// Функция для форматирования даты и времени (можно вынести в utils)
const formatMatchDateTime = (utcDateString, localMatchTimeString) => {
  // Приоритет отдаем localMatchTimeString, если оно есть и валидно
  let dateToFormat = localMatchTimeString || utcDateString;
  if (!dateToFormat) return "Время не указано";

  try {
    const date = new Date(dateToFormat); // new Date() хорошо работает с ISO строками, включая те, что с tz
    // Вы можете указать конкретный часовой пояс для отображения, если localMatchTime не содержит смещения
    // или если хотите всегда показывать в МСК, даже если localMatchTime - это время стадиона.
    // Для простоты, пока используем toLocaleString, который использует настройки браузера пользователя.
    // Для явного МСК нужно будет использовать date-fns-tz:
    // return formatInTimeZone(date, 'Europe/Moscow', 'dd.MM.yyyy HH:mm', { timeZone: 'Europe/Moscow' });
    return date.toLocaleString("ru-RU", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit",
      // timeZone: 'Europe/Moscow' // Можно добавить, чтобы принудительно показывать в МСК
    });
  } catch (e) {
    console.error(
      "Ошибка форматирования даты:",
      e,
      "Исходная строка:",
      dateToFormat
    );
    return typeof dateToFormat === "string"
      ? dateToFormat.substring(0, 16).replace("T", " ")
      : "Неверная дата";
  }
};

// Функция для определения цвета прогноза
const getPredictionColor = (prediction) => {
  if (prediction === "H") return "bg-green-600 hover:bg-green-700"; // Победа хозяев - зеленый
  if (prediction === "A") return "bg-red-600 hover:bg-red-700"; // Победа гостей - красный
  if (prediction === "D")
    return "bg-yellow-500 hover:bg-yellow-600 text-gray-900"; // Ничья - желтый
  return "bg-gray-600"; // По умолчанию
};

function MatchItem({ match }) {
  // TODO: Реализовать эффект подсветки под логотипом (потребует более сложного CSS/JS)
  // Например, можно попробовать использовать псевдоэлементы ::before или ::after
  // и устанавливать им background-color или box-shadow с цветом, извлеченным из лого (сложно)
  // или просто фиксированный цвет для каждой команды, если есть такая информация.

  return (
    <Link
      to={`/match/${match.id}`}
      className="block transform hover:scale-105 transition-transform duration-200"
    >
      <div className="bg-gray-800 rounded-lg shadow-xl p-5 flex flex-col h-full hover:shadow-brand-red/50">
        {" "}
        {/* h-full для одинаковой высоты карточек в гриде */}
        {/* Команды и логотипы */}
        <div className="flex justify-around items-center mb-3">
          <div className="flex flex-col items-center text-center w-2/5">
            {match.homeTeam_crest && (
              <img
                src={match.homeTeam_crest}
                alt={match.homeTeam_name}
                className="h-14 w-14 object-contain mb-1"
              />
            )}
            <span className="text-sm font-semibold truncate w-full">
              {match.homeTeam_name}
            </span>
          </div>
          <span className="text-2xl font-bold text-gray-500">VS</span>
          <div className="flex flex-col items-center text-center w-2/5">
            {match.awayTeam_crest && (
              <img
                src={match.awayTeam_crest}
                alt={match.awayTeam_name}
                className="h-14 w-14 object-contain mb-1"
              />
            )}
            <span className="text-sm font-semibold truncate w-full">
              {match.awayTeam_name}
            </span>
          </div>
        </div>
        {/* Прогноз */}
        {match.Predicted_Outcome && (
          <div className="my-3 text-center">
            <span
              className={`px-3 py-1 text-sm font-bold rounded-full text-white ${getPredictionColor(
                match.Predicted_Outcome
              )}`}
            >
              Прогноз: {match.Predicted_Outcome}
            </span>
          </div>
        )}
        {/* Если матч завершен, отображаем счет */}
        {match.status === "FINISHED" && (
          <p className="text-center text-lg font-bold my-2">
            {match.score_fullTime_home} - {match.score_fullTime_away}
          </p>
        )}
        {/* Дата и время (внизу карточки) */}v
        <div className="mt-auto text-center text-xs text-gray-400 pt-3">
          {" "}
          {/* mt-auto прижмет ко дну */}
          {formatMatchDateTime(match.utcDate, match.localMatchTime)}
          {match.localMatchTime ? ` (Местное время)` : ` (UTC)`}
        </div>
      </div>
    </Link>
  );
}

export default MatchItem;
