import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import MatchList from "../components/MatchList";
import { getMatches } from "../api";
import LeagueSidebar from "../components/Layout/LeagueSidebar";

import plLogo from "../assets/leagues/premier-league.png";
import blLogo from "../assets/leagues/bundesliga.png";
import saLogo from "../assets/leagues/serie-a.png";
import pdLogo from "../assets/leagues/la-liga.png";
import flLogo from "../assets/leagues/ligue-1.png";

const SidebarLeagues = [
  { code: null, name: "ВСЕ", shortName: "Все", logo: null },
  { code: "PL", name: "Premier League", shortName: "АПЛ", logo: plLogo },
  { code: "PD", name: "Primera Division", shortName: "Ла Лига", logo: pdLogo },
  { code: "SA", name: "Serie A", shortName: "Серия А", logo: saLogo },
  { code: "BL1", name: "Bundesliga", shortName: "Бундеслига", logo: blLogo },
  { code: "FL1", name: "Ligue 1", shortName: "Лига 1", logo: flLogo },
];

function HomePage() {
  const [allMatches, setAllMatches] = useState([]);
  const [displayMatches, setDisplayMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  const [searchParams, setSearchParams] = useSearchParams();
  const LeagueCode = searchParams.get("league");

  // Эффект для первоначальной загрузки ВСЕХ матчей (при монтировании компонента).
  useEffect(() => {
    const fetchAllMatches = async () => {
      setLoading(true); // Начало загрузки.
      console.log("HomePage: Запрос всех матчей...");
      const data = await getMatches(null); // Запрашиваем все матчи через API.
      if (Array.isArray(data)) {
        setAllMatches(data); // Обновляем состояние со всеми матчами.
        console.log("HomePage: Все матчи загружены:", data.length);
      } else {
        console.error(
          "HomePage: Данные от getMatches (всех) не являются массивом!",
          data
        );
        setAllMatches([]); // Устанавливаем пустой массив при ошибке.
      }
      setLoading(false); // Загрузка завершена.
    };
    fetchAllMatches(); // Вызываем функцию загрузки.
  }, []); // Пустой массив зависимостей: эффект выполняется только один раз.

  // Эффект для фильтрации и сортировки матчей при изменении данных или выбранной лиги.
  useEffect(() => {
    // Прекращаем выполнение, если данные еще грузятся или их нет.
    if (loading && allMatches.length === 0) {
      setDisplayMatches([]);
      return;
    }
    if (!loading && allMatches.length === 0) {
      setDisplayMatches([]);
      return;
    }

    let filteredMatchesSource = allMatches; // Исходник для фильтрации.

    // 1. Фильтруем по выбранной лиге (если выбрана).
    if (LeagueCode) {
      filteredMatchesSource = filteredMatchesSource.filter(
        (match) => match.competition_code === LeagueCode
      );
    }

    // 2. Фильтруем по предстоящим матчам на ближайшие 2 недели.
    // const now = new Date(); // Реальное время.
    const now = new Date("2025-05-14T00:00:00Z"); // Тестовая дата для отладки.
    const todayStart = new Date(
      now.getFullYear(),
      now.getMonth(),
      now.getDate()
    ); // Начало сегодняшнего дня.
    const twoWeekFromNowEnd = new Date(
      now.getTime() + 14 * 24 * 60 * 60 * 1000
    );
    twoWeekFromNowEnd.setHours(23, 59, 59, 999); // Конец дня через 2 недели.

    const upcomingFiltered = filteredMatchesSource.filter((match) => {
      if (!match.utcDate) return false;
      const matchDate = new Date(match.utcDate); // Дата матча.
      return (
        match.status !== "FINISHED" && // Матчи не завершены.
        matchDate >= todayStart && // Матчи сегодня или позже.
        matchDate <= twoWeekFromNowEnd // Матчи в пределах 2 недель.
      );
    });

    // 3. Сортируем отфильтрованные матчи по дате.
    upcomingFiltered.sort((a, b) => new Date(a.utcDate) - new Date(b.utcDate));

    setDisplayMatches(upcomingFiltered); // Обновляем отображаемые матчи.
  }, [allMatches, LeagueCode, loading]); // Зависимости эффекта.

  // Обработчик выбора лиги: обновляет URL-параметры.
  const LeagueSelect = (lCode) => {
    setSearchParams(lCode ? { league: lCode } : {});
  };

  // Определяем объект текущей лиги для заголовка.
  const currentLeagueObjectForTitle = LeagueCode
    ? SidebarLeagues.find((l) => l.code === LeagueCode)
    : { name: "Все матчи" };

  const pageTitle = currentLeagueObjectForTitle.name; // Заголовок страницы.

  // Классы для позиционирования сайдбаров (sticky, высота).
  const sidebarStickyTop = "top-16"; // Отступ сверху (высота хедера).
  const sidebarMaxHeight = "max-h-[calc(100vh_-_4rem_-_theme(spacing.8))]"; // Макс. высота для скролла.

  // JSX-разметка компонента.
  return (
    <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
      {/* Левый сайдбар с выбором лиг. */}
      <aside
        className={`
          w-full lg:w-60 xl:w-64 flex-shrink-0
          mb-6 lg:mb-0
          lg:sticky ${sidebarStickyTop} self-start
          ${sidebarMaxHeight} overflow-y-auto
          rounded-lg
        `}
      >
        <LeagueSidebar
          leagues={SidebarLeagues}
          selectedLeague={LeagueCode}
          onSelectLeague={LeagueSelect}
        />
      </aside>

      {/* Центральная колонка с заголовком и списком матчей. */}
      <section className="w-full lg:flex-grow min-w-0">
        <h1 className="text-2xl sm:text-3xl font-bold mb-6 text-center text-brand-primary-yellow uppercase tracking-wider">
          {pageTitle}
        </h1>
        {loading && displayMatches.length === 0 ? (
          // Индикатор загрузки, если матчей нет и идет загрузка.
          <div className="flex justify-center items-center h-64">
            <p className="text-xl text-brand-text-muted animate-pulse">
              Загрузка матчей...
            </p>
          </div>
        ) : (
          // Отображаем список матчей.
          <MatchList
            matches={displayMatches}
            selectedLeague={LeagueCode}
            orderedLeagues={SidebarLeagues}
          />
        )}
      </section>

      {/* Правый сайдбар (заглушка для турнирной таблицы). */}
      <aside
        className={`w-full lg:w-72 xl:w-80 flex-shrink-0 hidden md:block lg:sticky ${sidebarStickyTop} self-start ${sidebarMaxHeight} overflow-y-auto rounded-lg`}
      >
        <div className="bg-brand-surface p-4 rounded-lg shadow-md min-h-[300px] flex flex-col">
          <h3 className="text-xl font-semibold mb-3 text-brand-primary-yellow">
            Турнирная таблица
          </h3>
          <p className="text-brand-text-muted flex-grow flex items-center justify-center text-center">
            Функция в разработке.
          </p>
        </div>
      </aside>
    </div>
  );
}

export default HomePage; // Экспорт компонента.
