// frontend/src/components/Layout/Header.js
import React, { useState } from "react";
import { Link, NavLink } from "react-router-dom";
// import Logo from '../../assets/NeuroPredictLogo.png';

const leagues = [
  { code: "PL", name: "Premier League" },
  { code: "BL1", name: "Bundesliga" },
  { code: "SA", name: "Serie A" },
  { code: "PD", name: "La Liga" },
  { code: "FL1", name: "Ligue 1" },
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);

  return (
    // ДОБАВЛЯЕМ: sticky top-0 z-50 для закрепления хедера
    <header className="bg-gray-900 shadow-lg sticky top-0 z-50 w-full">
      {/* ИЗМЕНЕНО: убран container mx-auto, чтобы хедер был на всю ширину, 
                   а контент внутри хедера центрируем через justify-center для nav 
                   или добавляем container для лого и nav отдельно */}
      <div className="px-4 py-3 flex justify-between items-center max-w-7xl mx-auto">
        {" "}
        {/* Ограничиваем ширину контента внутри хедера */}
        {/* Логотип и название сайта */}
        <Link
          to="/"
          className="text-2xl font-bold text-brand-yellow hover:text-brand-red transition-colors"
        >
          {/* <img src={Logo} alt="NeuroPredict Logo" className="h-8 w-auto inline-block mr-2" /> */}
          NeuroPredict
        </Link>
        {/* Навигационное меню - ДОБАВЛЯЕМ: mx-auto для центрирования блока навигации */}
        <nav className="flex items-center space-x-4 md:space-x-6 mx-auto">
          {" "}
          {/* mx-auto для центрирования, если логотип слева, а тут ничего нет справа */}
          {/* Если нужно, чтобы логотип был слева, а меню строго по центру всей ширины хедера, 
              тогда можно использовать flex justify-center для родительского div и обернуть меню 
              или использовать абсолютное позиционирование для логотипа и правых элементов (если будут).
              Для простоты, если справа от меню ничего нет, mx-auto сдвинет его к центру относительно логотипа.
              Если логотип должен быть слева, а меню по центру ВСЕГО хедера, то flex-grow для пустого div справа от меню.

              Более простой вариант для центрирования ссылок, если логотип слева:
              Убрать mx-auto с nav, а сам nav сделать flex-grow и его контент центрировать:
          */}
          {/* <nav className="flex-grow flex justify-center items-center space-x-4 md:space-x-6"> */}
          <NavLink
            to="/"
            className={({ isActive }) =>
              `px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                isActive
                  ? "bg-brand-red text-white"
                  : "text-gray-300 hover:bg-gray-700 hover:text-brand-yellow"
              }`
            }
          >
            Главная
          </NavLink>
          {/* Выпадающий список лиг */}
          <div className="relative">
            <button
              onClick={() => setIsLeaguesDropdownOpen(!isLeaguesDropdownOpen)}
              onBlur={() =>
                setTimeout(() => setIsLeaguesDropdownOpen(false), 150)
              } // Закрывать при потере фокуса с небольшой задержкой
              className="px-3 py-2 rounded-md text-sm font-medium text-gray-300 hover:bg-gray-700 hover:text-brand-yellow focus:outline-none flex items-center transition-colors"
            >
              Лиги
              <svg
                className={`w-4 h-4 ml-1 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M19 9l-7 7-7-7"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              <div className="absolute left-1/2 -translate-x-1/2 md:left-auto md:-translate-x-0 md:right-0 mt-2 w-48 bg-gray-800 rounded-md shadow-xl z-20 py-1">
                {" "}
                {/* Центрируем дропдаун */}
                {leagues.map((league) => (
                  <NavLink
                    key={league.code}
                    to={`/?league=${league.code}`}
                    onClick={() => setIsLeaguesDropdownOpen(false)}
                    className="block px-4 py-2 text-sm text-gray-300 hover:bg-gray-700 hover:text-brand-yellow w-full text-left"
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>
          <NavLink
            to="/history" // Заглушка
            className={({ isActive }) =>
              `px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                isActive
                  ? "bg-brand-red text-white"
                  : "text-gray-300 hover:bg-gray-700 hover:text-brand-yellow"
              }`
            }
          >
            История матчей
          </NavLink>
          <NavLink
            to="/tables" // Заглушка
            className={({ isActive }) =>
              `px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                isActive
                  ? "bg-brand-red text-white"
                  : "text-gray-300 hover:bg-gray-700 hover:text-brand-yellow"
              }`
            }
          >
            Турнирные таблицы
          </NavLink>
        </nav>
        {/* Пустой div для выравнивания, если нужно (если логотип слева, меню по центру, а справа ничего) */}
        {/* <div className="w-[длина_логотипа_блока_px]"></div>  Или использовать flex-grow для nav */}
        <div className="w-auto">
          {" "}
          {/* Заглушка для правого края, чтобы justify-between работал, если меню строго по центру */}
          {/* Можно добавить сюда, например, иконку пользователя или настройки */}
        </div>
      </div>
    </header>
  );
}

export default Header;
