import React from "react";

function LeagueSidebar({ leagues, selectedLeague, onSelectLeague }) {
  return (
    <div className="bg-color-surface p-2 sm:p-3 rounded-lg shadow-md">
      <ul className="space-y-1.5 sm:space-y-2">
        {leagues.map((league) => {
          const isActive =
            selectedLeague === league.code ||
            (!selectedLeague && league.code === null);

          return (
            <li key={league.code || "all-leagues"}>
              <button
                onClick={() => onSelectLeague(league.code)}
                className={`
                  w-full flex flex-col items-center justify-center p-2 rounded-md
                  transition-colors duration-150 ease-in-out group
                  focus:outline-none
                `}
                title={league.name}
                // Атрибут `title` добавляет всплывающую подсказку при наведении мышкой.
              >
                {/* Квадрат для логотипа или текста "ALL" */}
                <div
                  className={`
                    w-14 h-14 sm:w-16 sm:h-16 mb-1 rounded-md
                    flex items-center justify-center overflow-hidden
                    transition-all duration-150 ease-in-out
                    border-2
                    ${
                      isActive
                        ? "bg-color-primary-red border-color-primary-red shadow-red-button"
                        : // Если лига активна: красный фон, красная рамка, красное свечение.
                          "bg-gray-700 border-gray-700 group-hover:border-color-primary-red"
                      // Если неактивна: серый фон, серая рамка. При наведении на родительскую кнопку (`group-hover`),
                      // рамка становится красной.
                    }
                  `}
                >
                  {league.logo ? (
                    // Если у лиги есть `logo` (URL изображения), то отображаем его.
                    <img
                      src={league.logo} // Путь к изображению логотипа.
                      alt={`${league.name} logo`} // Альтернативный текст для доступности.
                      className="w-full h-full object-contain p-1.5"
                      // Размеры, масштабирование (чтобы логотип полностью помещался), внутренний отступ.
                    />
                  ) : (
                    // Иначе (если `logo` нет, значит это кнопка "ВСЕ" или лига без лого), отображаем текст.
                    // Блок для кнопки "ВСЕ"
                    <span
                      className={`text-lg sm:text-xl font-bold
                      ${
                        // Текст "ALL": желтый на красном фоне (активный), светло-серый на сером, желтый при наведении
                        isActive
                          ? "text-color-primary-yellow" // Если активна, текст желтый (на красном фоне квадрата).
                          : "text-color-primary-yellow" // Если неактивна, текст также желтый (на сером фоне квадрата).
                        // Здесь можно было бы сделать разные цвета для активного/неактивного состояния текста, если нужно.
                      }`}
                    >
                      {/* Отображаем "ALL" для кнопки "ВСЕ" */}
                      {league.code === null
                        ? "ALL" // Если код лиги `null`, показываем "ALL".
                        : league.shortName || league.code}
                      {/* Иначе, показываем короткое имя лиги, или ее код, если короткое имя не указано. */}
                    </span>
                  )}
                </div>
                {/* Подпись под квадратиком */}
                <span
                  className={`text-xs text-center truncate w-full mt-1 px-1 transition-colors
                    ${
                      isActive
                        ? "text-color-primary-red font-semibold"
                        : // Если активна: красный текст, жирный шрифт.
                          "text-color-primary-yellow group-hover:text-color-yellow-bright"
                      // Если неактивна: желтый текст. При наведении на родительскую кнопку (`group-hover`),
                      // текст становится ярко-желтым.
                    }`}
                >
                  {league.name}
                  {/* Полное название лиги. `truncate w-full` обрезает длинный текст. */}
                </span>
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default LeagueSidebar;
// Экспортируем компонент `LeagueSidebar` по умолчанию, чтобы его можно было импортировать
// и использовать в `HomePage.js`.
