// frontend/src/components/MatchItem.js
import React from "react";
import { Link } from "react-router-dom";
// import { formatInTimeZone } from 'date-fns-tz'; // Если будете использовать

const formatMatchDateTime = (utcDateString, localMatchTimeString) => {
  let dateToFormat = localMatchTimeString || utcDateString; // Приоритет локальному времени, если есть
  if (!dateToFormat) return "Время не указано";
  try {
    const date = new Date(dateToFormat);
    // Для принудительного МСК на фронте (рекомендуется, если localMatchTime не всегда МСК):
    // if (utcDateString) { // Если есть UTC, конвертируем из него
    //   return formatInTimeZone(new Date(utcDateString), 'Europe/Moscow', 'dd.MM.yyyy, HH:mm', { timeZone: 'Europe/Moscow' }) + ' МСК';
    // }
    return (
      date.toLocaleString("ru-RU", {
        day: "2-digit",
        month: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      }) +
      (localMatchTimeString
        ? ` (${
            date
              .toLocaleTimeString("ru-RU", { timeZoneName: "shortGeneric" })
              .split(" ")[1] || "Местн."
          })`
        : " (UTC)")
    );
  } catch (e) {
    return typeof dateToFormat === "string"
      ? dateToFormat.substring(0, 16).replace("T", " ")
      : "Неверная дата";
  }
};

const getPredictionColorClasses = (prediction) => {
  if (prediction === "H") return "bg-green-500 text-white";
  if (prediction === "A") return "bg-brand-red text-white"; // Используем brand-red
  if (prediction === "D") return "bg-brand-yellow text-brand-dark-bg"; // Используем brand-yellow
  return "bg-gray-500 text-white";
};

function MatchItem({ match }) {
  // Placeholder для иконок, если будете добавлять
  // const CalendarIcon = () => <svg className="w-4 h-4 mr-1.5 text-brand-muted-text inline-block" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M6 2a1 1 0 00-1 1v1H4a2 2 0 00-2 2v10a2 2 0 002 2h12a2 2 0 002-2V6a2 2 0 00-2-2h-1V3a1 1 0 10-2 0v1H7V3a1 1 0 00-1-1zm0 5a1 1 0 000 2h8a1 1 0 100-2H6z" clipRule="evenodd"></path></svg>;
  // const LeagueIcon = () => <svg className="w-4 h-4 mr-1.5 text-brand-muted-text inline-block" fill="currentColor" viewBox="0 0 20 20"><path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z"></path></svg>;
  // const StadiumIcon = () => <svg className="w-4 h-4 mr-1.5 text-brand-muted-text inline-block" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM4.332 8.027a6.002 6.002 0 0111.336 0l2.038 4.99a1 1 0 01-.626 1.53H2.92a1 1 0 01-.626-1.53l2.038-4.99z" clipRule="evenodd"></path></svg>;

  return (
    <Link
      to={`/match/${match.id}`}
      className="block bg-brand-card-bg rounded-xl shadow-lg hover:shadow-brand-red/40 transition-all duration-200 ease-in-out transform hover:-translate-y-1"
    >
      <div className="p-5 flex flex-col h-full">
        {/* Названия команд - как на примере */}
        <h3 className="text-lg font-bold text-brand-yellow text-center mb-3 truncate">
          {match.homeTeam_name} vs {match.awayTeam_name}
        </h3>

        {/* Информация о матче (дата, лига, стадион) - как на примере */}
        <div className="text-xs text-brand-muted-text space-y-1 mb-4">
          <p className="flex items-center">
            {/* <CalendarIcon /> */}
            <span className="material-icons-outlined mr-1.5 text-sm">
              calendar_today
            </span>{" "}
            {/* Material Icon */}
            {formatMatchDateTime(match.utcDate, match.localMatchTime)}
          </p>
          <p className="flex items-center">
            {/* <LeagueIcon /> */}
            <span className="material-icons-outlined mr-1.5 text-sm">
              emoji_events
            </span>{" "}
            {/* Material Icon */}
            {match.competition_name} ({match.competition_code})
          </p>
          {/* Стадион пока уберем, его нет в ваших данных. Если появится - добавить. */}
          {/* <p className="flex items-center"><StadiumIcon /> Камп Ноу</p> */}
        </div>

        {/* Блок с рекомендуемой ставкой - как на примере */}
        {/* Если есть прогноз и счет */}
        {(match.Predicted_Outcome || match.score_fullTime_home !== null) && (
          <div className="bg-gray-700/50 p-4 rounded-lg mt-auto mb-3 relative">
            {" "}
            {/* mt-auto чтобы прижать к блоку даты, если прогноз короткий */}
            <div className="absolute top-2 left-0 w-1 h-6 bg-brand-yellow rounded-r-sm"></div>{" "}
            {/* Желтая полоска слева */}
            <p className="text-sm font-semibold text-brand-yellow mb-1">
              Рекомендуемая ставка:
            </p>
            {/* Отображаем или прогноз, или фактический счет, если матч завершен */}
            {match.status === "FINISHED" ? (
              <p className="text-brand-light-text text-sm">
                Результат:
                <span className="ml-2 px-2 py-0.5 bg-brand-red text-white text-xs font-bold rounded">
                  {match.score_fullTime_home} : {match.score_fullTime_away}
                </span>
              </p>
            ) : match.Predicted_Outcome ? (
              <p className="text-brand-light-text text-sm">
                Прогноз:
                <span
                  className={`ml-2 px-2 py-0.5 text-xs font-bold rounded ${getPredictionColorClasses(
                    match.Predicted_Outcome
                  )}`}
                >
                  {match.Predicted_Outcome === "H"
                    ? "П1"
                    : match.Predicted_Outcome === "A"
                    ? "П2"
                    : "Ничья"}
                </span>
              </p>
            ) : null}
            {/* Коэффициент (если будет, пока заглушка) */}
            {/* <p className="text-brand-light-text text-sm mt-1">
                    Коэффициент: 
                    <span className="ml-2 px-2 py-0.5 bg-brand-red text-white text-xs font-bold rounded">3.50</span>
                </p> */}
            {/* Анализ (если будет, пока заглушка) */}
            {/* <p className="text-xs text-brand-muted-text mt-2">
                    Анализ: Обе команды в хорошей форме, но Барселона сильнее дома.
                </p> */}
          </div>
        )}

        {/* Дата и время внизу, если не поместились в блок "Рекомендуемая ставка" */}
        {/* Этот блок теперь не нужен, т.к. дата в блоке инфо выше */}
        {/* <div className="text-center text-xs text-brand-muted-text mt-3 pt-3 border-t border-gray-700">
            {formatMatchDateTime(match.utcDate, match.localMatchTime)}
        </div> */}
      </div>
    </Link>
  );
}
export default MatchItem;
