import React from "react";
import MatchItem from "./MatchItem";

function MatchList({ matches, selectedLeague, orderedLeagues }) {
  if (matches.length === 0) {
    return (
      <p className="text-center text-color-text-second py-10">
        Матчи на ближайшую неделю не найдены.
      </p>
    );
  }

  let filterMatches = {};

  if (!selectedLeague) {
    matches.forEach((match) => {
      const leagueCode = match.competition_code;
      const LL = orderedLeagues.find((l) => l.code === leagueCode);
      const leagueName = LL ? LL.name : match.competition_name;

      if (!filterMatches[leagueCode]) {
        filterMatches[leagueCode] = {
          name: leagueName,
          code: leagueCode,
          matches: [],
        };
      }
      filterMatches[leagueCode].matches.push(match);
    });
  }

  let filterLeagues = [];

  if (!selectedLeague) {
    const leagueCodes = new Set();

    orderedLeagues.forEach((league) => {
      if (league.code && filterMatches[league.code]) {
        filterLeagues.push(filterMatches[league.code]);
        leagueCodes.add(league.code);
      }
    });
  }

  // 4. JSX (что будет отрисовано компонентом)

  return (
    <div>
      {!selectedLeague ? (
        // Условный рендеринг: если `selectedLeague` равен `null` (выбрано "ВСЕ"),
        // тогда отображаем матчи, сгруппированные по лигам.
        filterLeagues.map(
          (
            leagueGroup // Итерируем по отсортированному списку групп лиг (`filterLeagues`).
          ) => (
            <div key={leagueGroup.code || leagueGroup.name} className="mb-10">
              {" "}
              {/* Контейнер для каждой группы лиг. `key` важен для React. `mb-10` - нижний отступ. */}
              {/* Заголовок лиги с желтым текстом и красной подсветкой */}
              <h2 className="text-2xl sm:text-3xl font-bold mb-6 text-center text-color-primary-yellow uppercase tracking-wider relative">
                {leagueGroup.name}
                {/* Отображаем название лиги (например, "Premier League"). */}
                {/* Красная линия-подсветка по краям */}
                <div className="absolute left-1/2 -translate-x-1/2 bottom-0 w-3/4 h-0.5 bg-gradient-to-r from-transparent via-color-primary-red to-transparent opacity-75"></div>
                {/* Это декоративная линия под заголовком, созданная с помощью Tailwind CSS. */}
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
                {/* Сетка для отображения матчей: 1 колонка на мобильных, 2 на средних и больших экранах. */}
                {leagueGroup.matches.map((match) => (
                  // Итерируем по матчам внутри текущей группы лиг.
                  <MatchItem key={match.id} match={match} />
                  // Для каждого матча рендерим компонент `MatchItem`, передавая ему данные `match` и уникальный `key`.
                ))}
              </div>
            </div>
          )
        )
      ) : (
        // Иначе (если выбрана конкретная лига), отображаем матчи без группировки и заголовков лиг.
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
          {/* Аналогичная сетка для отображения матчей. */}
          {matches.map((match) => (
            // Итерируем по всем матчам, которые были переданы в `MatchList` (они уже отфильтрованы по одной лиге).
            <MatchItem key={match.id} match={match} />
            // Рендерим `MatchItem` для каждого матча.
          ))}
        </div>
      )}
    </div>
  );
}

export default MatchList;
