// frontend/src/components/Layout/LeagueSidebar.js
import React from "react";

function LeagueSidebar({ leagues, selectedLeague, onSelectLeague }) {
  return (
    <div className="bg-gray-800 p-3 rounded-lg shadow-md">
      {" "}
      {/* Уменьшил общие отступы p-3 */}
      <h2 className="text-lg sm:text-xl font-semibold mb-3 text-brand-yellow px-1">
        Лиги
      </h2>
      <ul className="space-y-2">
        {leagues.map((league) => (
          <li key={league.code || "all-leagues"}>
            {" "}
            {/* Ключ должен быть уникальным */}
            <button
              onClick={() => onSelectLeague(league.code)}
              className={`w-full flex flex-col items-center justify-center p-2 rounded-md transition-all duration-150 ease-in-out focus:outline-none focus:ring-2 focus:ring-brand-yellow
                ${
                  selectedLeague === league.code ||
                  (!selectedLeague && league.code === null)
                    ? "bg-brand-red text-white shadow-lg transform scale-105" // Активная кнопка
                    : "text-gray-300 hover:bg-gray-700 hover:text-brand-yellow" // Неактивная кнопка
                }`}
              title={league.name}
            >
              <div
                className="w-12 h-12 sm:w-14 sm:h-14 mb-1 bg-gray-700 rounded flex items-center justify-center overflow-hidden
                           border-2 border-transparent group-hover:border-brand-yellow" // Добавил group-hover для рамки
                // Можно добавить рамку для активного элемента:
                // ${ (selectedLeague === league.code) || (!selectedLeague && league.code === null) ? 'border-brand-yellow' : 'border-gray-700' }
              >
                {league.logo ? (
                  <img
                    src={league.logo}
                    alt={league.name}
                    className="w-full h-full object-contain p-1"
                  />
                ) : (
                  <span className="text-base sm:text-lg font-bold text-gray-400">
                    {league.code === null
                      ? "ВСЕ"
                      : league.shortName || league.code}
                  </span>
                )}
              </div>
              <span className="text-xs sm:text-sm text-center truncate w-full mt-1 px-1">
                {league.name}
              </span>
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default LeagueSidebar;
