import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api";

const parseData = (data) => {
  if (typeof data === "string") {
    try {
      return JSON.parse(data);
    } catch (e) {
      console.error("Ошибка парсинга JSON строки от API:", e);
      return null;
    }
  }
  return data;
};

export const getMatches = async (leagueCode = null) => {
  try {
    let url = `${API_BASE_URL}/matches/`;
    if (leagueCode) {
      url += `?league=${leagueCode}`;
    }
    const response = await axios.get(url);
    const parsedData = parseData(response.data);
    return Array.isArray(parsedData) ? parsedData : [];
  } catch (error) {
    console.error("Ошибка при загрузке списка матчей:", error);
    return [];
  }
};

export const getMatchId = async (matchId) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/matches/${matchId}`); // Запрос деталей матча.
    console.log("Полный ответ от axios (getMatchId):", response); // Логируем полный ответ.
    const parsedData = parseData(response.data); // Парсим данные.
    console.log("Распарсенные данные (getMatchId):", parsedData); // Логируем распарсенные данные.
    return parsedData; // Возвращаем данные матча (объект или null).
  } catch (error) {
    console.error(`Ошибка при загрузке матча с ID ${matchId}:`, error); // Логируем ошибку.
    return null; // Возвращаем null при ошибке.
  }
};
