import React from "react";
import MatchItem from "./MatchItem";

function MatchList({ matches, selectedLeague, orderedLeagues }) {
  if (matches.length === 0) {
    return (
      <p className="text-center text-brand-text-muted py-10">
        Матчи на ближайшую неделю не найдены.
      </p>
    );
  }

  let filterMatches = {};

  if (!selectedLeague) {
    matches.forEach((match) => {
      const leagueCode = match.competition_code;
      const LL = orderedLeagues.find((l) => l.code === leagueCode);
      const leagueName = LL ? LL.name : match.competition_name;

      if (!filterMatches[leagueCode]) {
        // Если для этой лиги еще нет группы в `filterMatches`, создаем ее.
        filterMatches[leagueCode] = {
          name: leagueName, // Имя лиги для заголовка.
          code: leagueCode, // Код лиги.
          matches: [], // Пустой массив для матчей этой лиги.
        };
      }
      filterMatches[leagueCode].matches.push(match);
      // Добавляем текущий матч в соответствующую группу лиги.
    });
  }

  // 3. Логика определения порядка отображения групп лиг

  let leaguesToDisplay = [];
  // Инициализируем пустой массив, который будет содержать группы лиг в правильном порядке.

  if (!selectedLeague) {
    // Этот блок также выполняется ТОЛЬКО, если выбрано "ВСЕ лиги".
    const addedLeagueCodes = new Set(); // Используем Set для быстрого отслеживания уже добавленных лиг, чтобы избежать дубликатов.

    // 1. Добавляем лиги в заданном порядке из orderedLeagues (полученного из sidebar'а)
    orderedLeagues.forEach((sidebarLeague) => {
      // Перебираем лиги в том порядке, в котором они заданы в сайдбаре (`leaguesForSidebar` из `HomePage.js`).
      if (sidebarLeague.code && filterMatches[sidebarLeague.code]) {
        // Если это не "ВСЕ" (sidebarLeague.code не null) И для этой лиги есть матчи, которые мы сгруппировали,
        leaguesToDisplay.push(filterMatches[sidebarLeague.code]);
        // Добавляем эту группу лиг в наш список для отображения.
        addedLeagueCodes.add(sidebarLeague.code); // Отмечаем, что этот код лиги уже добавлен.
      }
    });

    // 2. Добавляем "Другие лиги" (те, которых нет в orderedLeagues), если есть
    // Итерируем по всем группам, которые были сформированы, но еще не добавлены в `leaguesToDisplay`.
    Object.keys(filterMatches).forEach((code) => {
      // Проходим по всем кодам лиг, для которых у нас есть сгруппированные матчи.
      if (!addedLeagueCodes.has(code)) {
        // Если эта лига еще не была добавлена в `leaguesToDisplay` (т.е. ее не было в `orderedLeagues` или она была там, но после "ВСЕ"),
        leaguesToDisplay.push(filterMatches[code]);
        // Добавляем ее в конец списка.
      }
    });
  } else {
    // Если выбрана КОНКРЕТНАЯ лига, то группировка не нужна.
    // В этом случае `MatchList` просто отобразит уже отфильтрованные матчи, без заголовков групп.
    // Этот `else` блок на самом деле ничего не делает с `leaguesToDisplay` или `filterMatches`,
    // так как рендеринг будет происходить на основе исходного `matches` пропса,
    // который уже отфильтрован в `HomePage`.
  }

  // 4. JSX (что будет отрисовано компонентом)

  return (
    <div>
      {!selectedLeague ? (
        // Условный рендеринг: если `selectedLeague` равен `null` (выбрано "ВСЕ"),
        // тогда отображаем матчи, сгруппированные по лигам.
        leaguesToDisplay.map(
          (
            leagueGroup // Итерируем по отсортированному списку групп лиг (`leaguesToDisplay`).
          ) => (
            <div key={leagueGroup.code || leagueGroup.name} className="mb-10">
              {" "}
              {/* Контейнер для каждой группы лиг. `key` важен для React. `mb-10` - нижний отступ. */}
              {/* Заголовок лиги с желтым текстом и красной подсветкой */}
              <h2 className="text-2xl sm:text-3xl font-bold mb-6 text-center text-brand-primary-yellow uppercase tracking-wider relative">
                {leagueGroup.name}
                {/* Отображаем название лиги (например, "Premier League"). */}
                {/* Красная линия-подсветка по краям */}
                <div className="absolute left-1/2 -translate-x-1/2 bottom-0 w-3/4 h-0.5 bg-gradient-to-r from-transparent via-brand-primary-red to-transparent opacity-75"></div>
                {/* Это декоративная линия под заголовком, созданная с помощью Tailwind CSS. */}
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
                {/* Сетка для отображения матчей: 1 колонка на мобильных, 2 на средних и больших экранах. */}
                {leagueGroup.matches.map((match) => (
                  // Итерируем по матчам внутри текущей группы лиг.
                  <MatchItem key={match.id} match={match} />
                  // Для каждого матча рендерим компонент `MatchItem`, передавая ему данные `match` и уникальный `key`.
                ))}
              </div>
            </div>
          )
        )
      ) : (
        // Иначе (если выбрана конкретная лига), отображаем матчи без группировки и заголовков лиг.
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
          {/* Аналогичная сетка для отображения матчей. */}
          {matches.map((match) => (
            // Итерируем по всем матчам, которые были переданы в `MatchList` (они уже отфильтрованы по одной лиге).
            <MatchItem key={match.id} match={match} />
            // Рендерим `MatchItem` для каждого матча.
          ))}
        </div>
      )}
    </div>
  );
}

export default MatchList;
// Экспортируем компонент `MatchList`, чтобы его можно было использовать в других файлах (например, в `HomePage.js`).
