// frontend/src/pages/HomePage.js
import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import MatchList from "../components/MatchList";
import { getMatches } from "../api";
import LeagueSidebar from "../components/Layout/LeagueSidebar";
// import StandingsWidget from '../components/Widgets/StandingsWidget'; // <-- УДАЛЯЕМ ЭТОТ ИМПОРТ

// --- Импорты логотипов лиг (оставляем, т.к. они для сайдбара) ---
import plLogo from "../assets/leagues/premier-league.png";
import blLogo from "../assets/leagues/bundesliga.png";
import saLogo from "../assets/leagues/serie-a.png";
import pdLogo from "../assets/leagues/la-liga.png";
import flLogo from "../assets/leagues/ligue-1.png";

// --- Конфигурация данных для сайдбара (оставляем) ---
const leaguesForSidebar = [
  { code: null, name: "ВСЕ", shortName: "Все", logo: null },
  { code: "PL", name: "Premier League", shortName: "АПЛ", logo: plLogo },
  { code: "BL1", name: "Bundesliga", shortName: "Бундеслига", logo: blLogo },
  { code: "SA", name: "Serie A", shortName: "Серия А", logo: saLogo },
  { code: "PD", name: "La Liga", shortName: "Ла Лига", logo: pdLogo },
  { code: "FL1", name: "Ligue 1", shortName: "Лига 1", logo: flLogo },
];

// --- Вспомогательная функция для определения текущего сезона (больше не нужна для виджета, но может пригодиться для других целей) ---
const getCurrentSeasonYear = () => {
  const now = new Date();
  let year = now.getFullYear();
  if (now.getMonth() < 7) {
    year -= 1;
  }
  return year;
};

// --- Вспомогательная функция для сопоставления кодов лиг с ID для API-Sports (больше не нужна) ---
// const leagueCodeToApiSportsId = (code) => { /* ... */ };

function HomePage() {
  const [allFetchedMatches, setAllFetchedMatches] = useState([]);
  const [displayedMatches, setDisplayedMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchParams, setSearchParams] = useSearchParams();
  const selectedLeagueCode = searchParams.get("league");

  useEffect(() => {
    const fetchAllMatches = async () => {
      setLoading(true);
      console.log("HomePage: Запрос всех матчей...");
      const data = await getMatches(null);
      if (Array.isArray(data)) {
        setAllFetchedMatches(data);
        console.log("HomePage: Все матчи загружены:", data.length);
      } else {
        console.error(
          "HomePage: Данные от getMatches (всех) не являются массивом!",
          data
        );
        setAllFetchedMatches([]);
      }
      setLoading(false);
    };
    fetchAllMatches();
  }, []);

  useEffect(() => {
    if (loading && allFetchedMatches.length === 0) {
      setDisplayedMatches([]);
      return;
    }
    if (!loading && allFetchedMatches.length === 0) {
      setDisplayedMatches([]);
      return;
    }

    let filteredMatchesSource = allFetchedMatches;

    if (selectedLeagueCode) {
      filteredMatchesSource = filteredMatchesSource.filter(
        (match) => match.competition_code === selectedLeagueCode
      );
    }

    const now = new Date();
    const todayStart = new Date(
      now.getFullYear(),
      now.getMonth(),
      now.getDate()
    );
    const oneWeekFromNowEnd = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000);
    oneWeekFromNowEnd.setHours(23, 59, 59, 999);

    const upcomingFiltered = filteredMatchesSource.filter((match) => {
      if (!match.utcDate) return false;
      const matchDate = new Date(match.utcDate);
      return (
        match.status !== "FINISHED" &&
        matchDate >= todayStart &&
        matchDate <= oneWeekFromNowEnd
      );
    });

    upcomingFiltered.sort((a, b) => new Date(a.utcDate) - new Date(b.utcDate));
    setDisplayedMatches(upcomingFiltered);
  }, [allFetchedMatches, selectedLeagueCode, loading]);

  const handleLeagueSelect = (leagueCode) => {
    setSearchParams(leagueCode ? { league: leagueCode } : {});
  };

  const currentLeagueObjectForTitle = selectedLeagueCode
    ? leaguesForSidebar.find((l) => l.code === selectedLeagueCode)
    : { name: "Все предстоящие матчи (неделя)" };

  const pageTitle = currentLeagueObjectForTitle.name;

  // Удаляем apiSportsLeagueId и seasonForWidget
  // const apiSportsLeagueId = selectedLeagueCode ? leagueCodeToApiSportsId(selectedLeagueCode) : null;
  // const seasonForWidget = getCurrentSeasonYear();

  const sidebarStickyTop = "top-16";
  const sidebarMaxHeight = "max-h-[calc(100vh_-_4rem_-_theme(spacing.8))]";

  return (
    <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
      {/* Левый сайдбар */}
      <aside
        className={`w-full lg:w-60 xl:w-64 flex-shrink-0 mb-6 lg:mb-0 lg:sticky ${sidebarStickyTop} self-start ${sidebarMaxHeight} overflow-y-auto rounded-lg`}
      >
        <LeagueSidebar
          leagues={leaguesForSidebar}
          selectedLeague={selectedLeagueCode}
          onSelectLeague={handleLeagueSelect}
        />
      </aside>

      {/* Центральная колонка с матчами */}
      <section className="w-full lg:flex-grow min-w-0">
        <h1 className="text-2xl sm:text-3xl font-bold mb-6 text-center text-brand-primary-yellow uppercase tracking-wider">
          {pageTitle}
        </h1>
        {loading && displayedMatches.length === 0 ? (
          <div className="flex justify-center items-center h-64">
            <p className="text-xl text-brand-text-muted animate-pulse">
              Загрузка матчей...
            </p>
          </div>
        ) : (
          <MatchList
            matches={displayedMatches}
            selectedLeague={selectedLeagueCode}
          />
        )}
      </section>

      {/* Правый сайдбар для турнирной таблицы - возвращаем заглушку */}
      <aside
        className={`w-full lg:w-72 xl:w-80 flex-shrink-0 hidden md:block lg:sticky ${sidebarStickyTop} self-start ${sidebarMaxHeight} overflow-y-auto rounded-lg`}
      >
        <div className="bg-brand-surface p-4 rounded-lg shadow-md min-h-[300px] flex flex-col">
          <h3 className="text-xl font-semibold mb-3 text-brand-primary-yellow">
            Турнирная таблица
          </h3>
          <p className="text-brand-text-muted flex-grow flex items-center justify-center text-center">
            Выберите лигу в левом меню для отображения таблицы.
          </p>
        </div>
      </aside>
    </div>
  );
}

export default HomePage;
