// frontend/src/components/Layout/LeagueSidebar.js
import React from "react";

function LeagueSidebar({ leagues, selectedLeague, onSelectLeague }) {
  return (
    // Фон самого блока сайдбара
    <div className="bg-brand-surface p-2 sm:p-3 rounded-lg shadow-md">
      {/* Заголовок "Лиги" убран по вашему запросу */}
      <ul className="space-y-1.5 sm:space-y-2">
        {leagues.map((league) => {
          // Определяем, активен ли текущий элемент лиги
          const isActive =
            selectedLeague === league.code ||
            (!selectedLeague && league.code === null); // Кнопка "ВСЕ" активна, если selectedLeague null
          return (
            <li key={league.code || "all-leagues"}>
              {" "}
              {/* Уникальный ключ */}
              <button
                onClick={() => onSelectLeague(league.code)}
                className={`
                  w-full flex flex-col items-center justify-center p-2 rounded-md 
                  transition-all duration-150 ease-in-out group 
                  focus:outline-none 
                  ${isActive ? "" : "hover:bg-gray-700/40"} 
                  `}
                title={league.name}
              >
                {/* Квадрат для логотипа или текста "ВСЕ" */}
                <div
                  className={`
                    w-14 h-14 sm:w-16 sm:h-16 mb-1 rounded-md 
                    flex items-center justify-center overflow-hidden 
                    transition-all duration-150
                    border-2 
                    ${
                      isActive
                        ? "bg-brand-primary-red border-brand-primary-red shadow-red-button-glow" // Активный квадрат
                        : "bg-gray-700 border-transparent group-hover:border-brand-primary-yellow/50" // Неактивный квадрат
                    }
                  `}
                >
                  {league.logo ? (
                    <img
                      src={league.logo}
                      alt={`${league.name} logo`}
                      className="w-full h-full object-contain p-1.5"
                    />
                  ) : (
                    <span
                      className={`text-lg sm:text-xl font-bold 
                      ${
                        isActive
                          ? "text-white"
                          : "text-brand-text-light group-hover:text-brand-primary-yellow"
                      }`}
                    >
                      {league.code === null
                        ? "ВСЕ"
                        : league.shortName || league.code}
                    </span>
                  )}
                </div>
                {/* Подпись под квадратиком */}
                <span
                  className={`text-xs text-center truncate w-full mt-1 px-1 transition-colors
                    ${
                      isActive
                        ? "text-brand-primary-red font-semibold"
                        : "text-brand-text-muted group-hover:text-brand-text-light"
                    }`}
                >
                  {league.name}
                </span>
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default LeagueSidebar;
