// frontend/src/components/MatchItem.js
import React from "react";
import { Link } from "react-router-dom";
// Если вы будете использовать date-fns-tz для точного МСК времени:
// import { formatInTimeZone } from 'date-fns-tz';

// Функция для форматирования даты и времени (можно вынести в utils)
const formatMatchDateTime = (utcDateString, localMatchTimeString) => {
  let dateToFormat = localMatchTimeString || utcDateString;
  if (!dateToFormat) return "Время не указано";

  try {
    const date = new Date(dateToFormat);
    // Для принудительного МСК на фронте, если localMatchTime не содержит инфо о поясе или вы хотите переопределить
    // Если utcDateString всегда UTC, то:
    // return formatInTimeZone(new Date(utcDateString), 'Europe/Moscow', 'dd.MM.yyyy, HH:mm', { timeZone: 'Europe/Moscow' }) + ' МСК';

    // Используем toLocaleString, который попытается использовать настройки браузера.
    // Добавляем информацию о поясе, если localMatchTimeString был использован.
    return (
      date.toLocaleString("ru-RU", {
        day: "2-digit",
        month: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      }) +
      (localMatchTimeString && !utcDateString.endsWith("Z")
        ? ` (Местное время стадиона)`
        : ` (UTC)`)
    ); // Уточняем, какое время
  } catch (e) {
    console.error(
      "Ошибка форматирования даты:",
      e,
      "Исходная строка:",
      dateToFormat
    );
    return typeof dateToFormat === "string"
      ? dateToFormat.substring(0, 16).replace("T", " ")
      : "Неверная дата";
  }
};

// Функция для определения цвета и текста прогноза
const getPredictionDisplay = (prediction) => {
  if (prediction === "H")
    return { text: "П1 (Хозяева)", colorClasses: "bg-green-500 text-white" };
  if (prediction === "A")
    return { text: "П2 (Гости)", colorClasses: "bg-brand-red text-white" };
  if (prediction === "D")
    return {
      text: "Ничья",
      colorClasses: "bg-brand-yellow text-brand-dark-bg",
    };
  return { text: prediction, colorClasses: "bg-gray-500 text-white" }; // Для других возможных значений
};

function MatchItem({ match }) {
  const predictionDisplay = match.Predicted_Outcome
    ? getPredictionDisplay(match.Predicted_Outcome)
    : null;

  return (
    // Обертка карточки матча, сделанная ссылкой
    <Link
      to={`/match/${match.id}`}
      className="block transform hover:scale-[1.02] transition-transform duration-200 ease-in-out group" // group для hover эффектов на дочерних элементах
    >
      {/* Основной контейнер карточки */}
      <div className="bg-brand-card-bg rounded-xl shadow-lg p-5 flex flex-col h-full group-hover:shadow-brand-red/40 transition-shadow duration-200">
        {/* Логотипы и VS */}
        <div className="flex justify-around items-center mb-4">
          <div className="flex flex-col items-center text-center w-2/5">
            {match.homeTeam_crest && (
              <img
                src={match.homeTeam_crest}
                alt={match.homeTeam_name}
                className="h-16 w-16 md:h-20 md:w-20 object-contain mb-2 transition-transform duration-200 group-hover:scale-110"
              />
            )}
            <span className="text-sm sm:text-base font-semibold text-brand-light-text truncate w-full">
              {match.homeTeam_name}
            </span>
          </div>
          <span className="text-xl sm:text-2xl font-bold text-gray-500 group-hover:text-brand-yellow transition-colors">
            VS
          </span>
          <div className="flex flex-col items-center text-center w-2/5">
            {match.awayTeam_crest && (
              <img
                src={match.awayTeam_crest}
                alt={match.awayTeam_name}
                className="h-16 w-16 md:h-20 md:w-20 object-contain mb-2 transition-transform duration-200 group-hover:scale-110"
              />
            )}
            <span className="text-sm sm:text-base font-semibold text-brand-light-text truncate w-full">
              {match.awayTeam_name}
            </span>
          </div>
        </div>

        {/* Прогноз */}
        {predictionDisplay && (
          <div className="my-3 text-center">
            <span
              className={`px-4 py-1.5 text-xs sm:text-sm font-bold rounded-full shadow-md ${predictionDisplay.colorClasses}`}
            >
              Прогноз: {predictionDisplay.text}
            </span>
          </div>
        )}

        {/* Если матч завершен, отображаем счет */}
        {match.status === "FINISHED" && (
          <p className="text-center text-lg font-bold my-2 text-brand-yellow">
            {match.score_fullTime_home} - {match.score_fullTime_away}
          </p>
        )}

        {/* Дата и время (внизу карточки) */}
        <div className="mt-auto text-center text-xs text-brand-muted-text pt-4">
          {" "}
          {/* mt-auto прижмет ко дну, pt-4 для отступа */}
          {formatMatchDateTime(match.utcDate, match.localMatchTime)}
          {/* Пояснение, какое время показывается, если оно не UTC по умолчанию */}
          {/* (localMatchTime ? ` (Местное время)` : ` (UTC)`) - уже учтено в formatMatchDateTime */}
        </div>
      </div>
    </Link>
  );
}

export default MatchItem;
