import { useEffect } from "react";
import { useLocation } from "react-router-dom";

// Принимает ref на прокручиваемый контейнер в качестве пропса
function ScrollToContentTop({ scrollContainerRef }) {
  const { pathname } = useLocation();

  useEffect(() => {
    if (scrollContainerRef.current) {
      // Убедитесь, что реф установлен
      scrollContainerRef.current.scrollTo({
        top: 0,
        left: 0,
        // behavior: 'smooth'
      });
    }
  }, [pathname, scrollContainerRef]); // Добавляем scrollContainerRef в зависимости, хотя обычно он не меняется

  return null;
}

export default ScrollToTop;
