import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import HomePage from "./pages/HomePage";
import MatchDetailPage from "./pages/MatchDetailPage";
import Header from "./components/Layout/Header";
import HistoryPage from "./pages/HistoryPage";
import TablesPage from "./pages/TablesPage";

function App() {
  return (
    <Router>
      {/* <Router> оборачивает все компоненты, которые будут использовать навигацию. */}
      {/* Это как "установка глобальной навигационной системы" для всего дома. */}

      <div className="flex flex-col min-h-screen bg-brand-bg text-brand-text-light">
        {/* Это основной контейнер для всего содержимого сайта. */}
        {/* `className="..."` - это стили, которые применяются к этому контейнеру (скорее всего, с использованием Tailwind CSS). */}
        {/* `flex flex-col`: элементы внутри будут располагаться вертикально (колонка). */}
        {/* `min-h-screen`: минимальная высота будет равна высоте всего экрана. */}
        {/* `bg-brand-bg`: фоновый цвет из твоих настроек Tailwind. */}
        {/* `text-brand-text-light`: цвет текста из твоих настроек Tailwind. */}
        {/* Это как "общая обложка" или "каркас" для всего сайта, задающий базовые цвета и расположение. */}

        <Header />
        {/* Здесь мы вставляем наш компонент `Header`. */}
        {/* Он будет отображаться на каждой странице, потому что он находится вне блока `Routes`. */}
        {/* Это как "шапка" твоего сайта, которая всегда на виду, независимо от того, в какой ты "комнате". */}

        <main className="flex-grow container mx-auto px-4 py-8 pt-16">
          {/* Это основной блок содержимого страницы. */}
          {/* `main`: HTML-тег, который обозначает основное содержимое документа. */}
          {/* `flex-grow`: позволяет этому блоку "растягиваться" и занимать все доступное пространство по вертикали, толкая подвал (если бы он был) вниз. */}
          {/* `container mx-auto px-4 py-8 pt-16`: снова стили Tailwind для центрирования содержимого, добавления отступов. */}
          {/* Это как "основная жилая площадь" дома, где будут меняться "комнаты". */}

          <Routes>
            {/* <Routes> содержит все определения маршрутов (путей) в приложении. */}
            {/* Он говорит: "Вот список всех возможных 'дорог' в моем доме". */}
            <Route path="/" element={<HomePage />} />
            {/* Это определяет один "путь" (маршрут). */}
            {/* `path="/" `: Когда адрес в браузере будет просто `/` (главная страница), */}
            {/* `element={<HomePage />}`: показать компонент `HomePage`. */}
            {/* Это как сказать: "Если пользователь идет по 'главной дороге' (URL `/`), покажи ему 'Главную комнату'". */}
            <Route path="/match/:matchId" element={<MatchDetailPage />} />
            {/* Еще один "путь". */}
            {/* `path="/match/:matchId"`: Когда адрес в браузере будет `/match/` и любое значение после (например, `/match/123`). */}
            {/* `:matchId` - это "переменная часть" адреса, которую мы сможем потом использовать в `MatchDetailPage` для получения ID матча. */}
            {/* `element={<MatchDetailPage />}`: показать компонент `MatchDetailPage`. */}
            {/* Это как сказать: "Если пользователь идет по 'дороге матчей' с номером, покажи ему 'Комнату деталей матча' и передай ей номер матча". */}
            <Route path="/history" element={<HistoryPage />} />{" "}
            {/* Новый роут */}
            {/* `path="/history"`: Когда адрес в браузере будет `/history`. */}
            {/* `element={<HistoryPage />}`: показать компонент `HistoryPage`. */}
            {/* Это как сказать: "Если пользователь идет по 'дороге истории', покажи ему 'Историческую комнату'". */}
            <Route path="/tables" element={<TablesPage />} /> {/* Новый роут */}
            {/* `path="/tables"`: Когда адрес в браузере будет `/tables`. */}
            {/* `element={<TablesPage />}`: показать компонент `TablesPage`. */}
            {/* Это как сказать: "Если пользователь идет по 'дороге таблиц', покажи ему 'Комнату таблиц'". */}
          </Routes>
        </main>
      </div>
    </Router>
  );
}

// 3. Экспорт компонента
export default App;
// Эта строчка делает наш компонент `App` доступным для использования в других файлах (например, в `index.js`, где он будет "запущен").
// Это как сказать: "Вот готовый план 'дома', его можно использовать для строительства".
