// frontend/src/components/Layout/LeagueSidebar.js
import React from "react";

function LeagueSidebar({ leagues, selectedLeague, onSelectLeague }) {
  return (
    // Убираем p-2 sm:p-3 отсюда, так как отступ теперь задается в HomePage.js на <aside>
    // Здесь только фон и скругление
    <div className="bg-brand-surface rounded-lg shadow-md">
      {/* Заголовок "Лиги" УБРАН */}
      <ul className="space-y-1 sm:space-y-1.5 py-2 sm:py-3">
        {" "}
        {/* Добавляем отступы py для списка */}
        {leagues.map((league) => {
          const isActive =
            selectedLeague === league.code ||
            (!selectedLeague && league.code === null);
          return (
            <li key={league.code || "all-leagues"}>
              <button
                onClick={() => onSelectLeague(league.code)}
                className={`w-full flex flex-col items-center justify-center p-2 rounded-md transition-all duration-150 ease-in-out group focus:outline-none 
                  ${isActive ? "bg-opacity-0" : "hover:bg-gray-700/40"}`}
                title={league.name}
              >
                {/* Квадрат для логотипа или текста "ВСЕ" */}
                <div
                  className={`
                    w-14 h-14 sm:w-16 sm:h-16 mb-1 rounded-md flex items-center justify-center overflow-hidden transition-all duration-150
                    border-2 
                    ${
                      isActive
                        ? "bg-brand-primary-red border-brand-primary-red shadow-league-active-square-glow"
                        : "bg-gray-700 border-transparent group-hover:border-brand-primary-yellow/50"
                    }
                  `}
                >
                  {league.logo ? (
                    <img
                      src={league.logo}
                      alt={`${league.name} logo`}
                      className="w-full h-full object-contain p-1.5"
                    />
                  ) : (
                    <span
                      className={`text-lg sm:text-xl font-bold 
                      ${
                        isActive
                          ? "text-white"
                          : "text-brand-text-light group-hover:text-brand-primary-yellow"
                      }`}
                    >
                      {league.code === null
                        ? "ВСЕ"
                        : league.shortName || league.code}
                    </span>
                  )}
                </div>
                {/* Подпись под квадратиком */}
                <span
                  className={`text-xs sm:text-sm text-center truncate w-full mt-1 px-1 transition-colors
                    ${
                      isActive
                        ? "text-brand-primary-red font-semibold"
                        : "text-brand-text-muted group-hover:text-brand-text-light"
                    }`}
                >
                  {league.name}
                </span>
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
}
export default LeagueSidebar;
