// frontend/src/pages/HomePage.js
import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import MatchList from "../components/MatchList";
import { getMatches } from "../api";
import LeagueSidebar from "../components/Layout/LeagueSidebar";

// Убедитесь, что пути к вашим локальным логотипам верны
// или замените их на null/URL, если логотипы еще не загружены локально.
import plLogo from "../assets/leagues/premier-league.png";
import blLogo from "../assets/leagues/bundesliga.png";
import saLogo from "../assets/leagues/serie-a.png";
import pdLogo from "../assets/leagues/la-liga.png";
import flLogo from "../assets/leagues/ligue-1.png";

const leaguesForSidebar = [
  { code: null, name: "ВСЕ", shortName: "Все", logo: null },
  { code: "PL", name: "Premier League", shortName: "АПЛ", logo: plLogo },
  { code: "BL1", name: "Bundesliga", shortName: "Бундеслига", logo: blLogo },
  { code: "SA", name: "Serie A", shortName: "Серия А", logo: saLogo },
  { code: "PD", name: "La Liga", shortName: "Ла Лига", logo: pdLogo },
  { code: "FL1", name: "Ligue 1", shortName: "Лига 1", logo: flLogo },
];

function HomePage() {
  const [allFetchedMatches, setAllFetchedMatches] = useState([]);
  const [displayedMatches, setDisplayedMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchParams, setSearchParams] = useSearchParams();
  const selectedLeagueCode = searchParams.get("league");

  // Загрузка всех матчей один раз при монтировании компонента
  useEffect(() => {
    const fetchAllMatches = async () => {
      setLoading(true);
      const data = await getMatches(null); // Загружаем все матчи всех лиг
      if (Array.isArray(data)) {
        setAllFetchedMatches(data);
      } else {
        console.error(
          "HomePage: Данные от getMatches не являются массивом!",
          data
        );
        setAllFetchedMatches([]);
      }
      setLoading(false);
    };
    fetchAllMatches();
  }, []); // Пустой массив зависимостей означает, что эффект выполнится один раз после первого рендера

  // Фильтрация и сортировка отображаемых матчей при изменении allFetchedMatches, selectedLeagueCode или статуса загрузки
  useEffect(() => {
    // Не выполняем фильтрацию, если основные данные еще грузятся
    if (loading && allFetchedMatches.length === 0) {
      setDisplayedMatches([]);
      return;
    }
    // Если загрузка завершена, но матчей нет
    if (!loading && allFetchedMatches.length === 0) {
      setDisplayedMatches([]);
      return;
    }

    let filteredMatchesSource = allFetchedMatches;

    // Фильтр по выбранной лиге
    if (selectedLeagueCode) {
      filteredMatchesSource = filteredMatchesSource.filter(
        (match) => match.competition_code === selectedLeagueCode
      );
    }

    // Фильтр: только предстоящие матчи (не 'FINISHED') в пределах ближайшей недели
    const now = new Date();
    const todayStart = new Date(
      now.getFullYear(),
      now.getMonth(),
      now.getDate()
    ); // Начало сегодняшнего дня
    const oneWeekFromNowEnd = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000);
    // Устанавливаем конец дня для oneWeekFromNowEnd для более инклюзивного диапазона
    oneWeekFromNowEnd.setHours(23, 59, 59, 999);

    const upcomingFiltered = filteredMatchesSource.filter((match) => {
      if (!match.utcDate) return false; // Пропускаем матчи без даты
      const matchDate = new Date(match.utcDate); // utcDate должен быть в UTC
      return (
        match.status !== "FINISHED" &&
        matchDate >= todayStart && // Матч сегодня или в будущем
        matchDate <= oneWeekFromNowEnd // Матч в пределах недели
      );
    });

    // Сортировка предстоящих матчей по дате (самые ближайшие вверху)
    upcomingFiltered.sort((a, b) => new Date(a.utcDate) - new Date(b.utcDate));

    setDisplayedMatches(upcomingFiltered);
  }, [allFetchedMatches, selectedLeagueCode, loading]); // Зависимости эффекта

  const handleLeagueSelect = (leagueCode) => {
    // Устанавливаем query-параметр 'league' в URL или очищаем его, если выбрано "ВСЕ"
    setSearchParams(leagueCode ? { league: leagueCode } : {});
  };

  // Определяем заголовок страницы на основе выбранной лиги
  const currentLeagueObject = selectedLeagueCode
    ? leaguesForSidebar.find((l) => l.code === selectedLeagueCode)
    : // Если лига не выбрана (selectedLeagueCode is null), используем заголовок по умолчанию
      leaguesForSidebar.find((l) => l.code === null) || {
        name: "Все предстоящие матчи (неделя)",
      };

  const pageTitle = currentLeagueObject
    ? currentLeagueObject.name
    : "Все предстоящие матчи (неделя)";
  if (
    selectedLeagueCode &&
    currentLeagueObject &&
    currentLeagueObject.name !== "ВСЕ"
  ) {
    // Для конкретной лиги можно использовать ее имя
  } else {
    // Для "ВСЕ" или если объект не найден (хотя не должен)
    // pageTitle = 'Все предстоящие матчи (неделя)';
  }

  // Отступ сверху для sticky сайдбаров: должен быть равен высоте хедера.
  // Если <Header> имеет класс h-16 (что равно 4rem), то и здесь top-16.
  const sidebarStickyTop = "top-16";

  // Максимальная высота сайдбара:
  // высота окна (100vh)
  // минус высота хедера (4rem)
  // минус нижний padding основного контента страницы (py-8 у <main> в App.js -> theme('spacing.8') это 2rem)
  const sidebarMaxHeight = "max-h-[calc(100vh_-_4rem_-_theme(spacing.8))]";

  return (
    <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
      {/* Левый сайдбар */}
      <aside
        className={`
          w-full lg:w-60 xl:w-64 flex-shrink-0 
          mb-6 lg:mb-0 
          lg:sticky ${sidebarStickyTop} self-start 
          ${sidebarMaxHeight} overflow-y-auto 
          rounded-lg
        `}
      >
        <LeagueSidebar
          leagues={leaguesForSidebar}
          selectedLeague={selectedLeagueCode} // Передаем null для "ВСЕ"
          onSelectLeague={handleLeagueSelect}
        />
      </aside>

      {/* Центральная колонка с матчами */}
      <section className="w-full lg:flex-grow min-w-0">
        <h1 className="text-2xl sm:text-3xl font-bold mb-6 text-center text-brand-primary-yellow uppercase tracking-wider">
          {/* Используем имя из currentLeagueObject или дефолтное, если currentLeagueObject это "ВСЕ" */}
          {selectedLeagueCode && currentLeagueObject
            ? currentLeagueObject.name
            : "Все предстоящие матчи (неделя)"}
        </h1>
        {/* Показываем загрузку, только если displayedMatches еще не заполнен и идет загрузка */}
        {loading && displayedMatches.length === 0 ? (
          <div className="flex justify-center items-center h-64">
            <p className="text-xl text-brand-text-muted animate-pulse">
              Загрузка матчей...
            </p>
          </div>
        ) : (
          <MatchList
            matches={displayedMatches}
            selectedLeague={selectedLeagueCode}
          />
        )}
      </section>

      {/* Правый сайдбар */}
      <aside
        className={`
          w-full lg:w-72 xl:w-80 flex-shrink-0 
          hidden md:block 
          lg:sticky ${sidebarStickyTop} self-start 
          ${sidebarMaxHeight} overflow-y-auto 
          rounded-lg
        `}
      >
        <div className="bg-brand-surface p-4 rounded-lg shadow-md min-h-[300px]">
          <h3 className="text-xl font-semibold mb-3 text-brand-primary-yellow">
            Турнирная таблица
          </h3>
          <p className="text-brand-text-muted">
            {selectedLeagueCode && currentLeagueObject
              ? `Таблица для ${currentLeagueObject.name}`
              : "Выберите лигу"}
          </p>
          {/* Здесь будет ваш виджет турнирной таблицы */}
        </div>
      </aside>
    </div>
  );
}

export default HomePage;
