import React from "react";
import MatchItem from "./MatchItem";

// Вспомогательная карта для получения полного имени лиги по коду, если competition_name отсутствует.
// Используется для заголовков групп, когда selectedLeague === null.
const leagues = {
  // Этот объект используется как "словарь" для преобразования короткого кода лиги (например, "PL") в полное название
  // (например, "Premier League"). Это полезно, если данные, приходящие с бэкенда, не всегда содержат полное имя лиги.
  PL: "Premier League",
  PD: "La Liga",
  SA: "Serie A",
  BL1: "Bundesliga",
  FL1: "Ligue 1",
  // Добавьте другие лиги, если они могут появиться в данных, но не в leaguesForSidebar
};

// MatchList теперь принимает orderedLeagues для упорядочивания групп лиг
function MatchList({ matches, selectedLeague, orderedLeagues }) {
  // Это функциональный React-компонент `MatchList`.
  // Он принимает три "пропса" (свойства):
  // - `matches`: массив объектов, каждый из которых представляет собой данные о матче.
  // - `selectedLeague`: код выбранной лиги (или `null`, если выбрано "ВСЕ лиги").
  // - `orderedLeagues`: массив объектов лиг из `HomePage.js`, используемый для определения порядка отображения групп лиг.

  // 1. Проверки входных данных и обработка пустых состояний

  if (!Array.isArray(matches)) {
    // Проверяем, что `matches` действительно является массивом.
    // Если нет, выводим сообщение об ошибке, чтобы предотвратить сбой приложения.
    return (
      <p className="text-center text-color-primary-red py-10">
        Ошибка: данные матчей не в ожидаемом формате.
      </p>
    );
  }
  if (matches.length === 0) {
    // Если массив `matches` пустой (т.е. нет матчей для отображения),
    // выводим соответствующее сообщение.
    return (
      <p className="text-center text-color-text-muted py-10">
        Матчи на ближайшую неделю не найдены.
      </p>
    );
  }

  // 2. Логика группировки матчей по лигам (если не выбрана конкретная лига)

  let groupedMatchesByCode = {}; // Инициализируем пустой объект для хранения сгруппированных матчей.
  // Ключами будут коды лиг, значениями - объекты с именем лиги, кодом и массивом матчей этой лиги.

  if (!selectedLeague) {
    // Этот блок кода выполняется ТОЛЬКО, если в `HomePage` выбрана опция "ВСЕ лиги"
    // (т.е. `selectedLeague` равен `null`).
    // Если выбрана конкретная лига, то матчи уже отфильтрованы, и группировка по лигам не нужна.

    matches.forEach((match) => {
      // Перебираем каждый матч в полученном массиве `matches`.
      const leagueCode = match.competition_code;
      // Получаем код лиги для текущего матча.
      // Получаем полное имя лиги для отображения, если оно не указано в match.competition_name
      const leagueName =
        match.competition_name ||
        // Пытаемся взять имя лиги из данных матча.
        leagues[leagueCode] ||
        // Если его нет, ищем в нашем словаре `leagues`.
        "Другие лиги";
      // Если нигде не нашли, используем общее название "Другие лиги".

      if (!groupedMatchesByCode[leagueCode]) {
        // Если для этой лиги еще нет группы в `groupedMatchesByCode`, создаем ее.
        groupedMatchesByCode[leagueCode] = {
          name: leagueName, // Имя лиги для заголовка.
          code: leagueCode, // Код лиги.
          matches: [], // Пустой массив для матчей этой лиги.
        };
      }
      groupedMatchesByCode[leagueCode].matches.push(match);
      // Добавляем текущий матч в соответствующую группу лиги.
    });
  }

  // 3. Логика определения порядка отображения групп лиг

  let leaguesToDisplay = [];
  // Инициализируем пустой массив, который будет содержать группы лиг в правильном порядке.

  if (!selectedLeague) {
    // Этот блок также выполняется ТОЛЬКО, если выбрано "ВСЕ лиги".
    const addedLeagueCodes = new Set(); // Используем Set для быстрого отслеживания уже добавленных лиг, чтобы избежать дубликатов.

    // 1. Добавляем лиги в заданном порядке из orderedLeagues (полученного из sidebar'а)
    orderedLeagues.forEach((sidebarLeague) => {
      // Перебираем лиги в том порядке, в котором они заданы в сайдбаре (`leaguesForSidebar` из `HomePage.js`).
      if (sidebarLeague.code && groupedMatchesByCode[sidebarLeague.code]) {
        // Если это не "ВСЕ" (sidebarLeague.code не null) И для этой лиги есть матчи, которые мы сгруппировали,
        leaguesToDisplay.push(groupedMatchesByCode[sidebarLeague.code]);
        // Добавляем эту группу лиг в наш список для отображения.
        addedLeagueCodes.add(sidebarLeague.code); // Отмечаем, что этот код лиги уже добавлен.
      }
    });

    // 2. Добавляем "Другие лиги" (те, которых нет в orderedLeagues), если есть
    // Итерируем по всем группам, которые были сформированы, но еще не добавлены в `leaguesToDisplay`.
    Object.keys(groupedMatchesByCode).forEach((code) => {
      // Проходим по всем кодам лиг, для которых у нас есть сгруппированные матчи.
      if (!addedLeagueCodes.has(code)) {
        // Если эта лига еще не была добавлена в `leaguesToDisplay` (т.е. ее не было в `orderedLeagues` или она была там, но после "ВСЕ"),
        leaguesToDisplay.push(groupedMatchesByCode[code]);
        // Добавляем ее в конец списка.
      }
    });
  } else {
    // Если выбрана КОНКРЕТНАЯ лига, то группировка не нужна.
    // В этом случае `MatchList` просто отобразит уже отфильтрованные матчи, без заголовков групп.
    // Этот `else` блок на самом деле ничего не делает с `leaguesToDisplay` или `groupedMatchesByCode`,
    // так как рендеринг будет происходить на основе исходного `matches` пропса,
    // который уже отфильтрован в `HomePage`.
  }

  // 4. JSX (что будет отрисовано компонентом)

  return (
    <div>
      {!selectedLeague ? (
        // Условный рендеринг: если `selectedLeague` равен `null` (выбрано "ВСЕ"),
        // тогда отображаем матчи, сгруппированные по лигам.
        leaguesToDisplay.map(
          (
            leagueGroup // Итерируем по отсортированному списку групп лиг (`leaguesToDisplay`).
          ) => (
            <div key={leagueGroup.code || leagueGroup.name} className="mb-10">
              {" "}
              {/* Контейнер для каждой группы лиг. `key` важен для React. `mb-10` - нижний отступ. */}
              {/* Заголовок лиги с желтым текстом и красной подсветкой */}
              <h2 className="text-2xl sm:text-3xl font-bold mb-6 text-center text-color-primary-yellow uppercase tracking-wider relative">
                {leagueGroup.name}
                {/* Отображаем название лиги (например, "Premier League"). */}
                {/* Красная линия-подсветка по краям */}
                <div className="absolute left-1/2 -translate-x-1/2 bottom-0 w-3/4 h-0.5 bg-gradient-to-r from-transparent via-color-primary-red to-transparent opacity-75"></div>
                {/* Это декоративная линия под заголовком, созданная с помощью Tailwind CSS. */}
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
                {/* Сетка для отображения матчей: 1 колонка на мобильных, 2 на средних и больших экранах. */}
                {leagueGroup.matches.map((match) => (
                  // Итерируем по матчам внутри текущей группы лиг.
                  <MatchItem key={match.id} match={match} />
                  // Для каждого матча рендерим компонент `MatchItem`, передавая ему данные `match` и уникальный `key`.
                ))}
              </div>
            </div>
          )
        )
      ) : (
        // Иначе (если выбрана конкретная лига), отображаем матчи без группировки и заголовков лиг.
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
          {/* Аналогичная сетка для отображения матчей. */}
          {matches.map((match) => (
            // Итерируем по всем матчам, которые были переданы в `MatchList` (они уже отфильтрованы по одной лиге).
            <MatchItem key={match.id} match={match} />
            // Рендерим `MatchItem` для каждого матча.
          ))}
        </div>
      )}
    </div>
  );
}

export default MatchList;
