// frontend/src/components/Layout/Header.js
import React, { useState, useEffect, useRef } from "react";
import { Link, NavLink, useLocation } from "react-router-dom";
import NeuroPredictLogo from "../../assets/NeuroPredictLogo.png"; // Убедитесь, что логотип на месте

const leaguesNav = [
  { code: "PL", name: "Premier League" },
  { code: "BL1", name: "Bundesliga" },
  { code: "SA", name: "Serie A" },
  { code: "PD", name: "La Liga" },
  { code: "FL1", name: "Ligue 1" },
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const location = useLocation();

  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsLeaguesDropdownOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [dropdownRef]);

  useEffect(() => {
    setIsLeaguesDropdownOpen(false);
  }, [location.pathname, location.search]);

  // Стили для кнопок навигации, похожие на ваш первоначальный желтый овал
  const navButtonBaseClasses =
    "px-4 py-2 rounded-full text-sm font-semibold transition-all duration-150 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-brand-bg-nav";
  const navButtonActiveClasses =
    "bg-brand-yellow-accent text-brand-bg-dark shadow-yellow-glow-button";
  const navButtonInactiveClasses =
    "bg-gray-700 text-brand-text-muted hover:bg-gray-600 hover:text-brand-text-light";

  return (
    <div className="sticky top-0 z-50 w-full">
      {/* Верхний красный "hero" блок */}
      <div className="bg-red-hero-gradient shadow-red-glow-soft">
        {" "}
        {/* Градиент и свечение */}
        <div className="container mx-auto text-center py-5 md:py-6">
          {" "}
          {/* Уменьшил немного паддинги */}
          <Link to="/" className="inline-block">
            <div className="flex items-center justify-center">
              <img
                src={NeuroPredictLogo}
                alt="NeuroPredict Logo"
                className="h-10 md:h-12 w-auto mr-3"
              />
              <h1 className="text-3xl md:text-4xl font-bold text-brand-yellow-accent tracking-wide">
                NeuroPredict
              </h1>
            </div>
            <p className="text-xs md:text-sm text-gray-200 mt-1">
              Прогнозы с точностью до гола
            </p>
          </Link>
        </div>
      </div>

      {/* Навигационная панель (темно-серая) */}
      <nav className="bg-brand-bg-nav shadow-md">
        <div className="container mx-auto px-4 h-14 flex justify-center items-center space-x-2 sm:space-x-3 md:space-x-4">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `${navButtonBaseClasses} ${
                isActive ? navButtonActiveClasses : navButtonInactiveClasses
              }`
            }
          >
            Главная
          </NavLink>

          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setIsLeaguesDropdownOpen((prev) => !prev)}
              className={`${navButtonBaseClasses} ${navButtonInactiveClasses} flex items-center`} // Используем стили неактивной кнопки для самой кнопки дропдауна
            >
              Лиги
              <svg
                className={`w-4 h-4 ml-1.5 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                  clipRule="evenodd"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              <div className="absolute left-1/2 -translate-x-1/2 mt-2 w-48 bg-gray-700 rounded-md shadow-xl z-30 py-1 border border-gray-600">
                {leaguesNav.map((league) => (
                  <NavLink
                    key={league.code}
                    to={`/?league=${league.code}`}
                    className="block px-4 py-2 text-sm text-brand-text-light hover:bg-gray-600 hover:text-brand-yellow-accent w-full text-left"
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>

          <NavLink
            to="/history"
            className={({ isActive }) =>
              `${navButtonBaseClasses} ${
                isActive ? navButtonActiveClasses : navButtonInactiveClasses
              }`
            }
          >
            История матчей
          </NavLink>
          <NavLink
            to="/tables"
            className={({ isActive }) =>
              `${navButtonBaseClasses} ${
                isActive ? navButtonActiveClasses : navButtonInactiveClasses
              }`
            }
          >
            Турнирные таблицы
          </NavLink>
        </div>
      </nav>
    </div>
  );
}

export default Header;
