// frontend/src/components/MatchItem.js
import React from "react";
import { Link } from "react-router-dom";

// Функция форматирования даты и времени (можно вынести в utils)
const formatMatchDateTime = (utcDateString, localMatchTimeString) => {
  let dateToFormat = localMatchTimeString || utcDateString;
  if (!dateToFormat) return "Время не указано";
  try {
    const date = new Date(dateToFormat);
    return (
      date.toLocaleString("ru-RU", {
        day: "2-digit",
        month: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      }) +
      (localMatchTimeString && !String(utcDateString).endsWith("Z")
        ? ` (Местн.)`
        : ` (UTC)`)
    );
  } catch (e) {
    return String(dateToFormat).substring(0, 16).replace("T", " ");
  }
};

// Функция для определения цвета и текста прогноза (как у вас было)
const getPredictionDisplay = (prediction) => {
  if (prediction === "H")
    return { text: "П1", colorClasses: "bg-green-600 text-white" }; // Можете использовать brand-цвета
  if (prediction === "A")
    return { text: "П2", colorClasses: "bg-brand-red-primary text-white" };
  if (prediction === "D")
    return {
      text: "X",
      colorClasses: "bg-brand-yellow-accent text-brand-bg-dark",
    };
  return { text: prediction, colorClasses: "bg-gray-600 text-white" };
};

function MatchItem({ match }) {
  const predictionDisplay = match.Predicted_Outcome
    ? getPredictionDisplay(match.Predicted_Outcome)
    : null;

  return (
    <Link
      to={`/match/${match.id}`}
      className="block transform hover:scale-[1.02] transition-transform duration-200 ease-in-out group"
    >
      <div className="bg-brand-bg-card rounded-xl shadow-lg p-5 flex flex-col h-full group-hover:shadow-red-glow-soft transition-shadow duration-200">
        {/* Команды и логотипы */}
        <div className="flex justify-around items-center mb-3">
          <div className="flex flex-col items-center text-center w-2/5">
            {match.homeTeam_crest && (
              <img
                src={match.homeTeam_crest}
                alt={match.homeTeam_name}
                className="h-14 w-14 md:h-16 md:w-16 object-contain mb-1 group-hover:scale-105 transition-transform"
              />
            )}
            <span className="text-sm font-semibold text-brand-text-light truncate w-full">
              {match.homeTeam_name}
            </span>
          </div>
          <span className="text-xl font-bold text-brand-text-muted group-hover:text-brand-orange-title transition-colors">
            VS
          </span>
          <div className="flex flex-col items-center text-center w-2/5">
            {match.awayTeam_crest && (
              <img
                src={match.awayTeam_crest}
                alt={match.awayTeam_name}
                className="h-14 w-14 md:h-16 md:w-16 object-contain mb-1 group-hover:scale-105 transition-transform"
              />
            )}
            <span className="text-sm font-semibold text-brand-text-light truncate w-full">
              {match.awayTeam_name}
            </span>
          </div>
        </div>

        {/* Прогноз */}
        {predictionDisplay && (
          <div className="my-3 text-center">
            <span
              className={`px-3 py-1 text-xs sm:text-sm font-bold rounded-full shadow-md ${predictionDisplay.colorClasses}`}
            >
              Прогноз: {predictionDisplay.text}
            </span>
            {/* Если хотите показать вероятности, как раньше:
            <p className="text-xs text-brand-text-muted mt-1">
              (H: {match.Prob_H?.toFixed(2)}, D: {match.Prob_D?.toFixed(2)}, A: {match.Prob_A?.toFixed(2)})
            </p> 
            */}
          </div>
        )}

        {match.status === "FINISHED" && (
          <p className="text-center text-lg font-bold my-2 text-brand-orange-title">
            {match.score_fullTime_home} - {match.score_fullTime_away}
          </p>
        )}

        {/* Если нет ни прогноза, ни счета, можно добавить заполнитель для высоты */}
        {!predictionDisplay && match.status !== "FINISHED" && (
          <div className="my-3 h-10"></div> // Заполнитель высоты
        )}

        {/* Дата и время */}
        <div className="mt-auto text-center text-xs text-brand-text-muted pt-3">
          {formatMatchDateTime(match.utcDate, match.localMatchTime)}
        </div>
      </div>
    </Link>
  );
}

export default MatchItem;
