// frontend/src/components/Layout/LeagueSidebar.js
import React from "react";

function LeagueSidebar({ leagues, selectedLeague, onSelectLeague }) {
  return (
    <div className="bg-gray-800 p-4 rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4 text-brand-yellow">Лиги</h2>
      <ul className="space-y-2">
        {leagues.map((league) => (
          <li key={league.code || "all"}>
            <button
              onClick={() => onSelectLeague(league.code)}
              className={`w-full text-left px-3 py-2 rounded-md transition-colors focus:outline-none
                ${
                  selectedLeague === league.code ||
                  (!selectedLeague && !league.code) // Если выбран "ВСЕ" и текущий элемент "ВСЕ"
                    ? "bg-brand-red text-white font-semibold"
                    : "text-gray-300 hover:bg-gray-700 hover:text-brand-yellow"
                }`}
            >
              <div className="flex items-center">
                {league.logo && (
                  <img
                    src={league.logo}
                    alt={league.name}
                    className="w-6 h-6 mr-3 rounded-sm object-contain"
                  />
                )}
                {!league.logo &&
                  league.code !== null && ( // Заглушка, если нет лого, но это не "ВСЕ"
                    <div className="w-6 h-6 mr-3 rounded-sm bg-gray-700 flex items-center justify-center text-xs">
                      {league.code}
                    </div>
                  )}
                {league.name}
              </div>
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default LeagueSidebar;
