// frontend/src/components/Layout/LeagueSidebar.js
import React from "react";

function LeagueSidebar({ leagues, selectedLeague, onSelectLeague }) {
  return (
    // ИЗМЕНЕНО: Убрал p-4, чтобы кнопки занимали всю ширину, если нужно. Отступы будут у элементов.
    <div className="bg-gray-800 rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-3 text-brand-yellow px-4 pt-4">
        Лиги
      </h2>{" "}
      {/* Добавил отступы для заголовка */}
      <ul className="space-y-1 p-2">
        {" "}
        {/* Небольшой отступ между кнопками и внутренние отступы для списка */}
        {leagues.map((league) => (
          <li key={league.code || "all"}>
            <button
              onClick={() => onSelectLeague(league.code)}
              className={`w-full flex flex-col items-center justify-center p-3 rounded-md transition-all duration-200 ease-in-out focus:outline-none
                hover:bg-gray-700
                ${
                  selectedLeague === league.code ||
                  (!selectedLeague && !league.code)
                    ? "bg-brand-red text-white ring-2 ring-brand-yellow shadow-lg transform scale-105" // Стиль для активной кнопки
                    : "text-gray-300 hover:text-brand-yellow" // Стиль для неактивной
                }`}
              title={league.name} // Добавляем title для полного названия при наведении
            >
              {/* Квадратик для логотипа или текста "ВСЕ" */}
              <div className="w-12 h-12 md:w-14 md:h-14 lg:w-16 lg:h-16 mb-2 bg-gray-700 rounded flex items-center justify-center overflow-hidden">
                {league.logo ? (
                  <img
                    src={league.logo}
                    alt={league.name}
                    className="w-full h-full object-contain"
                  />
                ) : (
                  // Для "ВСЕ" или если нет лого
                  <span className="text-sm md:text-base font-bold">
                    {league.code === null ? "ВСЕ" : league.code}
                  </span>
                )}
              </div>
              {/* Подпись под квадратиком */}
              <span className="text-xs md:text-sm text-center truncate w-full">
                {league.code === null ? "" : league.name}{" "}
                {/* Не показываем подпись для "ВСЕ" или берем короткое имя */}
              </span>
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default LeagueSidebar;
