// frontend/src/components/MatchList.js
import React from "react";
import MatchItem from "./MatchItem";

function MatchList({ matches, selectedLeague }) {
  // selectedLeague передается для возможной будущей логики здесь
  if (!Array.isArray(matches)) {
    // Добавил стили Tailwind для сообщения об ошибке
    return (
      <p className="text-center text-brand-red py-10">
        Ошибка: данные матчей не в ожидаемом формате.
      </p>
    );
  }

  if (matches.length === 0) {
    // Добавил стили Tailwind
    return (
      <p className="text-center text-brand-muted-text py-10">
        Матчи на ближайшую неделю не найдены.
      </p>
    );
  }

  // Группировка матчей по лигам, если не выбрана конкретная лига (selectedLeague === null)
  let groupedMatches = {};
  if (!selectedLeague && matches.length > 0) {
    matches.forEach((match) => {
      // Используем competition_name или competition_code, если имя отсутствует
      const leagueIdentifier =
        match.competition_name || match.competition_code || "Другие лиги";
      if (!groupedMatches[leagueIdentifier]) {
        groupedMatches[leagueIdentifier] = [];
      }
      groupedMatches[leagueIdentifier].push(match);
    });
  }

  return (
    <div>
      {!selectedLeague && Object.keys(groupedMatches).length > 0 ? (
        // Отображение с группировкой по лигам
        Object.keys(groupedMatches).map((leagueIdentifier) => (
          <div key={leagueIdentifier} className="mb-10">
            {" "}
            {/* Увеличил отступ между группами лиг */}
            <h2 className="text-2xl font-semibold mb-4 text-brand-yellow border-b-2 border-gray-700 pb-2">
              {leagueIdentifier}
            </h2>
            {/* Используем grid для отображения карточек в несколько колонок */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
              {groupedMatches[leagueIdentifier].map((match) => (
                <MatchItem key={match.id} match={match} />
              ))}
            </div>
          </div>
        ))
      ) : (
        // Отображение для одной выбранной лиги или если матчи не нужно группировать
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
          {matches.map((match) => (
            <MatchItem key={match.id} match={match} />
          ))}
        </div>
      )}
    </div>
  );
}

export default MatchList;
