// frontend/src/components/MatchItem.js

import React from "react";
// Импортируем базовую библиотеку React, необходимую для создания пользовательских интерфейсов.

import { Link } from "react-router-dom";
// Импортируем компонент `Link` из 'react-router-dom'.
// `Link` используется для создания ссылок в приложении SPA (Single Page Application),
// которые позволяют переходить на другие страницы без перезагрузки всей страницы.

// Функция для форматирования даты и времени (можно вынести в utils)
const formatMatchDateTime = (utcDateString, localMatchTimeString) => {
  // Эта вспомогательная функция форматирует строку даты и времени матча в более читабельный формат.
  // Принимает `utcDateString` (дата/время в UTC) и `localMatchTimeString` (локальное время матча, если доступно).

  let dateToFormat = localMatchTimeString || utcDateString;
  // Предпочитаем использовать `localMatchTimeString`, если он предоставлен; иначе используем `utcDateString`.

  if (!dateToFormat) return "Время не указано";
  // Если ни одна из строк даты/времени не предоставлена, возвращаем соответствующее сообщение.

  try {
    // Используем блок try-catch для обработки ошибок при создании даты.
    const date = new Date(dateToFormat);
    // Создаем объект Date из переданной строки.

    return (
      date.toLocaleString("ru-RU", {
        // Форматируем дату и время, используя локальные настройки (в данном случае 'ru-RU' для русского языка).
        day: "2-digit", // День в двухзначном формате (например, 01).
        month: "2-digit", // Месяц в двухзначном формате (например, 05).
        year: "numeric", // Год (например, 2024).
        hour: "2-digit", // Час в двухзначном формате (например, 14).
        minute: "2-digit", // Минуты в двухзначном формате (например, 30).
      }) +
      // Добавляем пометку о часовом поясе.
      (localMatchTimeString && !String(utcDateString).endsWith("Z")
        ? ` (Местн.)` // Если есть локальное время и UTC-строка не заканчивается 'Z' (что обычно означает UTC), то это локальное время.
        : ` (UTC)`) // Иначе, это UTC время.
    );
  } catch (e) {
    // Если произошла ошибка при форматировании даты (например, невалидная строка),
    // возвращаем обрезанную исходную строку, заменяя 'T' на пробел для читабельности.
    return String(dateToFormat).substring(0, 16).replace("T", " ");
  }
};

// Функция для определения цвета и текста прогноза (теперь более понятный текст)
const getPredictionDisplay = (prediction) => {
  // Эта вспомогательная функция принимает код прогноза (например, "H", "A", "D")
  // и возвращает объект с текстом для отображения и CSS-классами для цвета.

  if (prediction === "H")
    // Если прогноз на победу "Home" (хозяев),
    return { text: "Прогноз: П1", colorClasses: "bg-green-600 text-white" }; // Возвращаем "П1" и зеленые классы.
  if (prediction === "A")
    // Если прогноз на победу "Away" (гостей),
    return {
      text: "Прогноз: П2",
      colorClasses: "bg-color-primary-red text-white",
    }; // Возвращаем "П2" и красные классы.
  if (prediction === "D")
    // Если прогноз на "Draw" (ничью),
    return {
      text: "Прогноз: X",
      colorClasses: "bg-color-primary-yellow text-color-bg",
    }; // Возвращаем "X" и желтые классы.
  return {
    // Для любого другого (неизвестного) прогноза,
    text: `Прогноз: ${prediction}`, // Возвращаем исходный код прогноза.
    colorClasses: "bg-gray-600 text-white",
  }; // И серые классы.
};

function MatchItem({ match }) {
  // Компонент `MatchItem` - отвечает за отображение одной карточки матча.
  // Принимает один "пропс": `match` - объект, содержащий все данные о матче.

  const predictionDisplay = match.Predicted_Outcome
    ? getPredictionDisplay(match.Predicted_Outcome)
    : null;
  // Определяем данные для отображения прогноза, если `Predicted_Outcome` существует в объекте `match`.

  return (
    // Обертка карточки матча, сделанная ссылкой
    <Link
      to={`/match/${match.id}`}
      // Используем компонент `Link` из `react-router-dom`.
      // `to` указывает на URL, куда перейдет пользователь при клике на карточку.
      // В данном случае, это `/match/ИД_МАТЧА`, что приведет на страницу деталей матча.

      // Классы для всей карточки: фон, скругление, тень, эффект наведения
      className="block bg-card-bg-gradient rounded-lg shadow-xl border border-color-card-border overflow-hidden
                 transform hover:-translate-y-1 hover:shadow-card-hover transition-all duration-300 ease-in-out"
      // Классы Tailwind CSS для стилизации карточки:
      // `block`: делает ссылку блочным элементом, чтобы она занимала всю доступную ширину.
      // `bg-card-bg-gradient`: фоновый градиент для карточки (определен в Tailwind конфиге).
      // `rounded-lg`: скругленные углы.
      // `shadow-xl`: большая тень.
      // `border border-color-card-border`: рамка с цветом из Tailwind конфига.
      // `overflow-hidden`: обрезает содержимое, выходящее за границы (для скругления).
      // `transform hover:-translate-y-1`: при наведении карточка слегка поднимается вверх.
      // `hover:shadow-card-hover-glow`: при наведении добавляется эффект свечения тени.
      // `transition-all duration-300 ease-in-out`: плавная анимация всех изменений при наведении.
    >
      <div className="p-4 md:p-5 flex flex-col h-full relative">
        {" "}
        {/* padding, flex-колонка, h-full, relative для полоски */}
        {/* Внутренний контейнер карточки:
            `p-4 md:p-5`: отступы внутри (больше на средних экранах).
            `flex flex-col`: элементы внутри располагаются вертикально.
            `h-full`: занимает всю высоту родителя (Link).
            `relative`: для позиционирования абсолютной красной полоски. */}
        {/* Красная вертикальная полоска слева */}
        <div className="absolute top-0 left-0 w-1 h-full bg-gradient-to-b from-transparent via-color-primary-red to-transparent opacity-75"></div>
        {/* Декоративная вертикальная полоска слева:
            `absolute`: позиционируется абсолютно внутри `relative` родителя.
            `top-0 left-0`: привязана к верхнему левому углу.
            `w-1 h-full`: ширина 1 пиксель, высота 100%.
            `bg-gradient-to-b from-transparent via-color-primary-red to-transparent`: градиент от прозрачного через красный к прозрачному.
            `opacity-75`: полупрозрачная. */}
        {/* Контент карточки с отступом от левой полоски */}
        <div className="pl-3">
          {/* Контейнер для всего содержимого карточки, с левым отступом, чтобы не перекрываться полоской. */}

          {/* Логотипы и VS */}
          <div className="flex justify-around items-center mb-3">
            {/* Контейнер для командных логотипов и текста "VS".
                `flex`: элементы располагаются горизонтально.
                `justify-around`: равномерно распределяет пространство между элементами.
                `items-center`: выравнивает элементы по центру по вертикали.
                `mb-3`: нижний отступ. */}
            <div className="flex flex-col items-center text-center w-2/5">
              {/* Контейнер для домашней команды.
                  `flex flex-col`: элементы (логотип, название) располагаются вертикально.
                  `items-center`: выравнивает по центру.
                  `text-center`: текст по центру.
                  `w-2/5`: занимает 40% ширины. */}
              {match.homeTeam_crest && (
                // Условный рендеринг: если есть URL для логотипа домашней команды, показываем его.
                <img
                  src={match.homeTeam_crest} // Источник изображения.
                  alt={match.homeTeam_name} // Альтернативный текст для доступности.
                  className="h-16 w-16 object-contain mb-1" // Размеры, масштабирование, нижний отступ.
                />
              )}
              <span className="text-sm font-semibold text-color-text-white truncate w-full">
                {match.homeTeam_name}
                {/* Название домашней команды.
                    `truncate w-full`: обрезает текст, если он слишком длинный, и добавляет многоточие. */}
              </span>
            </div>
            <span className="text-2xl font-bold text-color-text-second">
              VS
            </span>
            {/* Текст "VS" (против) по центру. */}
            <div className="flex flex-col items-center text-center w-2/5">
              {/* Аналогичный контейнер для гостевой команды. */}
              {match.awayTeam_crest && (
                <img
                  src={match.awayTeam_crest}
                  alt={match.awayTeam_name}
                  className="h-16 w-16 object-contain mb-1"
                />
              )}
              <span className="text-sm font-semibold text-color-text-white truncate w-full">
                {match.awayTeam_name}
              </span>
            </div>
          </div>

          {/* Прогноз */}
          {predictionDisplay && (
            // Условный рендеринг: если `predictionDisplay` существует (т.е. есть прогноз), показываем его.
            <div className="my-3 text-center">
              <span
                className={`px-4 py-1.5 text-xs font-bold rounded-full shadow-md ${predictionDisplay.colorClasses}`}
                // Стили для плашки прогноза: отступы, размер шрифта, жирный текст, скругленные углы, тень и динамические классы цвета.
              >
                {predictionDisplay.text}{" "}
                {/* Выводим полный текст прогноза (например, "Прогноз: П1"). */}
              </span>
            </div>
          )}

          {/* Счет, если матч завершен */}
          {match.status === "FINISHED" && (
            // Условный рендеринг: если статус матча "FINISHED" (завершен), показываем счет.
            <p className="text-center text-lg font-bold my-2 text-color-primary-yellow">
              {match.score_fullTime_home} - {match.score_fullTime_away}
              {/* Отображаем финальный счет домашней и гостевой команды. */}
            </p>
          )}

          {/* Заполнитель высоты, если нет ни прогноза, ни счета */}
          {!predictionDisplay && match.status !== "FINISHED" && (
            // Этот `div` используется как "пустой" заполнитель,
            // чтобы карточки выглядели одинаково по высоте, даже если у матча нет прогноза или он еще не завершен (нет счета).
            <div className="my-3 h-8"></div>
          )}

          {/* Дата и время */}
          <div className="mt-auto text-center text-xs text-color-text-second pt-3">
            {/* Контейнер для даты и времени:
                `mt-auto`: прижимает этот блок к низу flex-контейнера.
                `text-center`: текст по центру.
                `text-xs`: очень маленький размер шрифта.
                `text-color-text-second`: приглушенный цвет текста.
                `pt-3`: верхний отступ. */}
            {formatMatchDateTime(match.utcDate, match.localMatchTime)}
            {/* Вызываем вспомогательную функцию для форматирования и отображения даты/времени. */}
          </div>
        </div>
      </div>
    </Link>
  );
}

export default MatchItem;
