// frontend/src/App.js
import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import HomePage from "./pages/HomePage";
import MatchDetailPage from "./pages/MatchDetailPage";
import Header from "./components/Layout/Header";
import "./App.css"; // Если есть

function App() {
  return (
    <Router>
      <div className="flex flex-col min-h-screen bg-brand-dark-bg text-brand-light-text">
        <Header />
        {/* Отступ сверху для основного контента. 
            Высота красного блока + высота навигации + небольшой отступ.
            Красный блок: py-8 (2rem*2=4rem) + текст
            Навигация: h-14 (3.5rem)
            Итого примерно 7.5rem + высота текста в красном блоке.
            Подберите pt-X экспериментально. Начните с pt-32 или pt-36 (8rem, 9rem)
        */}
        <main className="flex-grow container mx-auto px-4 py-8 pt-32 md:pt-36">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/match/:matchId" element={<MatchDetailPage />} />
            {/* Добавьте роуты для /today, /all-predictions, /statistics, /experts, когда создадите страницы */}
          </Routes>
        </main>
      </div>
    </Router>
  );
}
export default App;
