// src/components/ScrollToTop.jsx
import { useEffect } from "react";
import { useLocation } from "react-router-dom";

function ScrollToTop() {
  // Хук useLocation предоставляет объект location, который содержит pathname
  const { pathname } = useLocation();

  useEffect(() => {
    // Эта функция будет выполняться при каждой смене маршрута (когда pathname меняется)
    // Прокручиваем окно браузера в самый верх (координаты 0, 0)
    window.scrollTo({
      top: 0,
      left: 0,
      // behavior: 'smooth' // Можно добавить для плавной прокрутки, если нужно
    });
  }, [pathname]); // Зависимость: эффект будет запускаться при изменении pathname

  // Этот компонент ничего не рендерит, его цель - только побочный эффект
  return null;
}

export default ScrollToTop;
